css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.quantamagazine.org") {
  
  body {
    color: #aaa;
    background-color: #222;
  }
  
  .theme__background-background {
    background-color: #222;
  }
  
  /**** Article Page e.g. www.quantamagazine.org/mathematicians-measure-infinities-find-theyre-equal-20170912/ ****/
  
  /*** Top Header ***/
  .header__inner {
    background-color: #282828;
  }
  
  ._pws9oi {
    color: #aaa;
  }
  
  .nav__container.is-solid .header__inner {
    background-color: #282828;
  }
  
  /* Social media buttons */
  .black {
    color: #ccc;
  }
  
  .c-1a1a1a {
    color: #ccc;
  }
  
  .nav__local .icon-button div, .nav__local .icon-button svg {
    color: #ccc;
  }
  
  .rc-tooltip-inner, .rc-tooltip-inner::before {
    color: #aaa;
    background-color: #222;
  }
  
  .rc-tooltip-placement-bottom .rc-tooltip-arrow::after {
    background-color: #222;
  }
  
  /*** Body ***/
  
  .theme__text {
    color: #aaa;
  }
  
  /* Image */
  .post__content > p > img, .post__content img {
    opacity: 0.7;
  }
  
  /*** Footer ***/
  
  .bg-gray1 {
    background-color: #282828; /* rgb(40, 40, 40) */
  }
  
  .comments__expand {
    background-image: linear-gradient(to top,rgb(40, 40, 40) 50%,hsla(0,0%,100%,0) 100%); /* linear-gradient(to top, rgb(40, 40, 40) 50%, rgba(40, 40, 40, 0) 100%); */
  }
  
  /* Comments */
  .bg-white {
    background-color: #222;
  }
  
  .wp-caption-text {
    color: #666;
  }
  
  /* Images */
  .image__inner {
    background-color: #282828;
  }
  
  /* Social Media icons */
  .social-links--footer .social-links__link .icon-button div, .social-links--footer .social-links__link .icon-button svg {
    color: #666;
  }
  
  /* Footer */
  .gray2 {
    color: #333;
  }
  
  /* 'Next Article' */
  .white {
    color: #aaa;
  }
  
  .next-post.disable-parallax .overlay {
    background-color: #222;
  }
  
  .next-post.disable-parallax h2 {
    color: #aaa;
  }
  
  /**** Subject Page e.g. www.quantamagazine.org/computer-science/ ****/
  
  /* Header */
  .hero--header {
    background-image: none;
    background-color: #222;
  }
  
  /* Items */
  .card__excerpt p {
    color: #ccc;
  }
  
  /* Images */
  .image__inner.is-loaded img, .image__inner.is-loaded video {
    opacity: 0.5;
  }

}

@-moz-document url-prefix("https://disqus.com/embed/comments/?base=default&f=quanta-mag") {
  
  body {
    color: #aaa;
  }
  
  /* 'Join the Discussion' comment box */
  .textarea-wrapper {
    background-color: #282828;
    border-color: #333;
  }
  
  .post-actions {
    background-color: #282828;
    border-top-color: #333;
  }
  
  .wysiwyg__item a.attach span {
    filter: invert(80%);
  }
  
  .btn {
    background-color: #282828;
    color: #aaa;
  }
  
  .placeholder {
    color: #666;
  }
  
  .textarea {
    color: #aaa;
  }
  
  /* 'N Comments', 'Quanta Magazine', <User name> row */
  .dark .tab-conversation.active > a {
    color: #aaa !important;
  }
  
  .dark .nav-tab > a {
    color: #aaa !important;
  }
  
  /* 'Recommended', 'Share' row */
  .dark .icon-cog, .dark .icon-export, .dark .label {
    color: #aaa;
  }
  
  .dark .thread-likes .label-count {
    background-color: #333;
    color: #aaa !important;
  }
  
  /* Other comments */
  .publisher-anchor-color a {
    color: #999 !important;
  }
  
  .vote-down {
    border-left-color: #333;
  }
  
  .see-more {
    border-top-color: #333;
  }
  
  /* 'Also on Quanta Magazine' */
  a.publisher-anchor-color {
    color: #aaa !important;
  }
  
  .discovery-post .meta {
    color: rgba(40, 40, 40, 0.4);
  }
  
  /* Footer */
  .disqus-footer__wrapper {
    border-top-color: #333;
  }
  
}

@-moz-document url-prefix("https://quanta-prod.s3.amazonaws.com/uploads/iframe") {
  
  #color_panel {
    background-color: #222;
  }
  
}
