/* Compatibility for every browser */

@-moz-document domain(freestylersworld.com) {

/* Reset CSS */

* {
    margin: 0;
    padding: 0;
    border: none;
    background: none;
}

/* ======> MAIN FORUM WEBPAGE <====== */

html {
    background: transparent url('https://wallpapers.wallhaven.cc/wallpapers/full/wallhaven-433712.png') no-repeat fixed !important;
}

/* Remove the background image at post frame */

.CSS1Compat {
    background-image: none!important;
    background-color: #333!important;
}

/* Table body line height adjustment */

body {
    line-height: 1.250;
    background: none;
}

/* Font replacement */

body,
legend {
    font-family: 'Roboto';
}

body a {
    color: #2196F3;
}

/* Logo image */

.logo-image {
    content: url('https://i.imgur.com/sEzTTUB.png');
    top: 25px;
    right: 660px;
}

/* FGunZ logo image */

.navtab > img {
    content: url('https://i.imgur.com/lOvPYiY.png');
    height: 45px!important;
    margin: 1px 1px;
}

/* Doc header */

.doc_header {
    height: 40px;
}

/* Make avatars round */

.postuseravatar img {
    border-radius: 50% !important;
    background-size: cover;
    background-position: center;
    max-width: 200px;
    max-height: 200px;
    width: auto;
    height: auto;
    border: 2px solid rgba(56, 56, 56, 0.78);
}

.toplinks img {
    border-radius: 70% !important;
    background-size: cover;
    background-position: center;
}

/* Removed the animation from header */

.animated-front {
    -webkit-animation: none;
    animation: none;
    background: none;
}

.animated-panel {
    position: inherit;
    top: 0;
    height: 0%;
    width: 0%;
}

.animated-wrap {
    width: 0;
}

/* Header buttons re-colored */

.btn.blue,
.blue {
    background-image: linear-gradient(to bottom, #3c3939 0%, #1f1a1a 100%);
    border: 1px solid #333333;
}

/* On hover */

.btn.blue:hover,
.blue:hover {
    background-image: linear-gradient(to bottom, #3c3939 0%, #1f1a1a 70%);
    border: 1px solid #333333;
    cursor: pointer;
}

/* Body <wrapper> */

.body_wrapper {
    background: url(https://i.imgur.com/lDmOnhY.jpg) no-repeat fixed;
    border: 1px solid #3c4a50;
}

/* Breadcrumb */

.breadcrumb {
    background: rgba(19, 19, 19, 0.75);
}

/* Tables */

table {
    font-size: inherit;
    background: url(https://i.imgur.com/lDmOnhY.jpg) no-repeat fixed;
}

/* Navigation bar */

.navbar {
    margin: 15px 0px -30px 1px;
    padding: 0px 190px 0px 0px;
    background: none;
    border-top-left-radius: 4px !important;
    border-top-right-radius: 4px !important;
    border-bottom-left-radius: 0px !important;
    border-bottom-right-radius: 0px !important;
}

.navbar a {
    margin: 10px 4px;
}

.navtabs {
    background: none;
    border-top-left-radius: 4px !important;
    border-top-right-radius: 4px !important;
    border-bottom-left-radius: 0px !important;
    border-bottom-right-radius: 0px !important;
}

.navtabs li.selected a.navtab {
    color: #ffffff;
    text-shadow: -1px 3px rgba(0, 0, 0, 0.82);
}

.navtabs > ul.floatcontainer li a,
.navtabs > ul.floatcontainer li a.popupctrl {
    color: #e6e6e6;
    text-shadow: -1px 2px rgba(0, 0, 0, 0.82);
    font: 12px Roboto;
    line-height: 25px;
    margin: 0px 4px;
}

.navtabs li a.navtab {
    background: none;
}

.navtabs li a.navtab {
    color: #e8e8e8!important;
}

.navtabs li a.navtab:hover {
    color: #fff!important;
}

/* Freestylers Chatbox blockhead text::before */

/*.forumhead::before,
.blockhead::before,
#forum_icon_legend h5::before {
    content: 'WasEz?';
	color: #fff;
    width: 100%;
    height: 100%;
    font-weight: 900;
    font-style: normal;
    font-family: 'Roboto'!important;
    font-size: 11px;
    text-transform: uppercase;
}*/


/* Forum heads / titles */

.forumhead::before,
.blockhead::before {
    background: url(https://i.imgur.com/L6uUhHY.jpg) no-repeat fixed;
}

/* Forum rows */

.forumhead + .childforum .L2:first-child .forumrow,
.forumhead + .L2 .forumrow,
.forumbit_nopost .forumbit_nopost .forumrow,
.forumbit_post .forumrow,
.threadbit .nonsticky,
.threadbit .discussionrow,
.threadbit .sticky {
    background: url(https://i.imgur.com/lDmOnhY.jpg) no-repeat fixed;
}

/* Search box */

.globalsearch input.textbox {
    width: 160px;
    border: 1px solid rgba(219, 219, 219, 0.59);
    background: rgba(0, 0, 0, 0.58);
    color: #dbdbdb;
    margin: 18px -38px 3px 0px;
    padding: 3px 4px;
}

/* Search icon */

.globalsearch .searchbutton {
    margin-top: 12px;
    margin-left: -2px;
}

/* Advanced search */

.globalsearch a {
    margin: 14px -12px 0 0;
    color: #fff;
    text-shadow: 1px 2px 2px #000!important;
}

.navbar a:hover {
    color: #fff;
}

/* Floatcontainer */

#navtabs > .floatcontainer {
    border-top-left-radius: 0px !important;
    border-top-right-radius: 0px !important;
    border-bottom-left-radius: 4px !important;
    border-bottom-right-radius: 4px !important;
    background: none;
    border: none;
}

/* Floatcontainer content padding */

body,
div,
dl,
dt,
dd,
ul,
ol,
li,
h1,
h2,
h3,
h4,
h5,
h6,
pre,
code,
form,
fieldset,
legend,
input,
button,
textarea,
p,
blockquote,
th,
td {
    padding: 1px;
}

/* Floatcontainer thread icons */

.forumbit_post .foruminfo .forumicon {
    content: url('https://i.imgur.com/sG9OB7x.png');
    opacity: 0.80;
}

/* Floatcontainer sub-text recolor */

.datacontainer,
.titleline,
forumtitle {
    color: #989898;
}

/* Notification popout menu */

.toplinks ul.isuser .notifications .popupbody {
    border: 1px solid #541412;
}

/* Size & padding adjustment */

.notifications .popupbody {
    top: 26px!important;
    left: -2px!important;
}

/* Color and other small adjustments */

.toplinks ul.isuser .popupbody li {
    padding: 0px 0px 0px 10px;
    background-image: linear-gradient(to bottom, #2f2f2f 0%, #1d1d1d 100%);
}

.toplinks ul.isuser .popupbody li:hover {
    background-image: linear-gradient(to bottom, #383838 0%, #252525 100%)
}

/* Username/welcome message color */
#toplinks > div > span > a {
	background: url('http://forum.ragezone.com/images/backround6.gif');
	font-weight: 700;
	text-shadow:rgba(255, 255, 255, 0.4) 0px 0px 2px;
}

/* Button text-shadow re-color */

.toplinks .btn {
    text-shadow: 2px 0px 2px rgba(234, 225, 197, 0.53);
}

/* Removed black border inside the notification menu */

.btn.dark,
.toplinks ul.isuser .notifications .popupbody li a {
    border: none;
}

.btn.dark,
.toplinks ul.isuser .notifications .popupbody li a:hover {
    border: none;
}

/* Logout button animation */

.btn.dark:hover {
    background-position: 0 -50%;
    background-repeat: repeat-x;
    transition: background-position .85s linear;
}

.btn.dark:hover {
    background-position: 100% 60%;
    color: white !important;
    background-image: url('https://s32.postimg.org/g5kiaqxit/wave_black.png');
    opacity: 0.90;
    text-shadow: 2px 0px 2px rgba(234, 225, 197, 0.53);
    border: none;
}

/* Logout button padding adjustment */
#toplinks > div > ul > li:nth-child(1) > a {
