/* ZChat glowing effect */

#zchat_form .zchat_text:focus {
    animation: glow 700ms ease-out infinite alternate;
}

#zchat_form .zchat_text:hover {
    animation: glow 700ms ease-out infinite alternate;
}

@keyframes glow {
    0% {
        border-color: #808080;
        box-shadow: 0 0 0px #808080, inset 0 0 2px #808080, 0 0px 0;
    }
    100% {
