css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("azlyrics.com") {
  
  
  
  
  /* header */  
  
  
  .navbar-default {
	background-color: #474790;
	border-color: #3B3B88;
}
  
  
  .navbar-default .navbar-collapse, .navbar-default .navbar-form {
	border-color: #383888;
}
  
  .btn-menu.focus, .btn-menu:focus, .btn-menu:hover {
	color: #474790;
	background-color: #fff;
  border-color: #ddd;
}
  
  
  .btn-menu {
	color: #FFF;
	background-color: #474790;
	border-color: #99c;
}
  
  
  .lboard-wrap, .links-menu-wrap {
	background-color: #2B2B68;
}
  
  
  
  
 /*   body */
  
  
  body {
	font-size: 18px;
	color: #ddd;
	background-color: #161616;
}
  
  .main-page {
	color: #ddd;
  background-color: #161616;
}
  
  img {
	border: 1px solid #fff;
}
  
  a:focus, a:hover {
	color: #0D0DFC;
}
  
  a {
	color: #4040FC;
}
  
  
  
  /* footer */ 
  
  
  .navbar-default {
  background-color: #474790;
	border-color: #3B3B88;
}
  
  .navbar-footer {
	background-color: #000;
	color: #4040FC;
	font-size: 12px;
}
  
  .footer-wrap {
	background-color: #000;
	text-align: center;
}
  
	
	
  /* search imput  */   

	.form-control {
	color: #e1e1e1;
	background-color: #161616;
		 }
  
	
  /* search results  */   
  
  
  
  .breadcrumb, .panel {
	background-color: #222;
		box-shadow: 1px 1px 0px 2px #555;
}
  
  .table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th {
	border-top: unset;
}
  
  .table-condensed > tbody > tr > td, .table-condensed > tbody > tr > th, .table-condensed > tfoot > tr > td, .table-condensed > tfoot > tr > th, .table-condensed > thead > tr > td, .table-condensed > thead > tr > th {
	padding-left:  15px;
}
  
  .btn-share {
		background-color: #474790;
	border-color: #99c;
}
 
	 .btn-share.focus, .btn-share:focus, .btn-share:hover {
	color: #474790 !important;
	text-decoration: none;
	background-color: #fff;
  border-color: #ddd;
}
	
	.btn-default {
  color: #FFF;
	background-color: #474790;
	border-color: #99c;
}
	
	.btn-default.focus, .btn-default:focus, .btn-default:hover {
	color: #474790;
	text-decoration: none;
	background-color: #fff;
  border-color: #ddd;
}

	div.panel > table > tbody> tr > td > a:visited, 
	#listAlbum > a:visited {
  color: #d2487b !important;
	}
	

	
	/*  submit lyrics  */  
	
	
	.alert-info {
	color: #FFF;
	background-color: #474790;
	border-color: #99c;
}
	
	.alert-warning {
	color: #D8BD8F;
	background-color: #222;
		box-shadow: 1px 1px 0px 2px #555;
	border-color: transparent;
}
	
	.tundra .dijitTextBox {
	color: #e1e1e1;
	background: #161616;	
}

}
