css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://docs.google.com/document/") {
#docs-chrome {
    background: #333 none repeat scroll 0% 0% !important;
    color: #DDD !important;
}

/* Account email in top right corner */
/* x total viewers text */
#gb#gb a.gb_b,
.docs-presence-plus-widget-status {
    color: #DDD !important;
}

/* Document title box */
.docs-title-input {
    background-color: #2B2B2C !important;
    border-radius: 2px !important;
    border: 1px solid #171717 !important;
    border-top-color: #0f0f0f !important;
    box-shadow: 0 1px rgba(255, 255, 255, 0.15), inset 0 1px rgba(0, 0, 0, 0.1) !important;
    font-style: normal !important;
}

.docs-title-input:focus {
    color: white !important;
    background-color: #2B2B2C !important;
    border: 1px solid #171717 !important;
    border-top-color: #0f0f0f !important;
}

.docs-title-untitled {
    display: none !important;
}

/* Fix for menu bar text when hover */
.goog-control-hover {
    color: black !important;
}

/* Main toolbar */
#docs-toolbar-wrapper {
    border-top: 1px solid #151516 !important;
    border-bottom: 1px solid #040404 !important;
    background-image: linear-gradient(#404143, #333436) !important;
    box-shadow: inset 0 1px rgba(255, 255, 255, 0.15) !important;
}

/* Toolbar's vertical dividers */
.goog-toolbar-separator.goog-inline-block {
    border-left: 1px solid #222 !important;
}

/* A very simple/lazy fix to get toolbar item colors to look good on a dark background */
.goog-toolbar-button,
.goog-toolbar-toggle-button,
.goog-toolbar-menu-button:not([id="textColorButton"]),
.goog-toolbar-combo-button,
.docs-icon-folder-solid,
.navigation-widget-hat-close {
    -webkit-filter: invert(1) !important;
    filter: invert(1) !important;
}

/* Fixes for suggestion mode when editing own doc, viewing mode when editing others' docs */
#docs-toolbar-mode-switcher,
#docs-access-level-indicator {
    background-color: transparent !important;
    color: black !important;
}

.docs-icon-mode-review-white,
.docs-icon-mode-view-white {
    filter: invert(1) !important;
}

/* Background behind the document */
#docs-editor {
    background: #333 none repeat scroll 0% 0% !important;
}

/* Document drop-shadow removal */
.kix-page-paginated {
    box-shadow: none !important;
}

/* Sepia pages */
.kix-page,
.kix-page-content-wrapper {
    background-color: #f5eee0 !important;
}

/* Navigation items in content overview colors */
.navigation-item-content {
    color: #ddd !important;
}

.navigation-widget-hat-title,
.navigation-widget-hat-close .docs-icon {
    color: #fff !important
}

.goog-button-hover {
    background-color: #ddd !important;
}

.goog-button-hover > .navigation-item-content {
    color: #333 !important;
}

/* Top-Ruler colors */
#kix-ruler {
    border-bottom: 1px solid #222;
}

.kix-ruler-background-inner {
    background-color: #eee !important;
}

/* Comments */
.docos-anchoreddocoview .docos-replyview-first.docos-anchoredreplyview,
.docos-anchoredreplyview.docos-replyview-comment {
    background: #f5eee0 !important;
}

.docos-layout-vertical .docos-anchoreddocoview .docos-anchoreddocoview-arrow-inner {
    border-right: 18px solid #f5eee0 !important;
}

.docos-replyview-resolve-button-original {
    opacity: 0.4 !important;
}
}
