/* ===== INPUT SWITCH ======= */
.input-switch-line:after {
  content: '';
  display: table;
  clear: both;
}

.input-switch.right {
  margin-left: 1em;
}

.input-switch-toggle {
  position: absolute;
  margin-left: -9999px;
  visibility: hidden;
}

.input-switch-label {
  font-weight: normal;
  padding-top: 5px;
  text-align: left;
}

.input-switch-toggle + label {
  display: block;
  position: relative;
  cursor: pointer;
  outline: none;
  user-select: none;
  padding: 2px;
  width: 40px;
  height: 20px;
  border-radius: 20px;
  margin-bottom: 0;
}

.input-switch-toggle:disabled + label {
  opacity: .5;
  cursor: not-allowed;
}

.input-switch-toggle + label:before,
.input-switch-toggle + label:after {
  display: block;
  position: absolute;
  top: 1px;
  left: 1px;
  bottom: 1px;
  content: "";
}

.input-switch-toggle + label:before {
  right: 1px;
  background-color: rgba(118, 210, 118, 0.3);
  border-radius: 30px;
  transition: background 0.4s;
}

.input-switch-toggle.subtheme + label:before {
  background-color: rgba(149, 117, 205, 0.3);
}

.input-switch-toggle + label:after {
  width: 18px;
  background-color: var(--bgcolor2);
  border-radius: 100%;
  box-shadow: 0 1px 0px rgba(0, 0, 0, 0.15);
  transition: margin 0.4s;
}

.input-switch-toggle:checked + label:before {
  background-color: #76D276;
}

.input-switch-toggle.subtheme:checked + label:before {
  background-color: #9575cd;
}

.input-switch-toggle:checked + label:after {
  margin-left: 20px;
}



