.profile__message {
  -moz-transition-duration: 0.3s;
  -o-transition-duration: 0.3s;
  -webkit-transition-duration: 0.3s;
  transition-duration: 0.3s;
  -moz-transition-property: background-color;
  -o-transition-property: background-color;
  -webkit-transition-property: background-color;
  transition-property: background-color;
  -moz-transition-timing-function: ease-in-out;
  -o-transition-timing-function: ease-in-out;
  -webkit-transition-timing-function: ease-in-out;
  transition-timing-function: ease-in-out;
  border-top: 1px solid #F0F0F0;
  clear: both;
  min-height: 39px;
  padding: 4px 6px;
  position: relative;
}
.profile__message.in + .in, .profile__message.out + .out {
  border-top: 0;
  min-height: 0;
}
.profile__message.in + .in.unread, .profile__message.out + .out.unread {
  border-top: 1px solid var(--bgcolor2);
}
.profile__message.in + .in .pc__photo, .profile__message.out + .out .pc__photo {
  display: none;
}
.profile__message .pc__date {
  -moz-transition-duration: 0.2s;
  -o-transition-duration: 0.2s;
  -webkit-transition-duration: 0.2s;
  transition-duration: 0.2s;
  -moz-transition-property: opacity;
  -o-transition-property: opacity;
  -webkit-transition-property: opacity;
  transition-property: opacity;
  -moz-transition-timing-function: ease-in-out;
  -o-transition-timing-function: ease-in-out;
  -webkit-transition-timing-function: ease-in-out;
  transition-timing-function: ease-in-out;
  color: var(--text3);
  background-color: var(--bgcolor2);
  font-size: .8em;
  position: absolute;
  right: 4px;
  bottom: 4px;
  opacity: 0;
  padding: 0 2px;
}
.profile__message .pc__photo {
