}

a:link, .usermenu .login a:focus
{
	color: #A1C6EB !important;
}

a:hover, .usermenu .login a:hover
{
	color: #7090B0 !important;
}

a:visited, .usermenu .login a:visited
{
	color: #69A3EA !important;
}

.summary > .no-overflow > div
{
	background-color: #417EC3 !important;
	color: #B3B3B3;
}

.page-context-header .page-header-headings h1
{
	color: #B3B3B3;
}

