}

.navbar .nav li.dropdown.open > .dropdown-toggle, .jsenabled .usermenu .moodle-actionmenu.show, .file-picker .fp-navbar
{
	background-color: #2C2C2C;
	color: #8C8C8C;
}

.moodle-dialogue-base .closebutton
{
	background-image: url(https://platforma.polsl.pl/rau1/theme/image.php/clean_polsl/theme/1526800699/sprite) !important;
	filter: invert(100%) !important;
}

.navbar .nav > li > a:hover, .usermenu .moodle-actionmenu .toggle-display:hover
{
	color: #D8D8D8;
}

.navbar .nav li.dropdown > a:hover > .caret, .usermenu > div ul.menubar > li a.toggle-display.textmenu:hover > b.caret
{
	border-top-color: #D8D8D8;
}

.dropdown-menu, .moodle-actionmenu.show[data-enhanced] .menu, .file-picker .fp-list .fp-repo.active
{
