css:
@-moz-document url("https://habitica.com/") {
    .tasks-column {
        height: 556px !important;
    }
    .tasks-list {
        padding-bottom: 0.1px !important;
        position: relative;
        height: calc(100% - 56px) !important;
        overflow: auto;
    }
    .tasks-column .dropdown-menu {
        right: 0;
        left: auto;
    }
    .popover {
        position: absolute !important;
        top: 100px !important;
    }
}
