css:
/* i really want this to be global */
/*@import url(https://fonts.googleapis.com/css?family=Share+Tech+Mono);*/
@font-face
{
  font-family: 'Share Tech Mono'; font-style: normal; font-weight: 400; src: local('Share Tech Mono'), local('ShareTechMono-Regular'), url(https://fonts.gstatic.com/s/sharetechmono/v5/RQxK-3RA0Lnf3gnnnNrAsaH0X__W3S3MJL29bc5CWfs.woff) format('woff');
}

/*-- entire web --*/
*
{
  color: lime !important;
  border-color: lime !important;
	/*text-shadow: 0 0 black !important;*/
  border-radius: 0 !important;
  background-image: none !important;
  text-shadow: none !important;
}

/*-- bg color key elements --*/
span,body,header,.header,#header,container,.container,#container,nav,.nav,.navigation,#nav,#navigation,.icon,div,footer,.footer,#footer,.page,#page,.module,.selected,.tab,.tabs,.tabs .selected a,a.wikia-button.secondary
{
  background-color: black !important;
}

/*-- font --*/
body,h1,h2,h3,h4,h5,h6,.container,div,span a,ul,li a,input,textarea,button,a,table, td, tr,.uiButtonText,p,p span
{
  font-family: 'Share Tech Mono' !important;
}

/*-- text color catch --*/
.video-time
{
  color: lime !important;
}

/*-- link color catch --*/
a.title,a.linkColor,a.link,.link
{
  color: lime !important;
}

a:hover,a[role=button]:hover
{
  background: lime !important;
  background-color: lime !important;
  color: black !important;
  border-radius: 0 !important;
  border: 0 !important;
  text-decoration: none !important;
}

/*-- input catch --*/
input,input[type=text],textarea,select,input[type="radio"]
{
  font-family: 'Share Tech Mono' !important;
  background: black !important;
  background-color: rgb(0, 0, 0) !important;
  color: lime !important;
  border-radius: 0 !important;
  border-color: lime !important;
}

/* input button style */
input[type=submit]
{
  background-color: lime !important;
  background-color: rgb(255, 165, 0) !important;
  border-color: lime !important;
  color: black !important;
  border-radius: 0 !important;
  border: 0 !important;
  text-shadow: none !important;
}

::-webkit-input-placeholder
{
 /* WebKit, Blink, Edge */
  color: lime !important;
}

:-moz-placeholder
{
 /* Mozilla Firefox 4 to 18 */
  color: lime !important;
  opacity: 1;
}

::-moz-placeholder
{
 /* Mozilla Firefox 19+ */
  color: lime !important;
  opacity: 1;
}

:-ms-input-placeholder
{
 /* Internet Explorer 10-11 */
  color: lime !important;
}

:placeholder-shown
{
 /* Standard (https://drafts.csswg.org/selectors-4/#placeholder) */
  color: lime !important;
}

button,a[role="button"]
{
  background: transparent !important;
  text-shadow: none !important;
  font-family: 'Share Tech Mono' !important;
}

button:hover
{
  background: black !important;
  color: lime !important;
}

input[type="radio"],input[type="checkbox"]
{
  background: lime !important;
  border: 1px solid lime !important;
}

/*-- img + google logo csb > lime */
img,video,.csb,iframe,#hplogo
{
  -webkit-filter: contrast(140%) brightness(23%) saturate(4000%) hue-rotate(-260deg);
}

/* minor bits */
cite
{
  border-bottom: 1px solid lime;
  font-size: 0.75em !important;
}



