@-moz-document regexp("^https?://so.mgtv.com/.*") {
/*
芒果tv搜索页面
^https?://so.mgtv.com/.*
2017.10.21
水月仙森
chiangchinming@gmail.com
*/

/*****搜索正文*****/

/*结果排列方式:综合,筛选*/
.search-title {
	display: none;
}

.so-container {
	padding-top: 60px;
}

.search-box.clearfix {
	margin-left: 20%;
}

