css:
@-moz-document regexp("https:\\/\\/rutracker\\.(.*)\\/forum\\/index\\.php") {
html,
body {
	height: 100%;
    min-height: 100%;
}

#body_container {
    height: 100%;
	padding: 0;
}

#page_container {
    height: 100%;
    display: flex;
    flex-direction: column;
	background: #1f1f1f;
	color: #eee;
}

.gen, .med, .small, .gensmall {
    color: #ffffff;
}

a:link, a:visited {
    color: #808080;
}

#page_header {
    margin: auto 0 0;
}

#page_footer {
    margin-top: auto;
    display: flex;
    flex-direction: column;
    justify-content: center;
	padding-bottom: 24px;
}

#page_content {
	display: none;
}

#footer-info-links {
    display: none;
}

#main-nav {
	display: none;
}


#logo {
	min-height: 0;
}


#logo:after {
    content: '_rutracker';
    font-size: 4vw;
    font-family: monospace;
    width: 100%;
    display: block;
    text-align: center;
}

#logo>table {
    display: none;
}

.topmenu {
    background: transparent;
    border: none;
    margin: 42px;
    padding: 0;
}

.topmenu tr {
	display: flex;
    flex-direction: column;
}

.topmenu td {
    padding: 14px;
    width: 100% !important;
	display: flex;
    justify-content: center;
}

#search-menu {
	display: none;
}

#search-submit {
    border: 1px solid #999;
	background: #fff;
    height: auto;
    padding: 10px 14px;
    width: auto;
}

#search-text {
    height: auto;
    width: 480px;
    padding: 10px 12px;
    border: 1px solid #999;
}

.w50 {
    width: 100% !important;
    text-align: center;
}

a.menu-root.menu-alt1 {
    display: none;
}
}
