css:
@-moz-document domain("projecteuler.net") {
body {
--base03:    #002b36;
--base02:    #073642;
--base01:    #586e75;
--base00:    #657b83;
--base0:     #839496;
--base1:     #93a1a1;
--base2:     #eee8d5;
--base3:     #fdf6e3;
--yellow:    #b58900;
--orange:    #cb4b16;
--red:       #dc322f;
--magenta:   #d33682;
--violet:    #6c71c4;
--blue:      #268bd2;
--cyan:      #2aa198;
--green:     #859900;
}

@font-face
{
    font-family: 'Inconsolata';
    font-style: normal;
    font-weight: 400;
    src: local('Inconsolata'), url(https://fonts.gstatic.com/s/inconsolata/v12/BjAYBlHtW3CJxDcjzrnZCI4P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
}

body {
    background-color: var(--base03);
    color: var(--base1);                    
}

div#header {
    background-color: var(--base02);
    box-shadow: none;
}

#nav li {
    background-color: var(--base03);
    border: 1px solid var(--base01);
}

#nav a {
    color: var(--base1);
}

#nav #current a {
    background-color: var(--yellow);       
    color: var(--base2);
}

#nav #highlight {
    color: var(--blue);
}

#nav li:hover {
    background-color: var(--blue);
}
#nav li:hover a {
    color: var(--base3);
}

#nav li:hover #highlight {
    color: var(--base3);
}

#info_panel {
    color: var(--base1);
}

h2 {
    color: var(--yellow);
}

h3 {
    color: var(--blue);
}

h4 {
    color: var(--blue);
}

.small_notice {
    color: var(--base01);
}
.problem_content {
    background-color: var(--base02);
    border-color: var(--base01);    
}
                                        
#container table {
    background-color: var(--base02);
    border-color: var(--base01);
}

#container table input[type=text] {
    background-color: var(--base3);
    color: var(--base01);
    border: 0px;
    padding: 3px;
    border-radius: 3px;
    width: 100%;
}

#container table td {
    width: 48%;
}

#container table input[type=submit] {
    background-color: var(--blue);
    color: var(--base2);
    border: 1px solid var(--base01);
    padding: 5px 12px 5px 12px;
    border-radius: 5px;
    font-weight: bold;
}

#container table td[colspan="2"] {
    text-align: right;
    width: 100%;
    color: var(--base1);
    padding: 5px;
}

#container table td[colspan="2"] img {
    float: right;
    text-align: right;
    width: 50% !important;
    margin-left: 0px;
}

#container table input[type=submit]:hover {
    background-color: var(--yellow) 
}

#footer {
    color: var(--base1);
}

#footer a {
    color: var(--base0);
}

.pagination a, .pagination a:link, .pagination a:visited, .pagination a:active {
    color: var(--base2);
    background-color: var(--base01);
}

.pagination a.current {
    background-color: var(--yellow);
}

.pagination a.visited {
    background-color: var(--blue);
}

#about_page #disclaimer {
    background-color: var(--base02);
    color: var(--base1);
    border-color: var(--base1);
    border-width: 1px;
}

#problems_table_page .sortby {
   color: var(--base02);
}

#problems_table_page .age_minutes {
   color: var(--base01);
}
#problems_table_page .age_hours {
   color: var(--base01);
}
#problems_table_page .age_days {
   color: var(--base01);
}
#problems_table_page .age_weeks {
   color: var(--base01);
}

.grid tr {
   background-color: var(--base02);
}
.grid th {
   color: var(--base2);
   background-color: var(--base01);
}
.grid td, .grid th {
   padding: 2px;
   border-color: var(--base01);
}

a {
    color: var(--blue);
}
a:hover {
    color: var(--yellow);
}
.difficulty_column > div {
    border-color: var(--base01) !important;
    border-radius: 6px;
}

.difficulty_column .info {
    background-color: var(--yellow) !important;
    border-top-left-radius: 6px;
    border-bottom-left-radius: 6px;
}

#progress_page #friend_box img {
   border-color: var(--base1)
}
#progress_page #level_image_box {
   border-color: var(--base1);
}
#progress_page #progress_bar_box {
   border-color: var(--base1);
}
#progress_page .problems_solved_table td {
   color: var(--base1);
}
#progress_page .problems_solved_table td.problem_solved {
   color: var(--base2);
   background-color: var(--yellow);
}
#progress_page .problem_unsolved {
   color: var(--base1);
   background-color: var(--base02);
}
#progress_page .own_problem_solved {
   color: var(--base1);
}
#progress_page #posts_made_section .post_permanent {
   color: var(--base1);
}
#progress_page #posts_made_section .kudos_permanent {
   color: var(--base2);
}
#progress_page #posts_made_section .post_temporary, #progress_page #posts_made_section .kudos_temporary {
   color: var(--base01);
}
#progress_page .form_box {
   background-color: var(--base02);
}

#news_page .news {
   background-color: var(--base02);
   border-color: var(--base01);
}

#container table#problems_table td {
    width: auto;
    border-width: 0px;
    border-bottom-width: 1px;
    
}

#container table#problems_table th.n_column,
#container table#problems_table th.equal_column,
#container table#problems_table th.atleast_column {
    background-color: var(--base01);
}

#container table#problems_table .blank_column,
#container table#problems_table .n_column {
    background-color: var(--base03);
}

#container table#problems_table tr:nth-child(even) {
    background-color: var(--base03);
}

.forum_table {
    background-color: var(--base02);
    border-color: var(--base01);
}

.forum_message {
   color: var(--base2);
}

.forum_info {
   background-color: var(--base01);
   color: var(--base2);
}
.forum_quote {
   background: var(--base03);
}
.forum_hide {
   background-color: var(--base03);
   color: var(--base03);
}
.forum_hide:hover {
   color: var(--base2);
}

.code_box {
   background-color: var(--base03);
   border-color: var(--base01);
}
.code_click {
   color: var(--base1);
   background-color: var(--base02);
}

.forum_message {
    font-family: "Inconsolata", consolas, monospace;
    font-size: 1em;
}

#logo span, h3, h4,
#friends_page #friends_key_section input[type="input"], #friends_page #friends_key_section input[type="text"] {
    font-family: "Inconsolata", consolas, monospace;
}
}
