css:
@-moz-document domain("klava.org") {
   
/* uncomment to available font icon */
/* @import url("https://maxcdn.icons8.com/fonts/line-awesome/1.1/css/line-awesome-font-awesome.min.css"); */

body {
        background: #444;
        font-family: Helvetica, Arial, sans-serif;
        zoom: 1.5;
    }
    #header {
        margin: 0px;
    }
    #dict * {
        color: #fff;
        margin-right: 30px;
    }
    #dict_sel {
        box-shadow: none;
        -webkit-box-shadow: none;
    }
    #intext,
    #intext.blur {
        font-family: Helvetica, Arial, sans-serif;
        border: 0px solid #6af952;
        border-radius: 2px;
        padding: 5px 5px;
        outline: 0px;
        font-weight: 400;
        line-height: 10px;
    }
    #text {
        font-family: Helvetica, Arial, sans-serif;
        color: #6af952;
        background: transparent;
        margin-left: 5px;
                                            
    }
    
    #text span.warn {
        background: transparent;
    }
    #text.penalt div{
        background: transparent;
    }
    #footer,
    #social,
    #logo_border {
        display: none;
    }
    /****awesome icons *************************/
    
    #time {
        top: 10px;
        background: none;
    }
    #time a {
        color: #56cdff;
        border-bottom: 0px;
        font-size: 10px;
        padding: 0px 0 0px 0px;
    }
    #time {
        background: none;
    }
    #time .link {
        float: right;
    }
    #time a:before {
        font-family: 'FontAwesome';
        content: "\f18f";
        font-size: 18px;
    }
    /************************/
    
    #refresh {
        color: transparent;
    }
    #refresh span {
        background: none;
    }
    #refresh:before {
        font-family: 'FontAwesome';
        content: "\f2d5";
        font-size: 18px;
        color: #56cdff;
    }
    #speed {
        background: none;
    }
    #speed .data .nimp {
        color: #fff;
        margin-left: 10px;
    }
    #speed:before {
        font-family: 'FontAwesome';
        content: "\f23f";
        font-size: 18px;
        color: #00fb5b;
    }
    #error {
        background: none;
    }
    #error .data .nimp {
        color: #fff;
        margin-left: 10px;
    }
    #error:before {
        font-family: 'FontAwesome';
        content: "\f376";
        font-size: 18px;
        color: #ff5a5a;
    }
}
