css:
@-moz-document url-prefix("http://beezilla/") {
/*Header*/

* {
    font-family: 'Open Sans', sans-serif;
}
body.beezilla {
    position: relative;
    min-height: 100vh;
    margin: 0;
    padding: 0;
    background: white;
}
#titles,
#header .links {
    background-color: #2B3340;
    border-radius: 0;
    border: none;
}
#titles {
    border-bottom: 1px solid #3a4556;
}
#titles #title {
    padding-left: 5px;
    font-size: 18px;
    font-weight: normal;
}
#header {
    margin-bottom: 0;
}
#header .links {
    width: 100%;
    display: block;
    padding: 0;
}
#header .links li {
    display: inline-block;
    font-size: 14px;
    padding: 15px;
}
#header .links li:hover {
    cursor: pointer;
    background-color: #01A6FF;
}
#header .links li .separator {
    display: none;
}
#header .links li a {
    text-decoration: none;
}
#header .links li.form {
    float: right;
    height: 100%;
}
#header .links li:nth-child(7) {
    display: none;
}
#header .links li:last-child {
    float: right;
}
#header .links li:nth-child(5):hover,
#header .links li:last-child:hover {
    background-color: transparent;
}
}

@-moz-document url-prefix("http://beezilla/") {
/*Footer*/

#bugzilla-body {
    padding-bottom: 50px;
}
#footer {
    position: absolute;
    right: 0;
    bottom: 0;
    border-radius: 0;
    border: none;
    background-color: #2B3340;
}
#footer #useful-links #links-actions {
    display: none;
}
}

@-moz-document url-prefix("http://beezilla/") {
/*Body geral*/

#bugzilla-body {
    padding-left: 0;
    padding-right: 0;
    padding-top: 0;
    border-radius: 0;
    border: none;
    background-color: white;
}
}

@-moz-document url-prefix("http://beezilla/buglist.cgi") {
/*Tela My Bugs*/

#bugzilla-body {
    padding-left: 25px;
    padding-right: 25px;
}
table.bz_buglist {
    margin-top: 15px;
    text-align: center;
}
table.bz_buglist .bz_buglist_header {
    height: 50px;
    background-color: #2B3340;
    color: white;
}
table.bz_buglist .bz_buglist_header a,
table.bz_buglist .bz_buglist_header a:visited,
table.bz_buglist .bz_sort_order_primary {
    color: white;
}
table.bz_buglist .bz_buglist_header a {
    text-decoration: none;
}
table.bz_buglist .bz_buglist_header a:hover {
    text-decoration: underline;
}
#bugzilla-body table.bz_buglist .bz_buglist_header th {
    vertical-align: middle;
    text-align: center;
    font-weight: normal;
}
table.bz_buglist tr {
    height: 40px;
    color: black;
    border-bottom: 1px solid black;
}
table.bz_buglist tr td {
    text-align: center;
}
table.bz_buglist tr a:visited {
    color: black;
}
table.bz_buglist tr.bz_time_summary_line td {
    text-align: right;
}
table.bz_buglist tr.bz_critical {
    background-color: #e23d3d;
}
}

@-moz-document url-prefix("http://beezilla/show_bug.cgi") {
/*Tela Show bug*/

#comments{
	padding-left: 10px;
    padding-right: 10px;
}

#comments .bz_comment_table .bz_comment{
    background-color: transparent;
    border: none;
    padding: 0;
}

#comments .bz_comment_table .bz_comment br{
	background-color:red;
}

#comments .bz_comment_table .bz_comment br{
	display: none;
}

#comments .bz_comment_table .bz_comment .bz_comment_head,
#comments .bz_comment_table .bz_comment .bz_comment_text,
#comments .bz_comment_table .bz_comment .bz_first_comment_head{
    box-sizing: border-box;
    
	border: 1px solid #2B3340;
    border-radius: 3px;
    
    background-color: #FCFCFC;
    box-shadow: 1px 1px 2px 0px #999;
}

#comments .bz_comment_table .bz_comment .bz_comment_head,
#comments .bz_comment_table .bz_comment .bz_first_comment_head{
    padding-left: 10px;
    padding-right: 10px;
    padding-top: 5px;
    padding-bottom: 5px;
    
    margin-top: 25px;
}

#comments .bz_comment_table .bz_comment .bz_comment_text{
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 10px;
    padding-bottom: 10px;
    
    margin-top: 12px;
    margin-left: 5px;    
    
    position: relative;
    
    
}

#comments .bz_comment_table .bz_comment .bz_comment_text:before,
#comments .bz_comment_table .bz_comment .bz_comment_text:after{
	content: '';
	position: absolute;
	top: 0;
    left: 1%;
	width: 0;
	height: 0;
}

#comments .bz_comment_table .bz_comment .bz_comment_text:before{
	border: 7px solid transparent;
	border-bottom-color: #2B3340;
	border-top: 0;
	margin-right: -7.5px;
	margin-top: -7px;
}

#comments .bz_comment_table .bz_comment .bz_comment_text:after{
	border: 6px solid transparent;
	border-bottom-color: #FCFCFC;
	border-top: 0;
	margin-left: 0.5px;
	margin-top: -6px;
}
}
