css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("python-graph-gallery.com") {
  
  body {
    background-color: #222;
    color: #aaa;
  }
  
  body.custom-background {
    background-color: #222;
    color: #aaa;
  }
  
  #wrapper {
    background-color: #222;
  }
  
  hr {
    border-top-color: #333;
  }
  
  /* Site Logo */
  img[alt="The Python Graph Gallery"] {
    filter: invert(86.5%);
  }
  
  /* Dropdown Menu */
  #mainmenu > li:hover > a, #mainmenu ul {
    background-color: #282828;
  }
  
  #mainmenu ul a {
    color: #aaa;
  }
  
  /* Active Item */
  #mainmenu > li.current-menu-item > a, #mainmenu > li.current_page_item > a, #mainmenu > li.current-menu-ancestor > a, #mainmenu > li.current_page_ancestor > a {
    background-color: #333;
  }
  
  #mainmenu > li.current-menu-item > a, #mainmenu > li.current_page_item > a, #mainmenu > li.current-menu-ancestor > a, #mainmenu > li.current_page_ancestor > a {
    color: #aaa;
  }
  
  /* Search - on top, right */
  input[type="text"], input[type="password"], input[type="email"], textarea, select {
    border-color: #333;
    color: #aaa;
    background-color: #222;
  }
  
  input[type="text"]:focus, input[type="password"]:focus, input[type="email"]:focus, textarea:focus {
    background-color: #282828;
  }
  
  .searchsubmit {
    background-color: #333;
    color: #aaa;
  }
  
  /* Footer */
  #footer-widget {
    background-color: #222;
    border-top-color: #333;
  }
  
  #bottom {
    background-color: #222;
    border-top-color: #333;
    color: #aaa;
  }
  
  /*** Imdividual Plot type Page ***/
  
  #wrapper .gallery img {
/*     filter: invert(86.5%); */
    opacity: 0.5;
  }
  
  img.aligncenter, img.alignleft {
    opacity: 0.5;
  }
  
  /* Tabs */
  
  ul.g7-tabs {
    border-bottom-color: #333;
  }
  
  ul.g7-tabs li a.active {
    background-color: #333;
    color: #aaa;
  }
  
  ul.g7-tabs li a {
    background-color: #282828;
    color: #aaa;
    border-color: #333;
  }
  
  ul.g7-tabs li a:hover {
    background-color: #363636;
  }
  
  /* Code */
  .g7-accordion {
    border-color: #333;
  }
  
  .g7-accordion > dt {
    background-color: #282828;
    border-bottom-color: #333;
  }
  
  .g7-accordion > dt:hover {
    background-color: #333;
  }
  
  .g7-accordion > dt.active {
    background-color: #333;
    color: #aaa;
  }
  
  .g7-accordion > dd {
    border-bottom-color: #333;
  }
  
  .syntaxhighlighter .line.alt1 {
    background-color: #1c1c1c !important;
  }
  
  .syntaxhighlighter .line.alt2 {
    background-color: #1c1c1c !important;
  }
  
  .syntaxhighlighter .plain, .syntaxhighlighter .plain a {
    color: #aaa !important;
  }
  
  /* Code - icons */
  .syntaxhighlighter .toolbar {
    background-color: #1c1c1c !important;
    border-color: #1c1c1c !important;
  }
  
  .syntaxhighlighter .toolbar .item.viewSource, 
  .syntaxhighlighter .toolbar .item.printSource, 
  .syntaxhighlighter .toolbar .item.about {
    opacity: 0.7;
  }
  
  /* Previous and Next Post */
  .next-prev {
    border-top-color: #333;
    border-bottom-color: #333;
  }
  
  .block-top img, 
/*   .posts .entry-image img, */
  .block-side img {
    filter: invert(86.5%);
  }
  
  .posts .entry-image img {
    opacity: 0.5;
  }
  
  .block-top, .block-1 .block-side, .posts .entry-image {
    border-bottom-color: #282828;
  }
  
  /* Response Boxes */
  #respond input[type="text"], #respond textarea {
    border-color: #333;
  }
  
}
