css:
@-moz-document url-prefix("https://blutopia.xyz/"), url-prefix("https://blutopia.xyz/")  {

.hoe-right-header {
    background: #141a21 none repeat scroll 0 0;
    background: linear-gradient(to right, #008dff , #222222);
}
#hoeapp-container[hoe-color-type="lpanel-bg8"] #hoe-left-panel .panel-list > li > a {
    background: #303030;
    border-top: 1px solid #545454;
    color: #383838;
    border-right: 1px solid #545454;
}
.img-thumbnail {
    background-color: #091965;
    border: 1px solid #50d1ff;
    box-shadow:
    inset 0 0 1px #fff,
    0 0 10px #0ff,
    -3px 0 10px #008CFD,
    3px 0 10px #008CFD;
}
.badge-extra {
    border: 1.5px solid #7d7d7d;
    background-color: black;
}
.badge-user {
    border: 1.25px solid #7d7d7d;
    background-color: black;
}
.text-green {
    color: #00bf00;
}
.text-red {
    color: #e80000;
}
.text-pink {
    color: #ff24ba;
}
.text-blue {
    color: #1889f7;
}
element.style {
    color: #cad9ff;
}
.panel-chat > .panel-heading {
    color: #fff;
    background: linear-gradient(to right, #008dff , #222222);
    border-color: #ddd;
}
.panel-default>.panel-heading, .panel-footer {
    color: #ccc;
    background: linear-gradient(to right, #008dff , #222222);
    border-color: #464545;
}
.hoe-sidebar-toggle a:after {
    color: #00dba3;
}
.panel-success > .panel-heading {
    color: #fff;
    background: linear-gradient(to right, #0d64ab, #00BC63);
    border-color: #79ffc0;
}
.fa-film:before {
    content: "\f008";
    color: #0189f7;
}
.fa-tv:before, .fa-television:before {
    content: "\f26c";
    color: #b09bff;
}
.fa-imdb:before {
    content: "\f2d8";
    color: #DCAA1A;
}
.wysibb {
    border: 1px solid #395286;
    background: #171717;
}
.table-striped>tbody>tr:nth-of-type(odd) td {
    border-color: #313131;
}
.label-success {
    background-color: #299bb3;
    border-bottom-color: #2e6177;
}
.btn-primary {
    background-color: #000000;
    color: #fff;
    background-image: linear-gradient(to bottom right, #9100ff, #4a568a);
    box-shadow: 2px 6px 15px rgba(88, 114, 181, .35);
    border-width: 0 0 3px !important;
    border-color: #5872b5;
}
.pagination>.active>a, .pagination>.active>a:focus, .pagination>.active>a:hover, .pagination>.active>span, .pagination>.active>span:focus, .pagination>.active>span:hover {
    color: #00dba3;
    background-color: #27494a;
    border-color: #464545;
}
.hoe-left-header {
    background: rgb(1, 106, 191);
}
.label-danger {
    background-color: #ff000c;
    border-bottom-color: #ffa965;
}
#hoeapp-container[hoe-color-type="lpanel-bg8"] #hoe-left-panel .panel-list > li > a {
    color: #018cfd;
}
#hoeapp-container[hoe-color-type="lpanel-bg8"] #hoe-left-panel .profile-box .media a > img {
    border: 2px solid #5283b3;
}
.hoe-header-profile a img {
    height: 40px;
    margin-top: -2px;
    width: 40px;
	border: 2px solid #5283b3;
}
a, h1.title a:visited {
    color: #00DBA3;
    text-decoration: none;
    font-weight: bold;
}
h4 {
 	font-size: 16px;
	font-weight: bold;
}
.h1 .small, .h1 small, .h2 .small, .h2 small, .h3 .small, .h3 small, .h4 .small, .h4 small, .h5 .small, .h5 small, .h6 .small, .h6 small, h1 .small, h1 small, h2 .small, h2 small, h3 .small, h3 small, h4 .small, h4 small, h5 .small, h5 small, h6 .small, h6 small {
    font-weight: 400;
    line-height: 1;
    color: #02ffde;
}
.fa-user:before {
    content: "\f007";
    color: lightblue;
}
.fa-group:before, .fa-users:before {
    content: "\f0c0";
    color: lightblue;
}
#hoeapp-container[hoe-color-type="lpanel-bg8"] #hoe-left-panel {
    background: #384b5f none repeat scroll 0 0;
    color: #41ffa9;
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.2);
}
.ratio-bar {
    border: 0;
    background-image: linear-gradient(to bottom, #303030, #484848);
}
/* Let's get this party started */
::-webkit-scrollbar {
    width: 12px;
}
 
/* Track */
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3); 
    -webkit-border-radius: 10px;
    border-radius: 10px;
}
 
/* Handle */
::-webkit-scrollbar-thumb {
    -webkit-border-radius: 10px;
    border-radius: 10px;
    background: rgba(0, 168, 235, 1);
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5); 
}
::-webkit-scrollbar-thumb:window-inactive {
	background: rgba(0, 84, 117, 1);
}
#formContent {
    background: linear-gradient(to bottom right, #008dff , #222222);
}
input[type=password] {
    border: 2px solid #0089ff;
}
input[type=text] {
    border: 2px solid #0089ff;
}
button {
    background-color: #00a9ff;
}	

}
