css:
@-moz-document domain("price.com.hk") {
*:not(a):not(span):not(sup):not(button):not(.search-box):not([class^=part-]):not(.zoomContainer):not([class^=im-list-trade]):not(.im-toggle-icon) {
    background-color: black !important;
}
p,
a,
h1,
h2,
h3,
body,
label {
    color: #fff;
}
.list-fav-product .item .item-inner .line-01,
.list-fav-merchant .item .item-inner .line-01 {
    color: #eeeeee !important;
}
.pd-name span {
    color: #eee !important;
}
.body-home .hp-revise .tab-outlet .nav-tabs > li > a {
    color: #ccc;
}
.body-home .hp-revise .home-block-01 .tab-outlet .nav-tabs > li.active > a {
    color: #fff;
}
.body-home .home-block-01 .nav-tabs .tab-title .process_bar .active_bar {
    background-color: #f1c400 !important;
}
.item-hot a {
    color: #eee;
}
.product-list-item-list .list-product .item .column-02 .line-01 a {
    color: #00a499;
}
.product-list-item-list .list-product .item .column-02 .line-04 {
    color: #ddd;
}
.club-price-wrapper .club-title {
    color: #fbcd26;
}
.club-price-wrapper:hover .club-title {
    color: #ffff00;
}
.product-col-info .item-attr,
.product-col-info .item-remark {
    color: #ddd;
}
h1,
h2,
h3,
h4,
h5,
h6,
.h1,
.h2,
.h3,
.h4,
.h5,
.h6 {
    color: #eee;
}
.breadcrumb-product a {
    color: #aaa;
}
.breadcrumb-news a {
    color: #aaa;
}
.menu-zone-unit a {
    color: #eee !important;
}
.quotation-merchant-container p.quotation-merchant-name a {
    color: #eee;
}
.product-detail-function .btn {
    color: #aaa;
}
.menu-mega-label * {
    background-color: #00a499 !important;
}
.referral-wrapper .title {
    color: #eee;
}
.product-detail-tab-bar ul li.active:hover a,
.merchant-detail-tab-bar ul li.active:hover a,
.page-layout-tab-bar ul li.active:hover a {
    color: #eee;
}
.product-detail-tab-bar ul li.active a,
.merchant-detail-tab-bar ul li.active a,
.page-layout-tab-bar ul li.active a {
    color: #fff;
}
.form-control {
    color: #eee;
}
.tab-news-pane-wrapper a {
    color: #ddd;
}
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:focus,
.nav-tabs > li.active > a:hover {
    background-color: #333 !important;
    color: white !important;
}
.mega-info-detail ul li li a {
    color: #aaa;
}
.news-page-title h3 {
    color: #fff;
}
.product-detail-tab-bar ul li,
.merchant-detail-tab-bar ul li,
.page-layout-tab-bar ul li {
    color: #eee;
}
.news_google_adsense {
    display: none;
}
div[id^='ad'] {
    display: none;
}
.search-wrapper .form-control {
    border-color: #000;
    border-radius: 2px;
}
.swiper-slide {
    border: 0;
}
img {
    border: 0px !important;
}
a#px1_closeBtn {
    display: none;
}
.pagination > li > a:hover {
    color: #000;
    background-color: #00a499;
}
.pagination > li > a {
    color: #00a499;
    background-color: #000;
    border-color: #666;
}
.pagination > .active > a:hover {
    color: #000;
    border-color: #f1c400;
}
.pagination > .active > a {
    color: #f1c400;
    background-color: #000;
    border-color: #f1c400;
}
a.btn.btn-default.view-grid {
    background-color: #000 !important;
}
a.btn.btn-default.view-list.active {
    background-color: #ccc !important;
}
.ec-menu ul li a {
    color: #eee !important;
}
.ec-menu ul li.ec-menu-haschild ul li a {
    color: #eee !important;
}
.ec-menu ul li:hover {
    background: #fcfcfc !important;
}
.ec-menu ul li:hover a {
    color: #00a499 !important;
}
.ec-menu ul li.active {
    border-left: 3px solid #f0c400 !important;
    background: #fff !important;
}
.ec-menu ul li.active a {
    color: #00a499 !important;
    background-color: #333;
}
.ec-product-title a {
    color: #eee;
}
input.form-control#pager_page {
    border-color: #666;
}
.oul-detail-tab-bar [type=radio]:checked ~ label {
    color: #fff;
}
.news-row-title a {
    color: #fff;
}
.news-row-intro a {
    color: #ddd;
}
.im-list-trade-username,
.im-list-trade-offer {
    color: white;
}
}
