css:
@-moz-document domain("123movieshub.to") {
  /*https://imgur.com/a/iixuj*/
body{ background: #1E212A url(https://i.imgur.com/2leQsqI.jpg) top repeat-x; color: #eee;}
a,a:hover, #footer .footer-link-head, #slider .slide-caption h2{ color: #F8AA0C;}
.cluetip-default .ui-cluetip-header{ color: #F8AA0C !important;}
header{ background: #282C35 !important;}
header .header-logo:after{ background: #555;}
.modal-cuz .modal-content:before{ content: ""; width: 100px; height: 100px; left: 10px; top: -70px; background-image: url(https://i.imgur.com/vbirXhv.png); position: absolute; background-position: 50% 25%; background-size: cover;}
#menu ul.top-menu li a{ color: #ddd;}
#logo{ background-image: url(https://i.imgur.com/y9iRhAO.gif);}
.home-main .hm-logo a{background-image: url(https://i.imgur.com/dKAdhVo.png) !important;;}
.movies-list-wrap .ml-title .nav-tabs li a{ color: #ddd;}
.nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover, .nav > li > a:hover, .nav > li > a:focus{ background-color: #40444D !important; border-radius: 3px;}
#menu .sub-container{ background: #1E212A !important; border-left: none; border-bottom: none; border-right: none;}
#menu .sub-container ul.sub-menu li a{ color: #aaa !important;}
#bread .breadcrumb a{ color: #777;}
#search input.search-input{ border: none; background: #40444D; box-shadow: none;}
#search input.search-input:focus{ background: #474c59;}
.search-suggest{ background: #33373F;}
.search-suggest ul li{ border-bottom: 1px solid #40444D;}
.search-suggest ul li:hover{ background: rgba(255,255,255,0.05);}
.search-suggest ul li .ss-info a.ss-title{ color: #eee; font-weight: normal;}
#top-news{ background: #282C35;}
#top-news .top-news-content ul.tn-news li:hover{ background: rgba(255,255,255,0.05);}
#top-news .top-news-content ul.tn-premium li a{ background: rgba(255,255,255,0.05);}
#top-news .nav, #top-news .top-news-content ul.tn-news li.view-more{background: #1e212a}
#mv-info .mvi-content{ background: rgba(255,255,255,0.05);}
#mv-keywords{ background: #40444D;}
#mv-keywords a{ color: #fff;}
#mv-keywords a:hover{ color: #F8AA0C;}
#mv-info .mvi-content .mvic-desc{ border-right: none;}
.modal-content{ background: #282C35;}
.modal-footer{ border-top: none; background: rgba(255,255,255,0.05);}
.close{ color: #fff !important; text-shadow: none; opacity: 1;}
.ui-cluetip-content{ color: #222;}
.mobile-btn{ background: #252525; border-bottom: none;}
.mobile-menu, .mobile-search{ background: #444; color: #eee;}
.mobile-menu.active, .mobile-search.active{background: #F48625 !important;}
#commentfb{ background: #252525;}
.popover-content{ color: #333;}
.logged .logged-feed .dropdown-menu > li > a > strong{color: #f8aa0c;}
#list-eps{ background: #2D2D2D;}
#list-eps .le-server{ border-bottom: 1px solid #3f3f3f;}
.filter-toggle{ background: #40444D;}
#filter{ background: #282C35;}
.filter-toggle.active{ background: #f48625;}
#filter .fc-title{ color: #f8aa0c;}
#filter ul{ background: rgba(255,255,255,0.05); border: none;}
#filter ul.fc-main-list li a{ background: #32363F; color: #fff;}
#filter ul.fc-main-list li a:hover{ background: #f48625;}
#filter .filter-btn{ border-top: none; background: rgba(255,255,255,0.05);}
.pagination > li > a, .pagination > li > span{ background: #252525; color: #ddd;}
#list-eps .le-server .les-content .btn-eps{ background: #777; border-bottom-color: #666; color: #eee;}
.infopage{ background: #252525;}
.infopage .infopage-head, .box .box-head{ background: #40444D; border-bottom: none;}
.infopage, .box{ background: #282C35;}
.news-list-item{ border-bottom: 1px solid #333;}
.news-list-item:hover{ background: rgba(255,255,255,0.02);}
.news-list-item .info h2 a{ color: #eee;}
.news-list-item .info p{ color: #aaa;}
.news-view-content{ border-bottom: 2px solid #555;}
#comment{ border-top: none !important; background: #ccc;}
#comment h3.title{ color: #333;}
.movies-embed .me-li{ border-bottom-color: #444;}
footer{ background: #282C35; position: relative; padding-top: 200px;}
footer:before{ content: ""; width: 100%; height: 200px; background: url(https://i.imgur.com/8UQjpnp.jpg) bottom repeat-x; position: absolute; top: 0px; left: 0;}
.report-list{ border-color: #555;}
.report-list .rl-block{ border-color: #555;}
.report-textarea textarea{ background: rgba(0,0,0,0.2); border-color: #555; color: #fff;}
.logged .btn-logged i{ color: #ddd;}
.logged .avatar .fa{ color: #ddd;}
.logged .dropdown-menu{ background: #333;}
.logged .logged-user .dropdown-menu > li > a:hover{ background: #444;}
.logged .logged-feed .dropdown-menu > li{ border-color: #555;}
.logged .logged-feed .dropdown-menu > li a{ color: #eee;}
.logged .logged-feed .dropdown-menu > li a:hover{ background: #444;}
.logged .logged-feed .dropdown-menu > li.more a{ background: #666; color: #eee;}
.logged .logged-user .dropdown-menu > li > a{ color: #ddd;}
.logged .logged-user .dropdown-menu > li{ border-color: #555;}
.profiles-wrap{ background: #222; color: #ddd;}
.profiles-wrap .pp-main{ background: #333;}
.sidebar .sidebar-menu ul li a{ color: #aaa;}
.sidebar .sidebar-menu ul li.active{ background: #333;}
.sidebar .sidebar-menu ul li.active a{ color: #eee;}
.user-content .uct-info .block{ border-bottom: 1px solid #555;}
.profiles-wrap .pp-main .ppm-head ul.nav-tabs li.active a, .profiles-wrap .pp-main .ppm-head ul.nav-tabs li a:hover{ color: #fff; background: #666 !important;}
.noti-content ul li a{ color: #ddd;}
.noti-content ul li{ border-bottom: 1px solid #444;}
.offer-content ul li{ border-color: #444;}
#commentfb{ background: #ddd;}
.letter-movies-lits .table-striped{ background: #222;}
.letter-movies-lits .table-striped > tbody > tr > td{ border-color: #333;}
.letter-movies-lits .table-striped > tbody > tr:nth-of-type(2n+1){ background: #292929;}
.letter-movies-lits a{ color: #ddd;}
.movies-letter a.btn-letter{ background: #333; color: #fff;}
.mgbox a{ color: #fff !important;}
.social-home{ background: #40444D;}
#search input.search-input{ color: #fff;}
.mv-ft-logo{ background-image: url(https://i.imgur.com/y9iRhAO.gif);}
.faq-div ul li{ background: none;}
.faq-div a h3{ color: #ddd;}
.faq-div ul li{ border-bottom: 1px solid #333;}
#switch-mode{ display: none;}
.movies-list-wrap .ml-title span, #top-news .top-news-content ul.tn-notice li a span i, .alert-bottom, #menu .sub-container ul.sub-menu li:hover a, .search-suggest ul li.ss-bottom a{ background: #F48625;}
.search-suggest{ border-color: #F48625;}
.alert-bottom .alert-bottom-content{ position: relative;}
.alert-bottom .alert-bottom-content:before{ content: ""; width: 250px; height: 173px; background: url(https://i.imgur.com/dS3GCIi.png) no-repeat; position: absolute; bottom: -30px; left: -280px;}
#list-eps .le-server .les-content .btn-eps{ background: #40444D; border-color: #32353d;}
.btn-successful, .btn-successful:hover, .btn-successful:focus, #list-eps .le-server .les-content .btn-eps.active{ background: #F48625 !important; border-color: #d8761f !important;}
.btn-successful:hover{ opacity: 0.9;}

.box .box-head .nav-tabs li.active a{ background: #F48625 !important; border-radius: 0;}
.pagination > .active > a, .pagination > .active > a:focus, .pagination > .active > a:hover, .pagination > .active > span, .pagination > .active > span:focus, .pagination > .active > span:hover{ background: #F48625;}
.pagination > li > a, .pagination > li > span{ background: #40444D;}
#menu ul.top-menu li:hover a, #menu ul.top-menu li.active a{ color: #F8AA0C; border-color: #F8AA0C;}
#top-news .nav li.active a{ border-color: #F48625;}
.movies-list .ml-item .mli-eps{ background: rgba(28,85,96,0.9);}
.movies-list .ml-item .ml-mask:after{ background-image: url(https://i.imgur.com/vDP5l0B.png);}
#menu .sub-container{border-color: #F48625;}
.top-content:before{ content: ""; width: 200px; height: 300px; background: url(https://i.imgur.com/UkrIIdJ.png); position: absolute; right: -130px; top: 00px;}
.top-content:after{ content: ""; width: 139.5px; height: 134px; background-image: url(https://i.imgur.com/93mmccj.png); background-position: 50% 25%; background-size: cover; position: absolute; top: 20px; left: -160px;}
#bar-player{ background: #40444D;}
#list-eps{ background: #262A33;}
/*#mv-info .mvi-cover:before{ background-image: url(https://i.imgur.com/vDP5l0B.png); width: 110px; height: 100px; border-radius: 40px; border-width: 4px;}
#mv-info .mvi-cover:hover:before{ border-width: 6px;}*/
#mv-info .mvi-cover:before{ background-image: url(https://i.imgur.com/vDP5l0B.png);}
.infopage .ip-right{ margin-top: 0; background: #ccc;}
.tips-content, .cookie-result{ background: #282C35 !important;}
.tips-tabs ul li a:hover, .tips-tabs ul li a:focus{ background: rgba(255,255,255,0.9) !important;}
.tips-tabs ul li.active a:hover, .tips-tabs ul li.active a:focus{ background: #ebf8e2 !important;}
.logged .dropdown-menu{ background: #40444D;}
.logged .logged-user .dropdown-menu > li.active, .sidebar .sb-title{ background: #F48625 !important;}
.profiles-wrap .pp-main .ppm-head .ppmh-title{ background: #40444D;}
.profiles-wrap{ background: #101823;}
.profiles-wrap .pp-main, .sidebar .sidebar-menu ul li.active{ background: #282C35;}
.noti-content ul li a strong, .qtip .qtip-title{ color: #f8aa0c !important;}
@media screen and (max-width:1919px){
	.top-content:before, .top-content:after, .alert-bottom .alert-bottom-content:before{ display: none;}
}
@media screen and (max-width:1600px){
	#menu ul.top-menu li a{ padding: 0 2px;}
}
@media screen and (max-width:1325px){
	#logo{ height: 41px;}
	#menu{ margin-left: 10px;}
	#menu ul.top-menu li a{ font-size: 14px;}
}
@media screen and (max-width: 991px){
	.movies-list-wrap .ml-title span{ color: #eee; height: 38px; line-height: 38px;}
	#menu .sub-container{ background: none !important;}
	header .header-logo{ margin-left: -95px;}
	.mv-rating{ background: #353535; color: #ddd;}
	.mv-rating .btn{ color: #ddd;}
	.movies-list-wrap .ml-title span{ padding-left: 10px; padding-right: 10px;}
}
@media screen and (max-width: 991px){
	#search{ background: #222;}
}
@media screen and (max-width: 520px){
	#logo{ width: 103px; height: 27px;}
	header .header-logo{ margin-left: 0;}
	footer{padding-top: 20px;}
	footer:before{display: none;}
}
}
