css:
@-moz-document domain("bing.com") {
#b_results{
    width:700px !important;;
  }
  
  #b_results > li:first-child {
    padding-top: 30px !important;
  }
  
  #b_results > .b_algo {
    padding: 28px 20px 0 !important;;
    padding: 25px !important;
    border-bottom: 1px solid rgba(192, 195, 195, 0.18);
  }

  #b_results > li a:visited{
    color: #111c5a !important;;
  }
  
  #b_results .b_algo .b_caption p {
    color: #333333 !important;;
    padding-top: 10px !important;
    line-height: 30px !important;
  }
  
  
  .b_footer {
     padding: 30px 0 35px !important;
  }
  
  .b_pag > nav {
    margin-top: 20px !important;
  }
  

  
  #b_context .b_ans, #b_context #wpc_ag {
    background-color: #fbfbfb !important;
    padding: 34px !important;
  }
   @media (max-width: 1024px) {
      #b_results{
        width:560px !important;;
      }
   }
 }
