css:
@-moz-document domain("twitter.com") {
div.promoted-tweet {
  display: none;
}
.tweet-has-context {
  display: none;
}
body {
  background-color: #f3f3f6;
  font-family: 'Helvetica Neue', Helvetica, sans;
  font-size: 12px;
  line-height: 18px;
}

div.Footer.module.roaming-module {
  display: none;
}
li.promoted-trend {
  display: none;
}

.ProfileTweet-actionList {
  margin-bottom: 1px;
  margin-top: 4px;
}

.TweetTextSize {
  font-size: 13px;
}
p.TweetTextSize {
  line-height: 21px;
}
.tweet .QuoteTweet {
  margin-top: 8px;
  margin-right: 55px;
}

.QuoteTweet [data-item-type="tweet"] {
  padding: 6px 12px;
}

.js-tweet-text-container {
  margin-right: 25px;
}

.tweet .card2 {
  margin-top: 6px;
}

.card2 {
  font-size: 12px;
  margin-right: 55px;
}

.TwitterCard-title, 
.TwitterCard .SummaryCard-content p {
  font-size: 13px;
  color: #666;
  line-height: 16px;
}

.TwitterCard .SummaryCard-destination {
  color: rgba(0, 25, 150, .6);
  font-size: 13px;
  font-weight: bolder;
}

.AdaptiveMedia {
  max-width: 452px;
}
.AdaptiveMedia.is-square, 
.AdaptiveMedia.is-square .AdaptiveMedia-videoPreview {
  max-height: 300px;
}
.stream-item .content .Tombstone {
  margin-right: 55px;
}

iframe {
  max-height: 322px;
}

.account-group .UserBadges, .fullname, 
.account-group .username, .time, 
.time a, .created-via, 
.ReplyingToContextBelowAuthor {
  font-size: 11px;
}

}
