css:
@-moz-document domain(wikipedia.org) {
div#mw-head {
    position: absolute;
    top: 0;
    right: 0;
    width: 100%;
    background-color: #1f1f1f;
}
body {
    background-color: #1f1f1f;
}
.mw-body {
    margin-left: 10em;
    border: 1px solid #ffffff;
    border-right-width: 0;
    margin-top: -1px;
}
.mw-body, .parsoid-body {
    padding: 1em;
    background-color: #191919;
    color: #ffffff;
    direction: ltr;
}
div.vectorTabs li.selected a, div.vectorTabs li.selected a:visited {
    color: #fff;
    text-decoration: none;
    background-color: #191919;
}
div.vectorTabs li a {
    display: inline-block;
    height: 1.9em;
    padding-left: 0.5em;
    padding-right: 0.5em;
    color: #cecece;
    cursor: pointer;
    font-size: 0.8em;
    background-color: #292929;
}
h1, h2, h3, h4, h5, h6 {
    color: #fff;
    background: none;
    font-weight: normal;
    margin: 0;
    overflow: hidden;
    padding-top: 0.5em;
    padding-bottom: 0.17em;
    border-bottom: 1px solid #a2a9b1;
}
a:visited {
    color: #908bc3;
}
a {
    text-decoration: none;
    color: #a3bfec;
    background: none;
}
.infobox {
    border: 1px solid #ffffff;
    border-spacing: 3px;
    background-color: #191919;
    color: white;
    margin: 0.5em 0 0.5em 1em;
    padding: 0.2em;
    float: right;
    clear: right;
    font-size: 88%;
    line-height: 1.5em;
}
element.style {
    text-align: center;
    font-size: 125%;
    font-weight: bold;
    background-color: #ffffff;
    color: #191919;
}
div#mw-panel div.portal div.body ul li a:visited {
    color: #908bc3;
}
div#mw-panel div.portal div.body ul li a {
    color: #a3bfec;
}
div#mw-panel div.portal h3 {
    font-size: 0.75em;
    color: #ffffff;
    font-weight: normal;
    margin: 0.5em 0 0 0.66666667em;
    padding: 0.25em 0;
    cursor: default;
    border: 0;
}
.navbox, .navbox-subgroup {
    background: #1f1f1f;
}
.navbox-abovebelow, th.navbox-group, .navbox-subgroup .navbox-title {
    background: #1f1f1f;
}
.catlinks {
    border: 1px solid #ffffff;
    background-color: #191919;
    padding: 5px;
    margin-top: 1em;
    clear: both;
}
.navbox {
    box-sizing: border-box;
    border: 1px solid #ffffff;
    width: 100%;
    clear: both;
    font-size: 88%;
    text-align: center;
    padding: 1px;
    margin: 1em auto 0;
}
.mw-wiki-logo {
    background-image: url(https://i.imgur.com/91TuMf6.png);
}
    table.tmbox-protection, table.tmbox-notice {
    border: 1px solid #ffffff;
}
table.tmbox {
    margin: 4px 10%;
    border-collapse: collapse;
    border: 1px solid #ffffff;
    background: #191919;
}
 
element.style {
    clear: both;
    position: relative;
    box-sizing: border-box;
    width: 100%;
    margin: 1.2em 0 6px;
    min-width: 47em;
    border: 1px solid #fff;
    background-color: #191919;
    color: #000;
    white-space: nowrap;
}
#toc, .toc, .mw-warning, .toccolours {
    border: 1px solid #ffffff;
    background-color: #191919;
    color: white;
    padding: 5px;
    font-size: 95%;
}
.tocnumber {
    padding-left: 0;
    padding-right: 0.5em;
    color: #fff;
}

}
