css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("metacademy.org") {
  
  #main {
    color: #aaa;
  }
  
  /*** Main Page ***/
  
  body {
    background-color: #222;
  }
  
  #main-search-input {
    background-color: #282828;
    border-color: #333;
  }
  
  .placeholder {
    color: #666;
  }
  
  /*** Sub Menu Pages e.g. Roadmaps, Course Guides ***/
  
  .search-res-wrapper {
    background-color: #222;
  }
  
  h1 {
    border-bottom-color: #333;
  }
  
  .search-res-li + .search-res-li {
    border-top-color: #333;
  }
  
  /*** Individual Page ***/
  
  /* Top Headers */
  .roadmap-header-nav a {
    color: #999;
    border-color: #333;
  }
  
  .roadmap-header-nav a:hover {
    background-color: #333;
  }
  
  /* Text */
  
  .container {
    color: #aaa;
  }
  
  .content {
    color: #aaa;
  }
  
  .content blockquote {
    background-color: #282828;
    border-left-color: #282828;
  }
  
  /* Images */
  .center-image {
    opacity: 0.5;
  }
  
  /* Separators */
  .content h2, .content h1 {
    border-bottom-color: #333;
  }
  
  /* Incomplete */
  .missing-link {
    color: #666;
  }
  
  /*** Learning Plan Page ***/
  
  /* Left Pane */
  
  #concept-title-header {
    background-color: #222;
    border-color: #333;
  }
  
  #concept-list-wrapper {
    border-right-color: #333;
  }
  
  .learn-title-display {
    color: #aaa;
    border-bottom-color: #333;
  }
  
  .learn-title-display:hover {
    background-color: #333;
  }
  
  .learn-title-display.clicked-title {
    background-color: #282828;
    color: #aaa;
  }
  
  .node-detail-view {
    background-color: #222;
    color: #aaa;
  }
  
  .resource-view {
    color: #aaa;
  }
  
  .node-detail-title {
    color: #666;
  }
  
  .node-detail-subtitle {
    color: #484848;
  }
  
  .dependency-reason, .outlink-reason {
    color: #666;
  }
  
  /* Link */
  .external-link:hover {
    color: #428BCA;
  }
  
  /* Topmost Bar */
  
  #header-search-input, user-form-field input {
    background-color: #282828;
  }
  
  .header-menu a.header-a {
    color: #aaa;
  }
  
  .meta-home-link {
    opacity: 0.5;
  }
  
  /* Header */
  #concept-list-wrapper h1, #concept-title-header h1 {
    color: #aaa;
  }
  
  /* Graph Symbol */
  #explore-learn-button-wrapper button, #hovertext-buttons button {
    background-color: #222;
  }
  
  #header-search-input, user-form-field input {
    border-color: #333;
  }
  
  .el-nav-button img {
    filter: invert(80%);
  }
  
  /* Search */
  #concept-list-search input {
    background-color: #222;
    border-color: #333;
  }
  
  #concept-list-search #cancel-search-input {
    background-color: #1c1c1c;
  }
  
  /* Transition Screen */
  .fetching-detail-waitbar {
    background-color: #fff; /* invert -> #222 */
    filter: invert(86.5%);
  }
  
  /* 'Learning Plan' collapsed side button */
  .small-vp-button {
    background-color: #282828;
    color: #666;
  }
  
  /** Graph View **/
  
  #graph-view svg .concept-g circle {
    fill: #282828;
  }
  
  #graph-view svg g.concept-g circle {
    fill: #282828;
  }
  
  svg g.concept-g circle {
    fill: #282828;
  }
  
  .scoped .concept-g .dep-circle {
    fill: #282828;
  }
  
  g.concept-g.scoped-circle-g > circle, g.concept-g.scoped-circle-g .dep-circle, g.concept-g.scoped-circle-g .ol-circle, g.concept-g.focused-circle-g > circle {
    fill: #282828;
  }

}
