css:
@-moz-document domain(h1z1.com) {
	.leaderboards-page {
	    background: none #2b2b2b;
	}

	.leaderboards-page .leaderboard-table tbody tr {
	    background: #2b2b2b;
	}

	.leaderboards-page .leaderboard-table tbody tr[role=row] td {
	    white-space: nowrap;
	    color: white;
	}

	.leaderboards-page .leaderboard-controls {
	    background-color: #2b2b2b;
	}
  
	.page-next {
		background: white;
	}

	.page-prev {
		background: white;
	}

	.page-first {
		background: white;
	}

	.page-last {
		background: white;
	}

	.leaderboards-page .leaderboard-controls .leaderboard-search {
		background: #902222;
		border: 1px solid #6b6b6b;
		color:white;
	}

	.leaderboards-page .leaderboard-controls .autocomplete-suggestions {
		background:#6b6b6b;
		border-color: #6b6b6b;
		color:#ffffff;
	}

	.leaderboards-page .leaderboard-controls .leaderboard-search-box .input-addon {
		color: white;
	}

	.leaderboards-page .leaderboard-controls .selectric {
		background: #902222;
		color: white;
		border-color: #6b6b6b;
	}

	.leaderboards-page .leaderboard-controls .selectric-items {
		background:#6b6b6b;
		border-color: #6b6b6b;
		color:#ffffff;
	}

	.leaderboards-page .leaderboard-controls .selectric-items li {
		background:#6b6b6b;
		border-color: #6b6b6b;
	}

	.leaderboards-page .leaderboard-controls .selectric-items li.selected, .leaderboards-page .leaderboard-controls .selectric-items li.highlighted {
		background: #902222;
	}

	.leaderboards-page .leaderboard-controls .selectric-items ul {
		background-color: #6b6b6b;
	}

	.leaderboard-tier-label {
		color:white;
	}

	.leaderboard-region-label {
		color:white;
	}

	.leaderboard-team-mode-label {
		color:white;
	}

	.result-count-label {
		color:white;
	}

	.leaderboards-page .leaderboard-controls input, .leaderboards-page .leaderboard-controls option{
	  	background: #902222;
		color: white;
		border-color: #6b6b6b;
	}

	.leaderboards-page .leaderboard-table tbody tr:hover {
		background-color: #902222;
	}

	.leaderboards-page .leaderboard-contents {
		padding-bottom: 85px;
	}

	.leaderboards-page .search-message {
		font-size: 25px;
		margin-bottom: 15px;
	}
}
