css:
@-moz-document domain("news.ycombinator.com") {
/**
 *
 * Title: hache-hackernews
 * Author: @hachesilva
 * Description: More readable HackerNews tuned to personal taste
**/

body {
    margin: 0;
    font-size: 16px;
}
#hnmain {
    max-width: 768px;
    background: transparent;
}
[bgcolor="#ff6600"] {
    background-color: #af4f0b;
}
[bgcolor="#ff6600"] *,
[bgcolor="#ff6600"] a,
[bgcolor="#ff6600"] a:link,
[bgcolor="#ff6600"] a:visited {
    color: #fff;
}
.fatitem {
    width: 100%;
}
.athing .title {
    color: #333;
    font-size: 1.1rem;
    line-height: 1.4;
}
.athing .title a {
    color: #333;
}
.subtext {
    font-size: 0.72rem;
    color: #555;
}
.athing + tr + tr + tr {
    color: #333;
    font-size: 1rem;
}
.athing + tr + tr + tr td {
    color: #333;
    font-size: 1rem;
    padding: 1em 0;
}
form textarea {
    box-sizing: border-box;
    height: 2em;
    width: 100%;
    transition: height 0.35s;
}
form textarea:focus {
    height: 10em;
}
form [type="submit"] {
    font-family: sans-serif;
    padding: 1em 1.5em;
    border-radius: 0.5rem;
    border: 1px solid #666;
    background-color: #ccc;
    cursor: pointer;
}
.comtr .default > div:first-of-type {
    font-size: 1rem;
    border-bottom: 1px solid #ccc;
    padding: 0.5em 0;
}
.comhead {
    font-size: 1rem;
}
.comhead .hnuser,
.comhead .hnuser:link {
    color: #c55216;
}
.comhead .age {
    font-size: 0.9rem;
}
.comment {
    font-size: 0.9rem;
    line-height: 1.4;
    padding: 1em 0 1em 1.5em;
}
}
