css:
ytd-browse.style-scope.ytd-page-manager {
    background: linear-gradient(to bottom, #28343b 0%, #828c95 64%, #b5bdc8 100%);
}
div.style-scope.ytd-app {
    background: linear-gradient(to bottom, #28343b 0%, #828c95 61%, #828c95 61%, #828c95 84%, #828c95 84%, #b5bdc8 100%);
}
span.style-scope.ytd-shelf-renderer {
    color: #ffffff;
}
span.style-scope.ytd-grid-video-renderer {
    color: #dbd4db;
    font-style: italic;
}
div.style-scope.ytd-masthead {
    background: linear-gradient(to bottom, #b5bdc8 0%, #828c95 36%, #28343b 100%);
    background-color: #424242;
    border-style: none;
}
a.yt-simple-endpoint.style-scope.ytd-grid-video-renderer {
    color: #f7f7f7;
    font-family: 'Catamaran', sans-serif;
    text-transform: capitalize;
    font-weight: bold;
    font-style: normal;
    font-variant: normal;
}
img.style-scope.ytd-moving-thumbnail-renderer.fade-in {
    border-style: none;
    border-color: #ffffff;
    border-width: 2px;
}
ytd-moving-thumbnail-renderer.style-scope.ytd-thumbnail {
    border-style: double;
    border-color: #1ab7f0;
    border-width: 2px;
    border-bottom-width: 3px;
}
yt-icon.style-scope.ytd-moving-thumbnail-renderer.fade-in {
    border-style: solid;
    border-width: 2px;
}
span.guide-entry-count.style-scope.ytd-guide-entry-renderer {
    color: #f5fcff;
}
a.yt-simple-endpoint.style-scope.yt-formatted-string {
    color: #1ab7f0;
}
/* i really want this to be global */
