css:
@namespace url(http://www.w3.org/1999/xhtml);
/*Shota Lelouch Familiar*/
/* i really want this to be global */
/*Images - pictures*/

@-moz-document domain("eldarya.es") {

#pet-headerPortrait-base {
    background: url("https://68.media.tumblr.com/b8bb68a7f9917bd3c804b1aa125a0a97/tumblr_ou9urq8olY1sedhvgo5_250.jpg") -20px top no-repeat !important;
    background-size: auto 100px !important;
}
#pet-portrait {
    background: url("https://68.media.tumblr.com/c3f04c1c88043a1d76f1dd6b97799f1e/tumblr_oucb7urfqF1sedhvgo2_540.jpg") center no-repeat !important;
    background-size: auto 450px !important;
    height: 450px !important;
}
#pet-portrait > img[src*="/static/img/pet/icon/"] {
    display: none !important;
}
/*Infos*/

#pet-info-name:before {
    content: "Shota Lelouch" !important;
    font-size: 20px !important;
}
#pet-info-name {
    font-size: 0 !important;
}
#pet-info-evolve > .on {
    font-size: 0 !important;
}
.pet-info-evolve-icon {
    font-size: 13px !important;
}
.pet-info-evolve-icon:after {
    content: "Lulu es completamente shota";
}
#pet-info-racegender:before {
    content: "Principe" !important;
    font-size: 16px !important;
    line-height: 20px !important;
}
#pet-info-racegender {
    font-size: 0 !important;
    margin-top: 5px;
}
img[src*="/static/img/f.png"] {
    background: url("http://www.eldarya.es/static/img/m.png") !important;
    padding-left: 14px !important;
    padding-top: 0px !important;
    width: 0 !important;
    height: 0 !important;
    margin-bottom: 5px !important;
}
/*Comida - food*/

#pet-info-food > img {
    background: url("https://s-media-cache-ak0.pinimg.com/736x/c8/ce/d5/c8ced5fd3cc15ea3a08b04af55d8111f--flan-album.jpg") !important;
    background-size: 60px 60px !important;
    margin-top: -5px !important;
    padding-left: 60px !important;
    padding-top: 60px !important;
    width: 0 !important;
    height: 0 !important;
}
#pet-info-food {
    font-size: 0 !important;
}
#pet-info-food:before {
    content: "Pudín/Flan" !important;
    font-size: 16px !important;
}
#pet-info-food > span {
    font-size: 16px !important;
}
/*Lelouch en tu perfil*/

#player-display-pet {
    background: url("https://68.media.tumblr.com/161a3eb59bcdc2a468b385cdb0d5c69c/tumblr_ou9urq8olY1sedhvgo3_1280.png") no-repeat;
    background-size: auto 100%;
    height: 200px !important;
    width: 250px !important;
}
#player-display-pet > img {
    display: none !important;
}

img[src="/i18n/static/fr/logo.png"] {
    width: 0;
    height: 0;
    background: url("https://68.media.tumblr.com/f841891fe6e30f5c6fc294ec9250820d/tumblr_ou9urq8olY1sedhvgo2_400.png");
    padding-left: 390px;
    padding-top: 160px;
    position: absolute;
    top: 20px;
    left: 50px;
}
}
