css:
@-moz-document regexp("https?://www.zhihu.com/question/.*") {
/*
名称:知乎-回答页
正则网站:https?://www.zhihu.com/question/.*
域上网址:
作者:水月仙森
联系方式:chiangchinming@gmail.com
最近更新:2018.1.4
*/

/*顶部悬浮*/
header[class="Sticky AppHeader is-fixed is-hidden"] {
	display: none !important;
}

/*第一行:搜索框:居中*/

/*.AppHeader-inner {
	margin: auto 0 auto 12%;
}  
*/

/*第一行:搜索框(鼠标划过显示)*/
.Sticky.AppHeader.is-fixed {
	position: absolute;
	top: 0;
	padding: 0;
	margin: 0;
	opacity: 1;
	z-index: 10;
	border-bottom: 0px;
	box-shadow: none;
}

.AppHeader:hover,
.AppHeader.is-fixed:hover {
	opacity: 1;
}

/*搜索框下面的内容上移*/

/*.QuestionPage {
	position: relative;
	top: -64px;
}
*/

/*****标题*****/
.QuestionHeader {
	box-shadow: none;
}

/*标题居中*/
.QuestionHeader-main {
	width: 70%;
	text-align: center;
	padding: 0px;
	margin: 0 auto;
}

/*标题字号*/
h1.QuestionHeader-title {
	margin: 0 auto !important;
	font-size: xx-large;
	font-weight: 700;
	line-height: 1.3;
}

/*标题左上:标题标签*/
.QuestionHeader-tags,

/*标题下方:标题内容*/
.QuestionHeader-detail,

/*标题下方:评论分享*/
.QuestionHeader-footer,

/*标题右方:问题关注*/
.QuestionHeader-side {
	display: none;
}

/*****正文:话题下所有答案******/

/*正文 上方-邀请回答*/
.Card.QuestionInvitation {
	display: none;
}

/*全文背景颜色*/
body {
	background-color: #FFFFFF !important;
}

/*正文-边框处理*/
.Question-mainColumn {

	/*增加阴影*/
	-moz-box-shadow: 0px -1px 10px #BBB;
	-webkit-box-shadow: 0px -1px 10px #BBB;
	box-shadow: 0px -1px 10px #BBB;/*调整内外边距*/
	margin: 0px !important;
	padding-bottom: 0px;
	padding: 20px !important;/*圆角*/
	-moz-border-radius: 20px;
	-webkit-border-radius: 20px;
}

/*正文-内容居中*/
.Question-mainColumn,
.Layout-main {
	width: 100% !important;
	background-color: #FFFFFF !important;
	margin: 0;
	padding: 0;
}

/*正文最下方:回答*/
.Card.QuestionAnswers-answerAdd {
	margin: 0 !important;
	position: relative;
	bottom: 0px;
	z-index: 10;
	opacity: 0;
}

.Card.QuestionAnswers-answerAdd:hover {
	opacity: 1;
	transition-duration: 1s;
}

/*
.Card.QuestionAnswers-answerAdd,
.AnswerAdd,

*/

/*****正文右部:相关问题、live推荐*****/
.Question-sideColumn {
	display: none !important;
}

/*长答案底部悬浮:评论分享收藏感谢;收起*/
.ContentItem-actions.Sticky.RichContent-actions.is-fixed.is-bottom {
	width: inherit;
	opacity: 0;
}

.ContentItem-actions.Sticky.RichContent-actions.is-fixed.is-bottom:hover {
	opacity: 1;
}

/*****其它*****/

/*底部右侧悬浮:建议反馈*/
.CornerButtons,

/*底部左侧悬浮:刘看山*/
.KanshanDiversion,

/*广告插件提示*/
.AdblockBanner {
	display: none;
}
}

@-moz-document regexp("https?://www.zhihu.com/topic.*") {
/*
知乎-话题页-具体话题
https?://www.zhihu.com/topic#.*
2017.10.19
水月仙森
chiangchinming@gmail.com
*/

/*****顶部*****/
.zhi.js-topic-feed-page.topic-feed-page {
	padding: 0px;
}

/***搜索框***/

/*悬浮-顶部搜索框*/
.zu-top {
	position: relative;
	z-index: 20;
	top: 0px;
	opacity: 1;
}

.zu-top:hover {
	opacity: 1;
}

/*****正文:已关注话题列表,关注话题及排序方式,该话题下问题列表*****/

/*搜索框下面的内容上移*/

/*.zu-main-content-inner {
	position: relative !important;
	top: -46px;
}*/

/*正文-居中拉伸*/
.zu-main-content
.zu-main-content-inner {
	width: 100%;
	margin: 0 auto !important;
	background-color: #FFFFFF !important;
}

/*正文-边框处理(问题列表)*/
.zm-topic-list-container {

	/*增加阴影*/
	-moz-box-shadow: 0px -1px 10px #BBB;
	-webkit-box-shadow: 0px -1px 10px #BBB;
	box-shadow: 0px -1px 10px #BBB;/*调整内外边距*/
	margin: 0px !important;
	padding-bottom: 0px;
	padding: 20px !important;/*圆角*/
	-moz-border-radius: 20px;
	-webkit-border-radius: 20px;
}

/*正文右方:其他人关注的话题*/
.zu-main-sidebar {
	display: none;
}

/*正文-各空隙*/
html.is-AppPromotionBarVisible body {
	padding-bottom: 0;
}

.zu-main {
	padding-bottom: 0;
}

/*****正文1:已关注话题列表*****/

/*关注话题个数*/
.topic-feed-title {
	display: none;
}

.zg-wrap.zu-main.clearfix {
	padding-top: 0;
}

/*关注话题列表*/
ul.zm-topic-cat-main.js-topic-cat-main.clearfix {
	border-bottom: 0px;
	padding: 20px;
	display: flex;/*首行居中,剩余行填满*/
	flex-wrap: wrap-reverse;
	justify-content: center;
}

/*****正文2:选择话题及排序方式*****/
.topic-title.clearfix.js-topic-title {
	display: none !important;
}

/*****正文3:选择话题下问题列表*****/
ul.zm-topic-cat-main.js-topic-cat-main.clearfix {
	position: static;
	z-index: 0;
}

/*单问题-左方-认同数*/
.zm-votebar {
	left: -55px !important;
	opacity: 1;
}

/*单问题-下方:关注问题,N条评论,作者保留权利*/
.zm-item-meta.answer-actions {
	display: none !important
}

/*作者保留权利*/
span.zg-bull,
a.meta-item.copyright {
	display: none;
}

/*分享,收藏,没有帮助,举报*/

/*.goog-inline-block.goog-menu-button-caption,*/
a.meta-item.zu-autohide.js-collect,
a.meta-item.zu-autohide.js-noHelp,
span.zg-bull,
zu-autohide,
a.meta-item.zu-autohide.js-report {
	display: none !important;
}

/*****底部*****/
#zh-footer.zh-footer {
	display: none;
}

/*****其它各种*****/

/*悬浮-右下角-建议反馈*/
.FeedbackButton-button-3waL,

/*广告插件提示*/
.AdblockBanner,

/*悬浮-右下角-回到顶部*/
.zh-backtotop {
	display: none;
}

/*****黑色背景-开始*****/

/***黑色背景-1.搜索框***/

/*搜索框-背景*/
.zu-top {
	background-image: none;
	background-color: #34495e;
}

/*搜索框-输入框*/
.zu-top-search-input {
	color: #536a81;
	background: #293a4a;
	border: none;
}

/*搜索框-搜索按钮*/
.zu-top-search-button,

/*搜索框-话题*/
#zh-top-nav-topic,

/*搜索框-发现*/
#zh-top-nav-explore {
	background-image: none !important;
	background: none;
	border: none !important;
	box-shadow: none !important;
}

/*搜索框-提问按钮*/
.zu-top-add-question {
	background-image: none !important;
	border-radius: 3px;
	border: 1px solid #4e6d8d;
	box-shadow: none !important;
	opacity: .9;
	-ms-filter: "alpha(Opacity=90)";
	line-height: 0px;
}

/*搜索框-个人头像悬浮*/
.zu-top-nav-userinfo,
.top-nav-profile a {
	background: #34495E !important;
	border: none !important;
	box-shadow: none !important;
}

.top-nav-profile a:hover {
	background: #293a4a !important;
}

/***黑色背景-2.正文部分***/
.topics-plaza a:nth-child(1),/*进入话题广场(~/topic#??)*/
.zm-topic-cat-item.current a,.zm-topic-cat-item:hover a/*已关注的话题动态(~/topic#??)*/ {
	background: #34495E !important;
}

/*****黑色背景-结束*****/
}

