css:
@-moz-document domain("web.tlhc.ylc.edu.tw"), domain("www.tlhc.ylc.edu.tw") {
body {
	font-size: 12px;
	font-family: "SF Pro TC", "SF Pro Text", "SF Pro Icons", "PingFang TC", "Helvetica Neue", "Helvetica", "Arial", "Microsoft JhengHei", wf_SegoeUI, "Segoe UI", Segoe, "Segoe WP", Tahoma, Verdana, Ubuntu, "Bitstream Vera Sans", "DejaVu Sans", Tahoma, 微軟正黑體, "LiHei Pro", "WenQuanYi Micro Hei", "Droid Sans Fallback", "AR PL UMing TW", Roboto, "Helvetica Neue", "Hiragino Maru Gothic ProN", メイリオ, "ヒラギノ丸ゴ ProN W4", Meiryo, "Droid Sans", sans-serif;
	color: #888;
	margin: 0;
	line-height: 1.75em;
}
.container, body, .foot_03, .mainfoot, .container-inner, .headnav, .md_style1 .mt_03, .md_style1 .mt_02, .md_style1 .mm_03, .md_top.show_title, div.module-link .h5, .module-complex .md_middle .mm_01, .module-complex .md_middle .mm_03, .submenu li a.active div div, .submenu li a div, .submenu li a.active div, .submenu li a div div, .submenu, .submenu-inner, .hasTH .thead, table.hasBD, table.hasBD th, table.hasBD td, .module-complex .md_bottom .mb_03, .module-special .h5 {
	background: transparent;
	border: 0px #000 solid;
}
a {
	color: #808080;
	text-decoration: none;
}
a:hover {
	color: #494949;
	text-decoration: none;
}
a:active {
	color: #999;
}

/*
	Hide Trash
*/
#Dyn_3_10, #Dyn_3_11, #Dyn_3_2, #Dyn_1_1, #Dyn_1_7, #Dyn_1_11, #Dyn_2_1, #Dyn_2_3, #Dyn_2_4, #Dyn_counter, .wrap {
	display: none;
}

/*
	Header
*/
#Dyn_head {
	background-color: #f6f6f6;
	border-radius: 0 0 10px 10px;
}
.head_01 {
	position: relative;
	padding-top: 0px;
}
.sitename {
	color: #000000;
	font-size: 32px;
	font-weight: normal;
	padding: 40px 0;
	text-align: center;
}
.slogan {
	display: none;
}

/*
	Menu
*/
.headnav-inner {
	min-height: 0px;
	padding: 0;
	border-top: 0px #fff solid;
}
.hdmenu {
	float: none;
}
.hdmenu ul li {
	padding-left: 0px;
	margin: 5px 7px;
	float: none;
	display: inline-block;
}
.hdmenu ul li a {
	font-weight: normal;
	font-size: 14px;
	margin: 0;
	padding: 5px;
	border-radius: 5px;
	transition: all 0.1s linear;
}
.hdmenu ul li a:hover {
	color: #000000;
	text-decoration: none;
	background: rgba(0, 0, 0, 0.1);
}
.hdmenu ul li a:active {
	color: #000000;
	text-decoration: none;
	background: rgba(0, 0, 0, 0.2);
}
.hdmenu .menu-item {
	position: relative;
	padding: 0;
	text-align: center;
}

/*
	Sidebar
*/
.md_style1 .mt_03, .md_style1 .mt_02 {
	padding-left: 0;
	height: auto;
}
.md_style1 .mt_01 {
	padding: 10px 0 0 10px;
	background: none;
}
.md_style1 .mt_01 .h3 {
	padding-top: 0;
	padding-left: 0;
	font-size: 22px;
}
.module {
	margin-bottom: 10px;
	background-color: #f6f6f6;
	border-radius: 5px;
}

/*
	Billboard NAV
*/
.submenu li {
	margin-right: 4px;
}
.submenu li a div div {
	height: auto;
	padding: 7px 15px;
	font-size: 20px;
}
.submenu li a div {
	background-color: rgba(0, 0, 0, 0.1);
	border-radius: 5px 5px 0px 0px;
	transition: all 0.1s linear;
}
.submenu li a.active div {
	background-color: rgba(0, 0, 0, 0.4);
	border-radius: 7px 7px 0px 0px;
}
.submenu li a div div {
	color: #2e2e2e;
	transition: all 0.1s linear;
}
.submenu li a.active div div {
	color: #FFF;
}
.submenu {
	height: auto;
}

/*
	Billboard
*/
.showthis .module, .hidethis .module {
	background-color: rgba(0, 0, 0, 0);
}
#Dyn_2_2.user_pm_4 .module {
	min-height: 200px;
}
.user_pm_4 .md_middle div {
	font-size: 14px !important;
}

/* something useless */
#Dyn_2_2 .mm_01 tr td:nth-child(4), #Dyn_2_2 .mm_01 th:nth-child(4), thead {
	display: none;
}
#Dyn_2_2 .mm_01 {
	padding: 10px 0px;
}

/*
	content
*/
#Dyn_2_2 .mm_01 tr td, #Dyn_2_2 .mm_01 th {
	padding: 5px;
}

/* width */
#Dyn_2_2 .mm_01 tr td:nth-child(1), #Dyn_2_2 .mm_01 th:nth-child(1) {
	width: 10%
}
#Dyn_2_2 .mm_01 tr td:nth-child(2), #Dyn_2_2 .mm_01 th:nth-child(2) {
	width: 85%
}
#Dyn_2_2 .mm_01 tr td:nth-child(3), #Dyn_2_2 .mm_01 th:nth-child(3) {
	width: 5%
}

/* ... */
.module-special .h5 {
	padding-left: 0px;
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
	width: 650px;
}

/*
	calendar
*/
iframe[src="https://www.google.com/calendar/embed?src=rjsu.tlhc%40gmail.com&ctz=Asia/Taipei"] {
	opacity: .1;
	transition: all 0.1s linear;
}
p:hover > iframe[src="https://www.google.com/calendar/embed?src=rjsu.tlhc%40gmail.com&ctz=Asia/Taipei"] {
	opacity: 1;
}

/*
	footer
*/
.mainfoot-inner, .mainfoot {
	border-radius: 5px 5px 0px 0px;
}
.mainfoot-inner {
	padding: 10px;
}
}

@-moz-document domain("web.tlhc.ylc.edu.tw"), domain("www.tlhc.ylc.edu.tw") {
/*
	Moe
*/
body {
	background: url(https://i.snag.gy/XuPnql.jpg) #000 center center fixed no-repeat;
	background-size: cover;
}
.mainhead, .cm_01 > div, .mainfoot {
	background: url(https://i.snag.gy/SpJkTq.jpg) #000 center center fixed no-repeat;
	background-size: cover;
}
#Dyn_head, .cm_01 > * > .module, .mainfoot-inner {
	background-color: rgba(255, 255, 255, 0.7);
}
}

