css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trimps.github.io") {

.battleCell.cellColorBeaten.Corruption
{
background: -webkit-linear-gradient(top, #8114ff -1000000%,#6e10cc 1000100%) !important;
}

.battleCell.cellColorBeaten
{
background: -webkit-linear-gradient(top, #008d00 -1000000%, #006c00 1000100%) !important;
}

#grid .battleRow
{
background: -webkit-linear-gradient(top, #999999 -1000000%,#686868 1000090%) !important;
}

.cellColorOverkill
{
background: -webkit-linear-gradient(top, #74e800 -1000000%,#5ab500 1000100%) !important;
}

.cellColorOverkill.voidCell
{
background: -webkit-linear-gradient(top, #845def -1000000%,#7251d6 1000100%) !important;
}

.cellColorBeaten.voidCell
{
background: -webkit-linear-gradient(top, #8114ff -1000000%,#6e10cc 1000100%) !important;
}

.battleCell.cellColorOverkill.Corruption
{
background: -webkit-linear-gradient(top, #845def -1000000%,#7251d6 1000100%) !important;
}

.battleCell.Magma.cellColorBeaten
{
background: -webkit-linear-gradient(top, #633300 -1000000%,#492600 1000100%) !important;
}

.battleCell.Magma.cellColorOverkill
{
background: -webkit-linear-gradient(top, #895000 -1000000%,#704100 1000100%) !important;
}

.battleCell.empoweredCellPoison
{
background: -webkit-linear-gradient(top,rgba(60, 180, 60, 0.75) -1000000%, rgba(45, 135, 45, 0.75) 1000100%) !important;
}

.battleCell.empoweredCellPoison.cellColorBeaten
{
background: -webkit-linear-gradient(top,rgba(40, 135, 40, 0.75) -1000000%, rgba(30, 90, 30, 0.75) 1000100%) !important;
}

.battleCell.empoweredCellPoison.cellColorOverkill
{
background: -webkit-linear-gradient(top,rgba(90, 220, 90, 0.75) -1000000%, rgba(50, 150, 50, 0.75) 1000100%) !important;
}

.battleCell.empoweredCellWind
{
background: -webkit-linear-gradient(top,rgba(72, 90, 156, 0.75) -1000000%, rgba(54, 68, 117, 0.75) 1000100%) !important;
}

.battleCell.empoweredCellWind.cellColorBeaten
{
background: -webkit-linear-gradient(top,rgba(33, 46, 165, 0.75) -1000000%, rgba(23, 33, 110, 0.75) 1000100%) !important;
}

.battleCell.empoweredCellWind.cellColorOverkill
{
background: -webkit-linear-gradient(top,rgba(124, 150, 208, 0.75) -1000000%, rgba(93, 116, 156, 0.75) 1000100%) !important;
}

.battleCell.empoweredCellIce
{
background: -webkit-linear-gradient(top,rgba(60, 60, 240, 0.75) -1000000%, rgba(45, 45, 180, 0.75) 1000100%) !important;
}

.battleCell.empoweredCellIce.cellColorBeaten
{
background: -webkit-linear-gradient(top,rgba(60, 60, 180, 0.75) -1000000%, rgba(40, 40, 120, 0.75) 1000100%) !important;
}

.battleCell.empoweredCellIce.cellColorOverkill
{
background: -webkit-linear-gradient(top,rgba(125, 125, 250, 0.75) -1000000%, rgba(83, 83, 165, 0.75) 1000100%) !important;
}

.battleCell.cellColorBeaten.Healthy
{
background: -webkit-linear-gradient(top, #38829b -1000000%,#246267 1000100%) !important;
}

.battleCell.cellColorOverkill.Healthy
{
background: -webkit-linear-gradient(top, #38a2bb -1000000%,#248287 1000100%) !important;
}

#mapGrid .cellColorBeaten.mapPumpkimp
{
background: -webkit-linear-gradient(top, #CF8600 -1000000%,#9C6200 1000100%) !important;
}

#mapGrid .cellColorOverkill.mapPumpkimp
{
background: -webkit-linear-gradient(top, #F2AA00 -1000000%,#BF8600 1000100%) !important;
}

.battleCell.cellColorBeaten.Pumpkimp
{
background: -webkit-linear-gradient(top, #BF8C00 -1000000%, #8C6700 1000100%) !important;
}

.battleCell.cellColorOverkill.Pumpkimp
{
background: -webkit-linear-gradient(top, #F2B200 -1000000%, #BF8C00 1000100%) !important;
}

#row9 .battleCell.cellColorBeaten.Pumpkimp
{
background: -webkit-linear-gradient(top, #007300 -1000000%, #005c00 1000100%) !important;
}

#row9 .battleCell.cellColorOverkill.Pumpkimp
{
background: -webkit-linear-gradient(top, #67CF00 -1000000%,#4D9C00 1000100%) !important;
}

.battleCell.cellColorCurrent, .battleCell.empoweredCellIce.cellColorCurrent, .battleCell.empoweredCellPoison.cellColorCurrent, .battleCell.empoweredCellWind.cellColorCurrent
{
background: yellow;
}

.battleCell.cellColorBeaten.Pumpkimp.Healthy
{
background: -webkit-linear-gradient(top, #658567 -1000000%, #476445 1000100%) !important;
}

.battleCell.cellColorOverkill.Pumpkimp.Healthy
{
background: -webkit-linear-gradient(top, #76A77D -1000000%, #58855A 1000100%) !important;
}

.battleCell.cellColorBeaten.Pumpkimp.Magma
{
background: -webkit-linear-gradient(top, #823100 -1000000%, #5F1C00 1000100%) !important;
}

.battleCell.cellColorOverkill.Pumpkimp.Magma
{
background: -webkit-linear-gradient(top, #AC4100 -1000000%, #8A2A00 1000100%) !important;
}

.battleCell.cellColorOverkill.Pumpkimp.Corruption
{
background: -webkit-linear-gradient(top, #AB7878 -1000000%, #895F6B 1000100%) !important;
}

.battleCell.cellColorBeaten.Pumpkimp.Corruption
{
background: -webkit-linear-gradient(top, #A05080 -1000000%, #7D3C66 1000100%) !important;
}

}
