css:
@-moz-document domain("shanaproject.com") {
:root {
	--bg-color: #202020;
    --bg2-color: #131313;
    --bg3-color: #252525;
    --bg4-color: #1a1a1a;
    --bg-yellow-color: #552;
    --border1-color: #333;
    --border2-color: #080808;
    --color: #aaa;
    --color-a: #ccc;
    --color-ah: #777;
    --search-bg: url('https://i.imgur.com/5DoLZWz.png');
    --gradients: url('https://i.imgur.com/dDn1YtP.png');
    --header-fades: url('https://i.imgur.com/2Tt4vd5.png')
}

input[type=submit],
a {
    color: var(--color-a);

}
input[type=submit]:hover,
a:hover {
    color: var(--color-ah);
}

#header_top_bar,
#content{
	background-color: var(--bg-color);
    color: var(--color);
}
#header_big {
	background: var(--bg2-color) var(--gradients) repeat-x;
    background-position: 0 0;
}
#header_fade_left, #header_fade_right {
    background-image: var(--header-fades);
}
.page_contents {
	background-color: var(--bg3-color);
    border-color: var(--border2-color);
    border-top-color: var(--border1-color);
    border-left-color: var(--border1-color);
}


.search_outer,
.search_title,
.search_subber
.search_button {
    background-color: var(--bg4-color);
    border-color: var(--border1-color);
	border-top-color: var(--border2-color);
    border-left-color: var(--border2-color);
}

.search_title {
    background: var(--search-bg) 316px 0 no-repeat;
    color: var(--color);
}
.search_subber {
    background: var(--search-bg) 298px -38px no-repeat;
    border-left: 1px solid var(--bg2-color);
    color: var(--color);
}
.search_button {
    background: grey var(--gradients) 0 -334px repeat-x;
    border-left: 1px solid var(--bg2-color);
    color: var(--color);
}


.ui-widget-content {
    border: 1px solid var(--border2-color);
    background: linear-gradient(var(--bg2-color), var(--bg-color), var(--bg-color), var(--bg4-color));
    color: var(--color);
}
.ui-widget-content a {
    color: var(--color-ah);
}

.ui-state-hover, .ui-widget-content .ui-state-hover, .ui-widget-header .ui-state-hover, .ui-state-focus, .ui-widget-content .ui-state-focus, .ui-widget-header .ui-state-focus {
    border: 1px solid var(--border1-color);
    background: linear-gradient(var(--bg3-color), var(--bg-color));
    color: var(--color-a);
}


.release_block {
	background-color: var(--bg3-color);
    border-color: var(--border1-color);
	border-bottom-color: var(--border2-color);
    border-right-color: var(--border2-color);
    color: var(--color);
}
.release_block:hover {
	background-color: var(--bg4-color);
    border-color: var(--border1-color);
	border-top-color: var(--border2-color);
    border-left-color: var(--border2-color);
}
.release_text_contents:hover {
    background: none;
}
.release_20,
.release_comment_text,
.release_episode,
.release_expand,
.release_leftover,
.release_quality_hd,
.release_quality_none,
.release_quality_sd,
.release_subber,
.release_title {
    border-right: 1px solid var(--border2-color);
}
.release_last {
    border-right: none !important;
}
.release_download,
.release_download_mini,
.release_ignore,
.release_queue_mini,
.release_watch,
.release_comment_text,
.release_episode,
.release_leftover,
.release_quality_hd,
.release_quality_none,
.release_quality_sd,
.release_size,
.release_subber,
.release_title {
    border-left: 1px solid var(--border1-color);
}
.release_info,
.release_website {
    border-left: 1px dotted var(--border2-color);
}
.release_episode {
    width: 38px;
}
.release_row {
	border-top-color: var(--border2-color);
    border-right: none;
}
.release_old_new_marker,
.release_mark {
    background-color: var(--bg-yellow-color);
}


.release_quality_none,
.release_quality_hd,
.release_quality_sd,
.release_info,
.release_download, .release_download_mini, .release_ignore, .release_queue_mini, .release_watch
.release_website {
	background-image: var(--gradients);

}
}
