css:
@-moz-document domain("www.inet.se"), domain("inet.se") {
body,
.inet-navbar,
.tree-wrapper {
    color: #aaa;
    background-color: #222225;
}
a,
a > span,
h1,
h2,
h3,
.product-page .product-description,
.btn-link,
.breadcrumb>li+li:before {
    color: #aaa;
}
.breadcrumb {
    border-color: #666;
    background-color: #222225;
}
.input-group .form-control,
select,
::placeholder,
.product-page .stock-box,
.product-page .purchase {
    color: #aaa !important;
    border-color: #666 !important;
    background-color: #333 !important;
}
.btn-primary {
    border-color: #666;
    background-color: #333;
}
#category-tree ol.lvl1 {
    border-color: #666;
}
#category-tree ol>li.open>.tree-item>button,
.contact-container a {
    color: #aaa;
}
#category-tree ol.lvl1>li>.tree-item {
    border-color: #666;
    background-color: #333;
}
#category-tree ol.lvl2>li>.tree-item {
    border-color: #666;
    background-color: #555;
}
#category-tree ol.lvl3,
#category-tree ol {
    border-color: #666;
    background-color: #666;
}
.nav-tabs>li.active>a,
.nav-tabs>li.active>a:focus,
.nav-tabs>li.active>a:hover {
    color: #aaa;
    background-color: #333;
}
.nav-tabs>li>a,
.nav-tabs>li>a:focus,
.nav-tabs>li>a:hover,
.nav-tabs>li.disabled>a,
.nav-tabs>li.disabled>a:focus,
.nav-tabs>li.disabled>a:hover {
    color: #aaa;
    background-color: #444;
}
.nav-tabs>li.active>a,
.nav-tabs>li.active>a:focus,
.nav-tabs>li.active>a:hover,
.nav-tabs>li>a,
.nav-tabs>li>a:focus,
.nav-tabs>li>a:hover,
.nav+.tab-content {
    border-color: #666;
}
.product-gallery .carousel-gallery {
    border-color: #666;
    background-color: #fff;
    opacity: 0.7;
}
.fa-star-o,
.glyphicon-star-empty {
    color: #333;
}
.nav.nav-tabs+.tab-content,
.specifications li:nth-child(odd),
.product-list>ul>li:nth-child(odd),
.inet-striped-list .list-row:nth-child(odd),
.inet-striped-table tbody tr:nth-child(2n),
.specifications h2 {
    background-color: #333;
}
.specifications li:nth-child(even),
.product-list>ul>li:nth-child(even) {
    background-color: #444;
}
.product-page .compare-product-list,
.product-page .specifications {
    margin-top: 0px;
}
.contact-container {
    background-image: url(https://i.imgur.com/egeB16r.png);
}
footer {
    background-color: #111;
}
.panel-default>.panel-heading,
.btn {
    color: #aaa;
    background-color: #222;
}
.product-reviews .panel-body>footer,
.product-page .accessories-panel .panel-body,
.product-reviews .review,
.panel {
    color: #aaa;
    background-color: #333;
}
.product-reviews .panel-body>footer,
.product-page .accessories-panel .panel-body,
.product-reviews .review {
    border-top: none;
}
.modal-content {
    background-color: #333;
}
.product-reviews .review .body .show-more {
    background-color: #222;
    color: #aaa;
}
.panel-heading {
    border-top: 1px solid #666 !important;
}
.form-control,
.inet-striped-list .list-header,
.order-list>li .products {
    color: #aaa !important;
    background-color: #333;
}
.my-pages ul.nav>li a {
    color: #aaa;
    background-color: #333;
    border-right: 1px solid #666;
    border-bottom: 1px solid #666;
    border-top: 1px solid #666;
}
.my-pages ul.nav>li.active a {
    color: #aaa;
    border-left: none;
    background-color: #222225;
}
.my-pages ul.nav>li:first-child a {
    border-left: 1px solid #666;
}
.checkout .shipping-methods>div,
.checkout .shipping-methods>div.active input,
.checkout .shipping-methods>div.active select,
.checkout .nav>li.active>a,
.well,
p.landningssidaKnappText.aktivKnapp,
p.landningssidaKnappText:hover {
    background-color: #333;
    color: #aaa;
}
.checkout .shipping-methods>div.active,
.checkout .nav>li>a,
.checkout .nav>li.disabled>a:focus,
.checkout .nav>li.disabled>a:hover,
p.landningssidaKnappText {
    background-color: #222;
}
.cart-content .price .product-price .active-price,
.checkout .shipping-methods>div>div:nth-child(2) {
    color: #aaa;
}
.btn-checkout-buy {
    border: 1px solid #206100;
}
.order-list>li.header,
.inet-striped-table thead tr th,
.order-list>li.header>*>.btn {
    background-color: #333;
}
.order-list>li,
.inet-striped-table,
.inet-striped-table thead tr th {
    border-color: #666;
}
}
