.avatar {
    max-width: 65px;
}

.alert-info{
	background: none;

}
a{
    font: 14.5px "Consolas" !important;
    color: #e1e8ed;
    text-shadow: rgba(0, 0, 0, 0.1) 1px 1px 0px !important;
}

/*käyty*/
a:focus, a:hover{
    color: #878b8e;
}
table.torrent-list tbody tr td a:visited{
    color: #878b8e !important;
}

body{
	background: #1f1f1f;
}

table,
.table-striped>tbody>tr:nth-of-type(odd){
	background: #1f1f1f;
}

/*trusted*/
.table>thead>tr>td.success, .table>tbody>tr>td.success, .table>tfoot>tr>td.success, .table>thead>tr>th.success, .table>tbody>tr>th.success, .table>tfoot>tr>th.success, .table>thead>tr.success>td, .table>tbody>tr.success>td, .table>tfoot>tr.success>td, .table>thead>tr.success>th, .table>tbody>tr.success>th, .table>tfoot>tr.success>th
.table-hover>tbody>tr>td.success:hover, .table-hover>tbody>tr>th.success:hover, .table-hover>tbody>tr.success:hover>td, .table-hover>tbody>tr:hover>.success, .table-hover>tbody>tr.success:hover>th{
background: transparent;
}
/*remake danger*/
.table-hover>tbody>tr.danger:hover>td, .table-hover>tbody>tr.danger:hover>th, .table-hover>tbody>tr:hover>.danger, .table-hover>tbody>tr>td.danger:hover, .table-hover>tbody>tr>th.danger:hover,
.table>thead>tr>td.danger, .table>tbody>tr>td.danger, .table>tfoot>tr>td.danger, .table>thead>tr>th.danger, .table>tbody>tr>th.danger, .table>tfoot>tr>th.danger, .table>thead>tr.danger>td, .table>tbody>tr.danger>td, .table>tfoot>tr.danger>td, .table>thead>tr.danger>th, .table>tbody>tr.danger>th, .table>tfoot>tr.danger>th{
	background:transparent;
}

/*ties vittu mikä joka toinen harmaa? */
.table-hover>tbody>tr{transition: ease 0.2s;}
.table-hover>tbody>tr:hover{
	background:transparent !important;
	transition: ease 0.2s;
}

tr.success,
body.dark .panel-success .panel-heading,
body.dark .panel-success, body.dark .panel-success .panel-heading{
    background-color: rgba(91, 241, 226, 0.22) !important;
	border-color: rgba(91, 241, 226, 0.22) !important;
}

tr.danger,
body.dark .panel-danger .panel-heading,
body.dark .panel-danger, body.dark .panel-danger .panel-heading{
    background-color: rgba(223, 91, 241, 0.22) !important;
	border-color: #452c2c rgba(223, 91, 241, 0.22) !important;
}


tr.aplus {/* ei ole olemassa .si */
    background-color: rgba(103, 91, 241, 0.52) !important;
}

}
