    to   { opacity: 1; transform: translateX(0px);}
}

.snow-container {
  position: absolute;
  height: 500px;
  width: 100%;
  max-width: 100%;
  top: 0;
  overflow: hidden;
  z-index: 2;
  pointer-events: none;
  background-color: red;
}

#header:before {
    content: "";
    position: absolute;
    top: 0;
	right: 0;
    bottom: 0;
    left: 0;
    background-color: transparent;
    background-image:url('https://i.imgur.com/Pr8vVFf.png'),
					 url('https://i.imgur.com/Pr8vVFf.png'), 
					 url('https://i.imgur.com/tkt55k5.png') !important;
	-moz-animation: snow 60s linear infinite;
    -ms-animation: snow 60s linear infinite;
    -webkit-animation: snow 60s linear infinite;
    animation: snow 60s linear infinite;
    pointer-events: none;
}
	@keyframes snow {
		0% {background-position: 0px 0px, 0px 0px, 0px 0px}
		100%{background-position: 100px 800px, 500px 1400px, -100px 1200px} /* ei näin :th*/
}
html:not(:lang(ns)) #header{background-image:none;animation-play-state:paused;}
#header:hover {animation-play-state: paused;}
