    right: -15%;
}

/*pituus sivusta sivuun */


.l-container {
    width: 93%;
}

.listenDetails__trackList:hover {
    opacity: 1;
    transition: 2s ease;
}
.trackItem__image {
    width: 44px;
    height: 44px;
    margin: 0;
}

.trackItem__play {
    top: 10px;
    left: 10px;
}

/* playlist lista pienenys */

.l-listen__mainContent .l-about-right {
    padding-left: 0;
}
.l-listen-wrapper .l-about-rows {
    padding-right: 300px;
}
/* lista */
/* playlist play pieneiinys */

.l-listen-hero,
.fullListenHero__foreground {
    height: 220px;
}

/*prof img*/

.l-listen__mainContent .l-about-left {
    right: -200px;
    left: auto !important;
    position: absolute !important;
}


/*actiivi    overflow-y: scroll;*/

.header__navMenu>li>a.selected,
.header>li>a:focus {
    background: rgba(255, 255, 255, 0.15);
}



.commentForm,
.commentForm.m-active,
.commentForm__wrapper{
    border: transparent;
    background: transparent !important;
}
.sound__soundActions {
    background: transparent !important;
}
.dashbox__box {
    visibility: hidden;
}
/* ------ ------- ------ */
/* transparent roskaa */
.l-fixed-top-one-column>.l-top,
.compactTrackListItem__additional,
.trackItem__additional,
.g-type-shrinkwrap-inline,
.g-type-shrinkwrap-inline:hover,
.searchTitle {
    background: transparent !important;
}
/*oikea quick list badge marginia oikeelle&tranparent*/
/* se */

.soundBadge__additional {
    background: transparent;
    margin-right: -10px;
    margin-top: -18px;
}
/* play nappi ^^*/

.sc-button,
.sc-tag {
    border-color: transparent;

    background: transparent;
    color: #fff;
    text-shadow: 1px 1px 1px #000;

}

.sc-button-active, .sc-button-selected {
    color: #f50;
}


/* ------ ------- ------ */

.profileHeader {
    top: 10px;
    margin: auto;
}
.fullListenHero,
.backgroundGradient,
.backgroundGradient__buffer,
.backgroundGradient__buffer1,
.backgroundGradient__buffer2 {
    /*background: rgba(255, 255, 255, 0.1) !important;*/
    
    background: transparent !important;
}
.profileHeaderBackground__visual,
.profileHeaderBackground {
    background-color: transparent;
}
/*mainokset*/
.paging-eof:before,
.sidebarModule.mobileApps,
.l-footer,
.l-fluid-fixed .soundActions__purchaseLink,
.l-fixed-fluid .soundActions__purchaseLink,
.sc-buylink,
.sc-buylink:visited,
.dropbar.m-active
