{
    display: none;
}
/*playlist hover . active */
.addToPlaylistItem__actions,

.trackItem.hover,
.trackItem.active,
.compactTrackListItem.clickToPlay.active,
.compactTrackListItem.clickToPlay:hover,
.compactTrackListItem.clickToPlay:focus,
.compactTrackList__moreLink:hover {
  /*  background: rgba(0, 0, 0, 0.22) !important;*/
	background:transparent !important;
    
    transition: 0.5s ease;
    padding-left: 0px;
    margin: 0;
    padding: 0;
}

.trackItem.active{
    border: 1px solid lightblue;
    background: rgba(0, 0, 0, 0.5);
    padding:0;
    margin:0;
}

/*que*/
.queue,
.queue__itemWrapper,
.queueItemView:hover{
	background: #34353b;
}
.queue__panel
{
	border-bottom: 1px solid rgba(50, 50, 50, 0.75);
}

.queueItemView:hover.m-active ,.queueItemView.m-active{
	background: #48494e !important;
}

.queueItemView__context,
.queueItemView:hover .queueItemView__context:not(:empty){
	display: block !important;
}

/* on tässäkin helvetin hienoja ideoita soundcloud deveilt /s */
.queueItemView .queueItemView__context::before{
	content: '·';
    margin: 0 .5em;
	
}
.queueItemView__dragHandle:hover{

}
.queueItemView__dragHandle{

}


/* playlist list animaatio*/

.trackItem,
.compactTrackList__item,
.compactTrackListItem.clickToPlay {
    transition: 0.5s ease;
    padding-left: 5px;
}
.compactTrackList__item,
.compactTrackList__moreLink {
    margin-top: 1px;
    margin-bottom: 1px;
}
.compactTrackList{overflow:visible;}

