css:
@-moz-document url-prefix("https://www.reddit.com/r/games"), url-prefix("https://www.reddit.com/r/Games/") {
.md pre{
	background: #262626 !important;
    color: gray;
    box-shadow: 0px 0px 1px 0px gray;
    border-top:none;
    border-bottom:none;
    border-right:none;
    margin-left: 8px;
}

a,link, body .side .md h5{
	color: #b27f87 !important;
}

body .side .md blockquote blockquote blockquote h6{
    color:#757575 !important;
}

/*tumma*/
body,
body .side .md h4,
body .side .md h6,
body .side .md h5,
.side a[href*="/wiki/reddit_101"]{
	background-color: #161616;
}

body .side .md h4+blockquote,
body .side .md h6+blockquote,
body .side .md h5+blockquote{
	background-color: #1a1a1a;
}


p, span, ul, li{
	color: #757575;
}
/*title*/
.thing .title a, .parent .title{
	color: #757575 !important;
}
span.score{
	color:#888;
}

.linklisting .odd, .linklisting .even {
    background-color: #1a1a1a;
    border-color: #1f1f1f !important;
    
    margin-top: 2px;
    margin-bottom: 2px;
    margin-right: 320px;
}

.link{padding: 8px 10px 6px 10px !important; margin-left: 10px !important; }
.entry{padding: 13px 0px 4px 0px !important; }

a[name=content]{ background-color: #161616}
.sitetable.nestedlisting,
.comments-page .linklisting .odd,
.panestack-title,
body div.side,
.md blockquote{
	border-color: #1f1f1f !important;
}
.titlebox .fancy-toggle-button .remove,
.side, .linkinfo, .linkinfo .shortlink{
	background-color: #1a1a1a;
}
textarea, .commentarea .usertext textarea, .link .usertext-body .md{
	background-color: #1a1a1a;
    border-color: #1f1f1f;
}


body .content .md blockquote, input,.linkinfo .shortlink{
	background-color: #262626 !important;
    border-color: #1f1f1f !important;
    color: #757575 !important;
}
.commentarea .usertext-edit:before, .markdownEditor,
.bottom-area,.RESSubscriptionButton{
	display:none;
}

.NERPageMarker {
    border: none;
    background-color: #161616;
    top: -2.5px;
    left: -35px;
    position: relative;
    text-align: left;
    padding: 3px 0 0px 50px;
}
#progressIndicator {
    font-size: 0px !important;
    padding: 3px 0 0px 50px;
    border: none;
    border-radius: 0px;
    background: #1a1a1a;
    border: 1px solid #1f1f1f;
    display: block;
    height: 20px;
    margin-right: 320px;
    margin-left: 10px;
}
.NERPageMarker .gearIcon, #progressIndicator .gearIcon{ display:none;}
.link.last-clicked{border: 1px solid #2a1e20;}
/*poistaa res seuraavan sivun animaation */
#progressIndicator > span.RESThrobber {
	display:none;
}
#progressIndicator h2{ color: grey; position:relative; right: 40px; font-weight: normal;}

