css:
@-moz-document url-prefix("https://slickdeals.net/deals/"), domain("slickdeals.net") {
body {
    background-color: black;
}

/******** Homepage ********/

/* search box */
input.searchBox {
    color: lightgrey !important;
    background-color: black;
}
.globalSearch {
    background-color: black;
}

/* login button */
.button {
    color: white !important;
    background-image: linear-gradient(#3c3c3c, #676767) !important;
    text-shadow: none !important;
    background-color: black !important;
}

/* category bar */
.categoryBar {
	background-color: darkgrey;
}
.categoryBar a {
    color: black !important;
}
.categoryBar .inner a:hover {
    color: #0074be !important;
    background-color: black;
}
.categoryBar a:first-child {
    padding: 0 15px;
}
.categoryBar+.announcementBar {
    background-color: #323a3f;
}
.announcementBar .content .copy span:nth-child(2) {
    color: white !important;
}

/* header */
.pageHeader h1 {
    color: lightgrey;
}

/* right side bar */
.sideGridBox {
    background-color: inherit;
}
.sideGridBox h3 {
    background-color: #202020;
}
.sideGridBox h3>a {
    color: lightgrey;
}

/**/
#fpMainContent .gridHeading .headingRight .button.fpSettingsButton #fpSettingsButton {
    color: lightgrey;
}
#fpMainContent .gridCategory ul.gridDeals .fpGridBox .fpItem .itemImageAndName .itemTitle {
    color: #0074be !important;
}
#mainColumn #fpMainContent .gridCategory:not([data-module-name='Featured Deals']):not(#fpNewsAndArticles) ul.gridDeals .fpGridBox .fpItem .itemImageAndName .itemTitle {
    color: #0074be !important;
}
#fpMainContent .gridCategory ul.gridDeals .fpGridBox .fpItem .itemBottomRow {
    background-color: black !important;
}
#fpMainContent .gridCategory .grid .fpItem .priceLine .itemPrice {
    color: #76b14c !important;
}
#mainColumn #fpMainContent .gridCategory:not([data-module-name='Featured Deals']):not(#fpNewsAndArticles) ul.gridDeals .fpGridBox .itemInfoLine .priceLine .itemPrice {
    color: #76b14c !important;
}
.viewDetailsBtn {
    background-color: #202020;
    color: lightgrey !important;
}
.viewDetailsBtn:hover {
    background-color: #0072bc;
}
.inlineStoreCoupons h2 {
    color: #0074be;
}
.inlineStoreCoupons .postload {
    background-color: inherit;
}
.inlineStoreCoupons .postload .storeName {
    color: lightgrey;
}
.inlineStoreCoupons .postload .couponDesc {
    color: grey;
}

/******** Category pages ********/

/* left column */
.leftColumn .box,
#searchSidebarContainer .box {
    background-color: #202020;
}
.sectionTitle {
    color: #0079c7;
}
.box .head {
    color: lightgrey;
}
.shopDeals>ul>li {
    background: transparent;
}
.shopDeals>ul>li a {
    color: lightgrey;
}
.shopDeals ul.subCat {
    background-color: #424242;
}
.facetedNav .section .header .sectionTitle {
    color: lightgrey;
}
.facetedNav .section .scrollable::-webkit-scrollbar {
    background-color: black;
}
.facetedNav .options li .content {
    color: #c3c3c3;
}
.facetedNav .section .options {
    color: lightgrey;
}
.facetedNav input[type="text"] {
    background-color: black;
    color: lightgrey;
}
.shoppingTools .section .title {
    color: lightgrey;
    text-decoration: underline;
}
/******** deals ********/
#fpMainContent .gridHeading>h2,
#fpMainContent .gridHeading>h1
{
    color: #0074be;
}
#fpMainContent .pagination .button {
    color: lightgrey;
}
#fpMainContent .gridCategory .fpGridBox {
    background-color: black;
    border-width: 1px;
}
#fpMainContent .gridCategory .grid .fpItem .itemBottomRow {
    background-color: black;
}
.couponItem .itemBottomRow {
    background-color: inherit;
}
.itemImageAndName:hover {
    background-color: #303030;
}

/******** coupons and discounts ********/
.couponsDiscounts .box {
    background-color: black;
}
.catModules .moduleTitle {
    color: inherit;
}
.dealGridTitle {
    color: #0074be;
}
.mainContent .box {
    background-color: black;
}
.box p {
    color: lightgrey;
}
.categoryInfo .title {
    color: #0074be;
}

/******** menu panes ********/
.subPane {
    background-color: #202020 !important;
    color: lightgrey !important;
}
.subContainer, .subContainer .first {
    background-color: #202020;
}
.topHeading .title {
    color: lightgrey;
}
.subContainer>li>a {
    color: lightgrey !important;
    background-color: #202020;
}
.globalNav .deals {
    background-color: #202020;
}
.globalNav ul.subContainer {
    background-color: #202020;
    color: lightgrey !important;
}
.subPaneInfo li a, 
.listContainer li a,
.headerArticle .articleTitle a {
    color: lightgrey !important;
}
#globalBar .globalNav li .subContainer p {
    color: grey;
}

/* modal windows */
.modal {
    background-color: black;
    color: lightgrey;
    border: 1px solid white;
}
.modal .headline {
    color: lightgrey;
}
.modal.modal-modern .dealAlertTitle {
    color: grey;
}
.modal footer {
    background-color: #202020;
}
.modal .close {
    color: white !important;
}
#addDealAlertModal .subtext {
    color: lightgrey;
}
.modal p {
    color: grey;
}

/* search results */
.resultTabs a.active {
    background-color: #303030;
}
.resultTabs a,
.searchFooter {
    background-color: black;
    color: lightgrey;
}
.dealAlertRow,
.notifBar {
    background-color: #202020;
    color: lightgrey;
}
.dealAlertRow .title {
    color: lightgrey;
}
.notifBar {
    margin: 10px 10px 10px 10px;
}
.mainSearchContent .searchWrapper {
    background-color: black;
}
#afscontainer {
    display: none;
}
.facetedNav .section select {
    color: lightgrey;
    background-image: linear-gradient(#3c3c3c, #676767);
}
.options option {
    background-color: black;
}
}

@-moz-document url-prefix("https://slickdeals.net/f/") {
/* front page deals */
.frontpageDeal .box,
.popularDeal .box,
.forumThread .box {
    background-color: black;
    color: lightgrey;
}
.frontpageDeal .aboutAuthor {
    color: lightgrey;
}
#weeklyAdBar {
    background-color: inherit;
}
#dealTitle h1,
#dealPrice {
    color: white;
}
#detailsArea {
    color: lightgrey;
}
#detailsTop .textDescription {
    color: lightgrey;
}
.postUsername a {
    color: #0072bc;
}
.postTextContent {
    color: lightgrey;
}
#originalPost, #statisticsAndSubmissions, #joinTheConversationBox, .commentBubble, #dealStats.activeDeal {
    background-color: #3a3a3a;
}
#detailContents .addCommentContainer,
.addCommentContainer {
    background-color: #202020;
}
.quotebox_header {
    color: #adadad;
}
.quotebox_content {
    background-color: #202020;
    color: grey;
}
.authorName>.label>a {
    color: lightgrey;
}
.invertedBadge {
    color: black;
}
#dealStats.activeDeal .getNotified {
    color: lightgrey;
}
.injectedAd {
    background-color: black;
}
.dropdown .dropdown-menu {
    background-color: #202020;
}
.dropdown .dropdown-menu li>a {
    color: lightgrey;
}
}

@-moz-document url-prefix("https://slickdeals.net/coupons") {
div.box {
    background-color: #202020;
    color: lightgrey;
}
.item {
    background-color: black;
}
.item .intro,
.item .title {
    color: lightgrey;
}
.moreDealsBox {
    background-color: black;
    color: grey;
}
.themedBox {
    background-color: #303030;
    
}
h2.title {
    color: lightgrey !important;
}

/* filters */
.showTabs .filter:not(.active) {
    color: grey;
}

/* model coupon detail */
#couponModal.modal .inner .couponItemInfo .details .title {
    color: lightgrey;
}
.modal .bottom {
    background-color: #202020;
}
.modal .bottom:not(.modal-modern) .subHead {
    color: grey;
}
#couponModal.modal .bottom form.one input.email {
    background-color: black;
    color: grey;
}
}

@-moz-document url-prefix("https://slickdeals.net/article") {
.box {
    background-color: #202020;
    border-top: 1px solid grey;
}
.topline {
    border-top: none;
}
#dealTitle h1 {
    color: lightgrey;
}
.subTitle {
    color: #a0a0a0;
}
.author .name {
    color: lightgrey;
}
.badge {
    color: black;
}
.wordpressContent h2 {
    color: grey;
    text-decoration: underline;
}
.wordpressContent ul {
    color: grey;
    background-color: black;
}
.aboutAuthor {
    color: lightgrey !important;
}
.share {
    background-color: #202020;
}
.share .shareArticle {
    color: lightgrey;
}

/* comments */
.postUsername a,
.articlePage h3,
.postTextContent,
.pageCounter {
    color: lightgrey;
}

#joinTheConversationBox {
    background-color: #404040;
}
#joinTheConversationBox .joinTheConversation {
    color: lightgrey;
}
}

@-moz-document url-prefix("https://slickdeals.net/forums/") {
.bodyBg {
    background: none;
}
.loginSignUp .inner {
    background-color: #202020;
}
.loginSignUp .head,
.loginSignUp .subHead {
    color: lightgrey;
}
.modal input.infoField {
    background-color: black;
    color: lightgrey;
}
.modal .bottom {
    background-color: #202020;
    color: grey;
}
.altAccess {
    color: lightgrey;
}
.vbmenu_popup td, .vbmenu_popup li {
    background-color: black;
    color: lightgrey;
}
.vbmenu_option a:link {
    color: lightgrey;
}
td.vbmenu_hilite {
    background-color: grey;
}
.vbmenu_option .bginput {
    background-color: black;
    color: lightgrey;
}
}

@-moz-document url-prefix("https://slickdeals.net/deal-alerts/") {
#pageContent .box {
    background-color: #202020;
    color: lightgrey;
}
#dealAlertKeyword, .dealAlertKeywordSearch {
    background-color: black;
    color: lightgrey;
}
.button.alert {
    color: black;
}
}

@-moz-document url-prefix("https://slickdeals.net/forums/forumdisplay.php") {
body {
    background: none;
    background-color: black;
}
#logoarea {
    display: none;
}
.global_wrap {
    background-color: #202020;
}
.alt1 {
    background-color: black;
    color: lightgrey;
}
.alt2 {
    background-color: #101010;
    color: lightgrey;
}
.alt2 a {
    color: lightgrey !important;
}
.tlv3_cat .icon,
.navbar h2.forum_navbar_header,
.pagenav_result_text {
    color: lightgrey;
}
a:link, a:visited {
    color: #367adc;
}
.forumbutton.blue {
    background-color: #007fa7;
    color: black;
}
.forumbutton {
    background-color: #797900;
    color: black !important;
}
.forumbutton.right {
    background-color: #101010;
    color: lightgrey !important;
}
.forumwrap_top {
    background-color: #101010;
}
h1.forum_threadlist_header, h2.forum_threadlist_header {
    color: grey;
}
.forumwrap_top td.search_pagenav, .search_pagenav {
    background-color: black;
}
.forumwrap>table {
    background-color: black;
}
.forumwrap_bottom_v2 .wrap_table, .forumwrap_bottom .wrap_table {
    background-color: black;
    color: grey;
    margin-top: 0px;
    margin-bottom: 0px;
}

/* forum options */
.forum_options_header {
    color: lightgrey;
}
.forum_options {
    background-color: #101010;
}
.forum_category_select,
.forum_options_right {
    background-color: black;
}
.forum_options h3,
.forum_legend,
.forum_rules {
    color: grey;
}
.forum_options_right select {
    background-color: black;
    color: grey;
}
/* right side */
.widget_hitbar a, #hottopic_hitbar a {
    color: black;
}
.colitem {
    background-color: #101010;
}
.colitem:hover {
    background-color: #202020;
}
.colitem a, .colitem a:link, .colitem a:visited {
    color: grey;
}
}
