css:
@-moz-document domain(linkhay.com) {
body{background:#fff}
body.fixtop {padding-top: 50px}
#lh-header{background:transparent;border-bottom:none}
.header-content{background:#fff;box-shadow:0 1px #eee;}
.V2-link-stream>h2{border:none;padding:5px 0 10px;}
.V2-link-stream > h2 .tab::before,
.V2-link-stream > h2 .rss {display:none !important}
.V2-link-stream > h2 .tab {padding:0}
.V2-link-stream > h2 .tab + .tab:not(.all) {margin: 0 0 0 10px;padding: 0 0 0 10px;border-left: 1px solid #ddd;}

.V2-two-cols-layout.separate:before{width:0}

#ticker-box {padding:0;border:1px solid #ddd !important;box-shadow:none;}
#ticker-box .osc-scroller .content {margin:5px 0 5px 10px}
#ticker-box .osc-scroller .track,
#ticker-box .osc-scroller .track .thumb {border-radius:0}

.V2-two-cols-layout.separate > .main-col {width: calc(100% - 320px);}
.V2-old-style-sidebar-box {border-radius:0;margin:20px 0!important}


.V2-link-list {border-top:1px solid #ddd;padding-top:15px}

.V2-other-links .V2-link-list {border-top:none}

.V2-vote-box[type="old-style"] .counter {border-color:#ddd;background:#fff;border-radius:20px}
.V2-vote-box[type="old-style"] .counter:after {border-top-color:#fff}
.V2-vote-box[type="old-style"] .voter {border-color:#ddd;background:transparent;color:#777;border-radius:10px}
.V2-vote-box[type="old-style"] .voter i {display:none}


.new-search-box {border: 1px solid #ddd;overflow:hidden}
.new-search-box .new-search-box-input,
.new-search-box .new-search-box-input:hover,
.new-search-box .new-search-box-input:focus{border:none;padding:5px 10px;width:calc(100% - 50px);background:#fff}
.new-search-box .new-search-box-btn,
.new-search-box .new-search-box-btn.focus{border:none; border-left:1px solid #ddd !important;float:right}

.V2-link-list > li.load-more {background:transparent;border-radius:0;color:#777}
.V2-link-list > li.load-more::before {margin:0;border-top:none}
.V2-link-report {display:none}

#ft .ft-top {border-color:#ddd;border-width:1px 0}
#ft .ft-top, 
#ft .copyright { margin:20px 0;overflow:hidden;padding:20px}
#ft .copyright {padding-bottom:10px}
.V2-link-list > li{padding:0}

.V2-ticker-item + .V2-ticker-item,
.V2-link-list > li + li {border-top:none}

.V2-link-list > li.tile {margin:10px 0}

.V2-link-list > li.link-48h-ranked.tile::after, 
.V2-link-list > li.link-48h-ranked.bottom-spacing::after {background:transparent !important;box-shadow:none !important}

.V2-link-list > li.link-48h-ranked.bottom-spacing::before,
.V2-link-list > li.link-48h-ranked.tile::before {background:transparent !important;border-top:1px solid #ddd;margin:15px 0 0 0}

.V2-ticker-item + .V2-ticker-item::before,
.V2-link-list > li+li::before {border-top:1px solid #ddd;margin:-5px 0 5px 44px;display: block;content:'';box-shadow:none !important}
.V2-link-list > li+li::before {margin:12px 0 12px 74px}

.V2-link-list > li.link-48h-ranked+li.link-48h-ranked:before {left:0;position:relative !important;width:unset !important}
.V2-link-list > li.tile+li::before,
.V2-link-list > li.bottom-spacing+li::before {display:none}

.comment-btn {background:transparent !important}
.V2-comments > ul > li.no-comment {border:none;background:transparent}

.V2-link-item .link-info .info .user-link img {border-radius:10px;border:none}
.V2-link-item .link-info .info a,
.V2-link-item .link-info .info .V2-reshare > span:first-child {color:#777 !important}
.V2-link-item .link-info .info .comments span {display:none !important}

.pagewrap .new-search-box .new-search-box-input {width:196px !important;margin:0 !important}
.pagewrap .new-search-box {margin:0 0 15px 0}

.suggestbox_PYMK  ul li {border:none}
.suggestbox_PYMK  ul li + li {border-top:1px solid #ddd}

.thumbnail img,
.feed-photo img,
.avatar img,
.thumb,
.media-border,
.dis-b img {border:none !important;padding:0}
}
