css:
@-moz-document domain("www.cryptocompare.com") {
html.lights-off ,html,body {
    background: #303030;
    color: #EEE;
}
body.lights-off {
    background: transparent;
    color: #EEE;
      
}
.current-price-value{border:0;}
span.ng-binding.ng-scope {
    color: #000000;
  
}
.text-small.ng-binding {
    color: #949494;
}
.lights-off .normal-browser,.normal-browser {
    background: transparent;
}
.lights-off md-tabs.md-dynamic-height md-tab-content.md-active ,md-tabs.md-dynamic-height md-tab-content.md-active {
    background: transparent;
}
.lights-off .navbar-main .navbar-nav > li > a,.navbar-main .navbar-nav > li > a {
    font-size: 13px;
    color: #717171;
    border-left: 1px solid #292d31;
     border-right: 0px solid #292d31;
}
.navbar-main .navbar-brand {opacity:0.1}
.lights-off .navbar-main > .container,.navbar-main > .container {
    background: #303030;
    border-bottom: 0px solid transparent;
    box-shadow: 0px 0px 0px 0px rgba(0,0,0,0.14);
        max-width: 1130px;
}
.other .navbar-main > .container .ribbon-content:after ,.other .navbar-main > .container .ribbon-content:before {

   display:none;
}
 .btn-switch.selected {
    background: #333333;
    border-color: #3a3a3a;
    color: #565656;
}
.btn-switch {
    border: 0px solid #d5e6f1;
 color: #6b6b6b;
}

.lights-off .widget.widget-portfolio.widget-total.highlight-border,.widget.widget-portfolio.widget-total.highlight-border{
color:red;
 border-bottom:1px solid  rgb(0, 96, 152);

}
.lights-off .md-tabs.md-default-theme md-ink-bar,.lights-off  md-tabs md-ink-bar,.md-tabs.md-default-theme md-ink-bar,md-tabs md-ink-bar {
    color: rgb(51,122,183);
    background: rgb(51,122,183);
}

.md-tabs md-tabs-wrapper {
    background-color: #2d2d2d;
    border-radius: 5px;
}
md-content ,.lights-off .md-cryptocompare md-tabs-canvas,.lights-off .header-portfolio,.lights-off .toolbar-portfolio,.lights-off .portfolio-addcoin-bottom,md-tabs-canvas,.header-portfolio,.toolbar-portfolio,md-content{
    background: transparent!important;
    color: #ccc !important;
}
body .main-wrapper > .container {
    background: transparent;
    border: 0;
    min-height: 700px;
    -webkit-box-shadow: 0px 0px 0px 0px rgba(0,0,0,0.14);
    -moz-box-shadow: 0px 0px 0px 0px rgba(0,0,0,0.14);
     box-shadow: 0px 0px 0px 0px rgba(0,0,0,0.14); 
}
.stats-body {
     color: #ccc; 
}
.lights-off .widget,.widget  {
    width: 19.1%;
    height:150px;
    box-shadow: 1px 11px 20px 0px #2b2828;
   
}

.lights-off .header-portfolio,.header-portfolio {
    background: #404040;
    border-bottom: 0px solid #404040;
}
 md-tabs md-tabs-wrapper {
    background-color: #2d2d2d;
    border-color: rgba(0,0,0,0.12);
    width: 96.5%;
    margin-left: 20px;
    border-radius: 5px;
}
.chart-portfolio-widget {
    border: 0px solid #EEE;
     border-radius: 0px 0px 5px 5px;
}
.lights-off .period-change,.period-change  {
    border-bottom: 1px solid rgba(33, 33, 33, 0.2196078431372549)!important;
}
.lights-off .block-stat, .block-stat {
    border-bottom: 1px solid rgba(33, 33, 33, 0.2196078431372549)!important;
}
.list-portfolio {
    border-left: 0px solid #EEE;
}
.lights-off .md-cryptocompare md-tabs-canvas,.md-cryptocompare .md-tabs-canvas {
    background: #2e3338;
}
.lights-off .block-stat,.block-stat {
    border-bottom: 1px solid #212121;
}
.chart-portfolio {
    padding: 19px 0;
    padding-right: 0;
    border: 0px solid #EEE;
    border-top: 0;
    border-bottom: 0;
    border-right: 0;
}
.period-change {
    border-bottom: 0px solid #999;
}
.lights-off .amcharts-axis-grid {
    stroke: #2e3338;
}
.md-faketabs {
    background: #404040!important;
    border: 0px solid #EEE !important; 
    margin-top: 20px;
    width:100%;
    border-radius: 5px 5px 0px 0px;
}
.lights-off .toolbar-portfolio,.toolbar-portfolio {
    background: #404040;
    border-color: #333;
    border:none;
    opacity:0.2;
}
.lights-off .toolbar-portfolio:hover{opacity:1;}
span.toolbar-portfolio-labelaction {
    display: none;
}
.lights-off .block-readGuide,.block-readGuide,p.portfolio-caution,.portfolio-explanation ,.portfolio-ad{
    display :none;
}

.lights-off .footer .container,.footer .container {
    background: transparent;
    opacity: 0.0;
}
.lights-off .footer .container:hover,.footer .container:hover {
    background: transparent;
    opacity: 0.3;
}
.lights-off .chart-portfolio-widget,.chart-portfolio-widget {
    background: #404040;
    border-color: #333;
}

.lights-off .table-col,.table-col {
    background: #404040;
    border-right: 0px solid #333;
    border-bottom: 0px solid #212121;
    color: #fffefe;
    font-size: 15px;
    font-weight: 100;
}
.table-row, .table-row {
    border-top: 0px solid #333 !important;
    border-left: 0px solid #333 !important;
    border-bottom: 1px solid #303030!important;
    border-right: 0px solid #333!important;
    border-radius:5px;
} 
.portfolio-addcoin-bottom {
    background: #404040;
    border: 0px solid #eee;  
}

.lights-off .portfolio-addcoin-bottom,.portfolio-addcoin-bottom {
    background: #404040;
    border: 0px solid #333;
}
.lights-off .list-portfolio,.list-portfolio {
    border-left: 0px solid #333;
}
.list-portfolio .table-hcol, .list-accounting .table-hcol{
  border-bottom: 1px solid #303030;
}
.lights-off .table-hcol,.table-hcol {
    background: #404040;
    border-right: 0px solid #333333;
    border-bottom: 1px solid #303030;
    color: #929292;
    font-weight: 100;
    text-transform: uppercase;
    font-size: 10px;
}
raised:not([disabled]) md-icon {
    color: #999;
}
.text-small {
    font-size: 15px;
    line-height: 35px;
    font-weight: 100;
}
.widgets-container{
height:150px;

}
.lights-off .widget .widget-header,.widget .widget-header  {
    background: #404040;
    color: rgb(125, 125, 125);
    font-weight: 100;
    font-size: 11px;
    text-align: left;
}
.lights-off .widget,.widget,.widget,.widget {
    border: 0px solid #333;
    background: #404040;
}
.widget-portfolio {
    float: right;
    margin-right: 5px;
    margin-left: 5px;
    
}
.widget .widget-body {
    font-size: 30px;
    padding: 30px;
    text-align: center;
    border:0px solid transparent;
    font-weight:100;
}
.price-trend {
    position: absolute;
    top: -32px;
    right: -15px;
    font-size: 13px;
    line-height: 20px;
    width: 80px;
}
.up-text, .text-red {
    color: #4ad77c;
}
.lights-off .btn-switch,.btn-switch {
    background: rgba(33, 33, 33, 0);
    border: 0px solid #444b52;
    color: #CCC;
}
.lights-off .md-button.md-default-theme.md-primary.md-raised, .lights-off .md-button.md-primary.md-raised, .lights-off .md-button.md-default-theme.md-primary.md-fab, .lights-off .md-button.md-primary.md-fab
 .md-button.md-default-theme.md-primary.md-raised,  .md-button.md-primary.md-raised,  .md-button.md-default-theme.md-primary.md-fab, .md-button.md-primary.md-fab
{
    background-color: rgb(44, 44, 45);
}
.lights-off .md-faketabs .md-button.md-primary.md-raised:not(.md-new-coin),.md-faketabs .md-button.md-primary.md-raised:not(.md-new-coin) {
    background-color: transparent!important;
    color: #777;
}
.lights-off .btn-switch.selected,.btn-switch.selected {
    background: rgb(53, 53, 53);
    border-color: #3C923C;
    color: #FFF;
}
.md-faketab.selected .md-button.md-raised:not([disabled]) {
    border-top: 1px solid #7f4d16;
        border-bottom: 0px solid #7f4d16;

}
.lights-off .md-button.md-raised,.md-button.md-raised {
    color: #777;
}
button.md-raised:not([disabled]) {
    background: #404040;
}
.md-button.md-raised:not([disabled]):hover {
    box-shadow: 0 2px 5px 0 rgba(0,0,0,.26);
}
.md-button.md-raised:not([disabled]) {
    box-shadow: 0 2px 5px 0 rgba(0,0,0,.0);
}
.lights-off .md-button.md-raised:not([disabled]), .md-button.md-raised:not([disabled]) {
    background: transparent;
}
.fa {
    font: normal normal normal 13px/1 FontAwesome;
    opacity: 0.4;
}
.lights-off .search-top .form-control:focus,.search-top .form-control:focus {
    background: #313131;
    border-color: #303030;
}
.lights-off .form-control,.form-control {
       color: #a5a5a5;
    background-color: #313131;
    border: 1px solid #333333;
}
.portfolio-summary {
    clear: both;
    font-size: 12px;
    margin-top: 15px;
    margin: 15px;
    color: #ccc!important;
}
.lights-off .label-portfolio-stat,.label-portfolio-stat {
    color: #565656;
}
.portfolio-member-details {
    font-size: 12px;
    font-weight: normal;
    color: #636363;
}
.lights-off .header-portfolio h1,.header-portfolio h1 {
    color: #212121;
}
.header-portfolio h1  {
    display: none;
}
.chart-portfolio .btn-group > .btn:not(:first-child):not(:last-child):not(.dropdown-toggle) {
    border: 0px solid #d5e6f1;
}
img {
    -webkit-filter: grayscale(100%); /* Safari 6.0 - 9.0 */
    filter: grayscale(100%);
}
.lights-off .portfolio-name a,.portfolio-name a {
    color: rgb(160, 160, 160);
    text-transform: uppercase;
    font-weight: 100;
}
.lights-off .toolbar-portfolio .toolbar-portfolio-labelaction,.toolbar-portfolio .toolbar-portfolio-labelaction {
    color: #606061;
    font-weight: 100;
}
.lights-off text ,text{
    fill: #808080;
}
.lights-off .chart-name,.chart-name {
    color: #5d5c5c;
    text-transform: uppercase;
    font-weight: 100;
    font-size: 12px;
}
.change-indicator .glyphicon-chevron-up {
    background: #44ac69;
    color: white;
}
.secondary-stats {
    margin-bottom: 5px;
    height: auto;
    opacity:1;
    overflow:hidden;
}
.secondary-stats:hover {
    margin-bottom: 15px;
    opacity:1;
    height: auto;
}
body.lights-off .main-wrapper > .container {
    background: transparent;
}


.lights-off .header-portfolio h1,.header-portfolio h1 {
    display: none;
}

.btn-newportfolio {
   
    right: 10px;
    top: 0px;
    z-index: 9999;
}
.lights-off .portfolio-addcoin-bottom,.portfolio-addcoin-bottom {
    display: none;
}
.md-select-menu,.md-select-menu-container.md-active{background:#444;}

/* CHROME SCROLL */
  .uiScrollableArea
  {
    background-color: #303030 !important;
  }

  ::-webkit-scrollbar
  {
    width: 9px !important;
    height: 9px !important;
  }

  ::-webkit-scrollbar-track-piece
  {
    background-color: #303030 !important;
    background: #303030!important;
  }

  body::-webkit-scrollbar-track-piece
  {
    background-color: #222 !important;
  }

  ::-webkit-scrollbar-track-piece:no-button
  {
  }

  ::-webkit-scrollbar-thumb
  {
    background-color: #444 !important;
    border-radius: 3px !important;
  }

  ::-webkit-scrollbar-thumb:hover
  {
    background-color: #777 !important;
  }

  ::-webkit-scrollbar-thumb:active
  {
    background-color: #666 !important;
  }

  #mv-tiles
  {
    text-align: center !important;
    width: 740px !important;
  }

/* AGENT_SHEET */
  scrollbar
  {
    -moz-appearance: none !important;
    background: none #303030 !important;
    padding: 2px !important;
    width: 9px !important;
  }

  scrollbar[orient="vertical"]
  {
    padding: 2px !important;
  }

  scrollbar[orient="horizontal"]
  {
    height: 9px !important;
  }

  scrollbarbutton[sbattr="scrollbar-up-top"],
scrollbarbutton[sbattr="scrollbar-down-top"],
scrollbarbutton[sbattr="scrollbar-up-bottom"],
scrollbarbutton[sbattr="scrollbar-down-bottom"]
  {
    -moz-appearance: none !important;
    display: none !important;
  }

  scrollbar thumb
  {
    -moz-appearance: none !important;
    background: none #444 !important;
    border: none !important;
    border-radius: 5px !important;
  }

  scrollbar thumb:hover
  {
    background: none #555 !important;
  }

  scrollbar thumb:active
  {
    background: none #555 !important;
  }
}
