css:
/* i really want this to be global */
@-moz-document domain("virtualpopstar.com") {

body {
    background-color: #222;
    background: url(https://i.imgur.com/16eWfGI.jpg) no-repeat center center fixed;  
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    font-family: Roboto, 'PT Sans Narrow', Corbel;
    letter-spacing: 0.8pt;
    color: black;
}

/*INLOGPAGINA ehhh, still gotta fix this ugly mess*/
.regbox {
    background-color: rgba(250,250,250,0.8); 
    border-style: solid;
    border-width: 1px;
    border-color: white; 
}

.bar_top,
.header_bg_overlay {
    display: none;
}
.top_menu_container {
    background-color: white;
    color: black;
    font-size: 11pt;
}
/*TXT IN TOP HEADER*/
.top_menu_container .menu li a {
    color: black;
}

.block_content_title_small {
    font-family: Abel;
    font-size: 13pt;
    font-weight: 500;
}
.block_menu_single {
    background-image: url(https://i.imgur.com/g6VLEBE.png);
    color: black;
}
.block_menu_multi {
    background-image: url(https://i.imgur.com/hWhshBH.png);
    color: black;
}
.block_menu_multi_active {
    background-image: url(https://i.imgur.com/kpT2CHy.png);
    color: black;
}
.block_content_top_left,
.block_content_top_right {
    background-image: url(https://i.imgur.com/HdWFBdl.png);
}
.block_content_top_center_small,
.block_content_top_center_med,
.block_content_title_mini,
.block_title,
.block_content_top,
input[type="submit"] {
    background-image: url(https://i.imgur.com/JBngbqC.png);
}

/*TXT COLOR*/
.block_content_title_small,
.block_content_title_med,
.block_content_title, 
.block_title, 
.username-player {
 color: black;   
}


.block_content_bottom_mini,
.block_content_bottom_small,
.block_bottom.avatar,
.block_content_bottom_med,
.block_content_bottom {
    background-image: url(https://i.imgur.com/sLTpGfH.png);
}
.okButton {
    background-image: url(https://i.imgur.com/Vpgjq1l.png);
    color: black
}
.header_bg { /*HEADER*/
    background-image: url(https://i.imgur.com/pFstbWu.png);
}
input[type="submit"].dark {
    background-image: url(https://i.imgur.com/qYtACeU.png);
}
.block_menu_single:hover {
    background-image: url(https://i.imgur.com/fOLo637.png);
}
.block_menu_single.active {
    background-image: url(https://i.imgur.com/khziGJh.png);
}

/*NAV ICONS*/
.icons_main {
    background-image: url(http://i.imgur.com/KrrffJF.png);
}
.icons_games {
    background-image: url(http://i.imgur.com/DpIESuX.png);
}
.icons_clothing {
    background-image: url(http://i.imgur.com/Kvs8LXd.png);
}
.icons_career {
    background-image: url(http://i.imgur.com/NCQUDLU.png);
}
.icons_financial {
    background-image: url(http://i.imgur.com/EHQD8t0.png);
}
.icons_social {
    background-image: url(http://i.imgur.com/QMATeTu.png);
}

/*CURRENT PAGE*/
.icons_main_active {
    background-image: url(https://i.imgur.com/CjubnFT.png);
}
.icons_games_active {
    background-image: url(https://i.imgur.com/54Vw8S9.png);
}
.icons_clothing_active {
    background-image: url(https://i.imgur.com/l7iAiFD.png);
}
.icons_career_active {
    background-image: url(https://i.imgur.com/REioNuZ.png);
}
.icons_financial_active {
    background-image: url(http://i.imgur.com/8ER54NQ.png);
}
.icons_social_active {
    background-image: url(https://i.imgur.com/4niY60z.png);
}

.icons_main:hover, .icons_games:hover, .icons_social:hover, .icons_career:hover, .icons_financial:hover, .icons_clothing:hover {
    background-image: url(https://i.imgur.com/CnWGD4G.png);
}

input[type="submit"] {
    color: black;
}

}
