css:
/* WBIJAM DARK BY DAVIVAD */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wbijam.pl") {

/* ****************** */
/* ----- G£ÓWNE ----- */
/* ****************** */

.dzielna_gildia_lista {
  width: 656px;
  height: 34px;
  font-size: 16px;
  font-weight: bold;
  padding: 5px;
}

#tresc_dzielna_gildia {
  text-align: justify;
  clear: both;
  font-size: 13px;
  background-color: #ffffff;
  border: 1px solid #b9b9b9;
  border-bottom: 0px;
  border-top: 0px;
  padding-bottom: 15px;
  padding-top: 15px;
}

table.dzielna_gildia tr {
  height: 200px;
  text-align: center;
}

.ogladaj_wideo_vk {
  cursor: pointer;
  color:#073143;
}

.ogladaj_wideo_vk:hover{
	text-decoration:underline;
}

.vid_link {
  color: #999;
  transition: 0.2s;
}

.vid_link:hover{
	color:#fff;
  text-decoration:none;
}

div#actual-time {
	display: none;
}

div#episode-gallery {
	display: none;
}

img#refresh-gallery {
	cursor: pointer;
}

div#show-gallery img {
	vertical-align: middle;
}

div#show-gallery {
	font-size: 16px;
	margin: 10px auto;
	width: 350px;
	height: 30px;
	text-align: center;
	line-height: 30px;
	cursor: pointer;
}

div.episode-calendar-wrapper {
	font-size: 16px;
	margin: 10px auto;
	width: 350px;
	height: 30px;
	text-align: center;
	line-height: 30px;
}

div.episode-calendar-wrapper.active {
	background-color: #c2ecbe;
}

.link_green {
	color: green;
}

.buttony{
  vertical-align: middle;
  margin: 0 auto;
  height: 50px;
  text-align: center;
  line-height: 50px;
}

body{
  background:#111;
  margin: 10px 0 0 0;
  font-size: 12px;
  font-family: 'Trebuchet MS', 'Arial', 'Verdana';
  color:#fff;
}

ul{
  list-style-type: none;
  padding:0px;
  margin:0px;
}

#szkielet{
  margin:0px auto;
  width:925px;
}

#komunikat{
  left:0;
  margin:0;
  padding:20px;
  background-color:#9e0000;
  color:#ffffff;
  border-bottom:2px solid #f5f5f5;
  width:100%;
  text-align:center;
  font-weight:bold;
  font-size:20px;
}

#error{
  text-align:center;
  font-weight:bold;
  margin-bottom:10px;
  color:#9e0000;
  clear:both;
}

input, button{
  border:1px solid #b9b9b9;
  background-color:#ffffff;
  color:#202020;
}

img{
  border:0px;
}

.bold{
  font-weight:bold;
}

.italic{
  font-style:italic;
}

.center{
  text-align:center;
}

.wysrodkuj{
  text-align:center;
}

.left{
  text-align:left;
}

.right{
  text-align:right;
}

.vertical_top{
  vertical-align:top;
}

a:link{
  color: #999;
  transition: 0.2s;
  text-decoration:none;
}
a:hover{
	color:#fff;
}

a:visited{
  color: #fff;
  transition: 0.2s;
  text-decoration:none;
}

a:visited:hover{
	color:#999;
}

.lista a:link{
  color: #999;
  transition: 0.2s;
}
.lista a:hover{
	color:#fff;
  text-decoration:none;
}

.lista a:visited{
  color: #fff;
  transition: 0.2s;
}

.lista a:visited:hover{
	color:#999;
  text-decoration:none;
}

.red {
	color:#cd0a0a;
}

.green {
	color:#32782d;
}

/* ****************** */
/* ------- NAV ------ */
/* ****************** */
.nawigacja {
	width: 656px;
	height: 30px;
}

.nawigacja_lewa {
	width: 200px;
	text-align: left;
	float: left;
}

.nawigacja_srodek {
	width: 256px;
	text-align: center;
	float: left;
}

.nawigacja_prawa {
	width: 200px;
	text-align: right;
	float: left;
}

/* ****************** */
/* ------- TOP ------ */
/* ****************** */

#top{
  width:925px;
  margin-bottom:20px;
  margin-top: 10px;
	text-align: center;
}

#adv-footer {
	width: 925px;
	float: left;
	clear: both;
	text-align: center;
	padding-top: 10px;
}

#adv-top {
	width: 950px;
	margin: 10px auto;
}

iframe#iframe-adv-top {
	width: 960px;
	height: 210px;
	border: 0;
}

#center_vert{
  vertical-align: middle;
}

/* ****************** */
/* --- MENU PRAWA --- */
/* ****************** */

#menu_prawa_fb{
  width: 249px;
  background: #222;
  color: #fff;
}

#menu_prawa{
  width:249px;
  margin-left:20px;
  float:right;
  background-color:#333;
}

.pmenu_naglowek_blue, .pmenu_naglowek_red, .pmenu_naglowek_black{
  width:249px;
  height:28px;
  text-align:center;
  font-size:17px;
  color:#FFFFFF;
  padding-top:5px;
}

.pmenu_naglowek_blue{
  background:url('http://naruto.wbijam.pl/images/menu_blue.gif') no-repeat;
}

.pmenu_naglowek_red{
  background:url('http://naruto.wbijam.pl/images/menu_red.gif') no-repeat;
}

.pmenu_naglowek_black{
  background:url('http://naruto.wbijam.pl/images/menu_black.gif') no-repeat;
}

.pmenu{
  clear:both;
  width:229px;
  padding-left:10px;
  padding-right:10px;
  padding-top:10px;
  padding-bottom:10px;
}

.pmenu li{
  display:block;
}

.pmenu li a{
  margin-left:3px;
  color:#999;
  transition: 0.2s;
}

.pmenu li.red a{
	color:#cd0a0a;
}

.pmenu li.red a:hover{
  color:#ff2222;
	text-decoration:none;
}
  
.pmenu li a:hover{
  color:#fff;
	text-decoration:none;
}

/* ****************** */
/* --- TRESC LEWA --- */
/* ****************** */

#tresc_lewa{
  width:656px;
  float:left;
}

.naglowek{
  width:636px;
  height:26px;
  background-color:#222;
  font-size:17px;
  padding-left:10px;
  padding-top:4px;
  clear:both;
  border-left:5px solid #222;
  border-right:5px solid #222;
  color:#fff;
}
  
.naglowek a:link{
  color:#999;
  transition: 0.2s;
  text-decoration: none;
}
  
.naglowek a:hover{
  color:#fff;
}

.kalendarz{
  width:636px;
  background-color:#FFFFFF;
  font-size:13px;
  padding-left:10px;
  padding-top:4px;
  padding-bottom:4px;
  clear:both;
  border-left:1px solid #292929;
  border-right:1px solid #292929;
  border-top:1px solid #292929;
  border-bottom:1px solid #292929;
  color:#292929;
  margin-bottom:20px;
}

.pod_naglowek{
  border-bottom:1px solid #bbbbbb;
  padding-bottom:1px;
  font-size:11px;
  float:left;
  clear:both;
  width:656px;
  margin-bottom:10px;
  font-weight:bold;
  margin-top:0px;
}

.tresc{
  text-align:justify;
  clear:both;
  font-size:13px;
  background-color:#333;
  border:1px solid #333;
  border-bottom:0px;
  padding:10px;
}
  
.tresc a:link{
  color: #999;
  text-decoration: none;
  transition: 0.2s
}
  
 .tresc a:hover{
  color: #fff;
}

.wstep{
  font-weight:bold;
  margin-top:0px;
  margin-bottom:10px;
}

.tresc_left{
  float:left;
  margin:0px;
}

.tresc_left img{
  margin-right:10px;
  margin-bottom:2px;
}

.tresc_right{
  float:right;
  margin:0px;
}

.tresc_right img{
  margin-left:10px;
  margin-bottom:2px;
  margin-right:0px;
}

.tresc_center{
  text-align:center;
}

.tresc_justify{
  text-align:justify;
  margin:0px;
}

.tresc_zdjecie{
  float:left;
  margin-right:10px;
  margin-bottom:2px;
  border:3px solid #444;
}

.tresc_bar_nowy{
  background-color:#333;
  border: 1px solid #333;
  border-top: 1px solid #444;
  padding-top:3px;
  padding-bottom:3px;
  padding-left:8px;
  padding-right:8px;
  float:right;
  clear:both;
  margin-bottom:20px;
  width:638px;
  height:26px;
}

.tresc_bar_nowy p{
  width: 150px;
  float: left;
  font-size: 14px;
  font-style: italic;
  margin: 3px 0 0 0;
}

.tresc_bar_nowy p.tresc_bar_fb{
  width:171px;
  float:right;
}

.tresc_bar_nowy a{
  color:#484848;
  text-decoration:none;
}

.tresc_bar_nowy a:hover{
  color:#ababab;
}

.tresc_bar{
  background-color:#333;
  border: 1px solid #333;
  border-top: 1px solid #444;
  padding-top:3px;
  padding-bottom:3px;
  padding-left:8px;
  padding-right:8px;
  float:right;
  clear:both;
  margin-bottom:20px;
  width:638px;
}

.tresc_bar p{
  width:150px;
  float:right;
  font-size:14px;
  font-style:italic;
  margin:0px;
}

.tresc_bar a{
  color:#484848;
  text-decoration:none;
}

.tresc_bar a:hover{
  color:#ababab;
}

.tresc_bar_fb_ramka{
  height: 20px;
  width: 171px;
  border: none;
  overflow: hidden;
}

.wyniki_wyszukiwania{
  width: 656px;
  height: 26px;
  margin-bottom: 10px;
  margin-top: 15px;
}

.wyniki_wyszukiwania_pomoc{
  float: left;
  height: 26px;
  line-height: 26px;
}

.wyniki_wyszukiwania_fb{
  float: right;
}

.wyniki_wyszukiwania_fb_ramka{
  height: 20px;
  width: 179px;
  border: none;
  overflow: hidden;
}

.odcinek_oczekiwanie_info{
  width: 656px;
  clear: both;
  margin-bottom: 10px;
}

#fb_ramka_glowna{
  height: 28px;
  width: 230px;
  border: none;
  overflow: hidden;
  margin-bottom: 5px;
  margin-top: 3px;
}

.naglowek_fb{
  font-size: 11px;
  font-weight: bold;
  width: 656px;
  height: 26px;
  margin-bottom: 10px;
  margin-top: 0px;
  border-bottom: 1px solid #bbbbbb;
  clear: both;
}

.naglowek_fb_lewa{
  float: left;
  height: 26px;
  line-height: 26px;
}

.naglowek_fb_prawa{
  float: right;
}

.naglowek_fb_ramka{
  height: 20px;
  width: 179px;
  border: none;
  overflow: hidden;
}

/* ****************** */
/* ----- STRONY ----- */
/* ****************** */

.strony{
  text-align:center;
  clear:both;
}

.strony a{
  border:1px solid #bbbbbb;
  background-color:#ffffff;
  padding-left:3px;
  padding-right:3px;
  text-decoration:none;
  color:#484848;
  font-weight:bold;
}

.strony a:hover{
  border:1px solid #484848;
}

.obecna a{
  background-color:#085778;
  border:1px solid #085778;
  color:#ffffff;
}


/* ****************** */
/* ----- STOPKA ----- */
/* ****************** */

#stopka_srodek a:link{
	color: #868686;
  text-decoration: none;
}

#stopka_srodek a:hover{
  text-decoration: underline;
}

#stopka_srodek a:visited{
	color: #868686;
  text-decoration: none;
}

#stopka_srodek a:visited:hover{
  text-decoration: underline;
}

#stopka_srodek{
  background: #333333;
  color: white;
  clear: both;
  text-align: center;
  padding: 25px;
}

#stopka_gora{
  clear: both;
  width: 925px;
  height: 20px;
  margin: 0px auto;
  padding-top: 20px;
}

#stopka_gora_lewy{
  width: 200px;
  float: left;
  text-align: left;
}

#stopka_gora_prawy{
  width: 200px;
  float: right;
  text-align: right;
}

#stopka_gora a:link{
  text-decoration: none;
  color: #333333;
}

#stopka_gora a:hover{
  text-decoration: overline;
  color: #333333;
}

#stopka_gora a:visited{
  text-decoration: none;
  color: #333333;
}

#stopka_gora a:visited:hover{
  text-decoration: overline;
  color: #333333;
}

#stopka_dol{
  clear: both;
  padding: 20px;
  text-align: center;
  color: #868686;
}

/* ****************** */
/* ------ ADMIN ----- */
/* ****************** */

.admin_menu{
  margin-bottom:20px;
}

.admin_menu a{
  text-decoration:none;
}

/* ****************** */
/* ---- FORMULARZ --- */
/* ****************** */

form, form p{
  margin-top:0px;
  margin-bottom:5px;
}

.formularz_lewa{
  float:left;
  clear:both;
  width:100px;
  font-weight:bold;
}

.formularz_srodek{
  float:left;
  width:300px;
}

.formularz_prawa{
  float:left;
  width:250px;
}

.input_srodek{
  width:275px;
  float:left;
}

.formularz_textarea{
  float:left;
  width:556px;
}

.formularz_pojedynczy{
  clear:both;
  text-align:center;
  font-weight:bold;
}

.formularz_komentarz{
  clear:both;
  text-align:left;
  width:400px;
  margin:0px auto;
  margin-bottom:1px;
  margin-top:5px;
}

.formularz_komentarz img{
  vertical-align:bottom;
}


.kategorie_lewa{
  float:left;
  clear:both;
  width:25px;
  font-weight:bold;
}

.kategorie_srodek{
  float:left;
  width:200px;
  font-weight:bold;
}

.kategorie_prawa{
  float:left;
  width:250px;
}


/* ****************** */
/* --- KOMENTARZE --- */
/* ****************** */

#komentarze_tytul{
  border-bottom:1px solid #bbbbbb;
  padding-bottom:1px;
  font-size:11px;
  float:left;
  clear:both;
  width:656px;
  margin-bottom:10px;
}

#komentarze_tytul p{
  float:left;
  width:50%;
  margin:0px;
}

#komentarze_tytul p a{
  text-decoration:none;
}

#komentarze_tytul p a:hover{
  color:#484848;
}

#komentarz_dodaj{
  width:636px;
  float:left;
  clear:both;
  padding:10px;
  border:1px solid #efefef;
  background-color:#ffffff;
  text-align:center;
  margin-top:10px;
}

#komentarz_dodaj span{
  display:block;
}

#komentarz_dodaj textarea{
  width:400px;
  height:130px;
  margin-bottom:2px;
}

#komentarz_dodaj input{
  margin-top:5px;
}

.komentarz_nieparzysty, .komentarz_parzysty, .komentarz_admin{
  width:646px;
  float:left;
  clear:both;
  padding:5px;
  margin-bottom:10px;
  margin-top:0px;
}

.komentarz_parzysty{
  background-color:#ecf0f3;
}

.komentarz_admin{
  background-color:#c4d3d9;
}

.komentarz_lewa{
  width:125px;
  float:left;
  text-align:center;
}

.komentarz_lewa span{
  display:block;
}

.komentarz_lewa span a{
  text-decoration:none;
}

.komentarz_lewa span a:hover{
  color:#484848;
}

.komentarz_prawa{
  width:515px;
  float:right;
}

.komentarz_prawa p{
  margin:0px;
}

.komentarz_data{
  font-weight:bold;
  margin-bottom:5px;
  margin-top:0px;
}

.zacytuj a{
  text-decoration:none;
  font-weight:normal;
}

.zacytuj a:hover{
  color:#000000;
}

.cytat{
  border:1px dashed #909090;
  background-color:#ffffff;
  padding:5px;
  width:495px;
  float:left;
  margin:5px;
}

.cytat_autor{
  font-weight:bold;
  display:block;
  margin-bottom:3px;
}

/* ****************** */
/* --- LOGOWANIE ---- */
/* ****************** */

#logowanie{
  margin-top:10px;
}

#logowanie p{
  float:left;
}

.logowanie_lewa{
  width:50px;
  font-weight:bold;
  clear:both;
  margin-left:30px;
}

/* ****************** */
/* ---- GALERIA ----- */
/* ****************** */

.img_hover img{
  display:none; 
  border:none;
}

.img_hover{
  position: relative;
}

.img_hover:hover img{
  display: block;
  position: absolute; bottom: 2em; left: 0;
}

/* ****************** */
/* ------ LISTA ----- */
/* ****************** */

.lista{
  border:1px solid #333;
  background-color:#222;
  width:656px;
  border-spacing:2px;
  clear:both;
}
  
.lista td{
  border:1px solid #333;
  background-color:#333;
  padding-top:1px;
  padding-bottom:1px;
  padding-left:5px;
}

.lista th{
  border:0px;
  font-size:14px;
  color:#fff;
}

.lista th a{
  color: #999;
  transition: 0.2s;
}

.lista th a:hover{
  color:#fff;
  text-decoration:none;
}

.lista_tytul{
  width:400px;
  text-align:left;
  padding-left:5px;
  color: #999;
  transition: 0.2s;
}

.lista_hover:hover{
  color:#fff;
  text-decoration:none;
}

.lista_sortowanie{
  width:656px;
  height:26px;
  float:left;
  clear:both;
}

.lista_sortowanie li{
  width:25px;
  height:21px;
  float:left;
  margin-right:3px;
}

.lista_sortowanie li a{
  width:25px;
  height:21px;
  background:url('http://naruto.wbijam.pl/images/alfabet.gif');
  float:left;
  margin-right:3px;
  text-align:center;
  text-decoration:none;
  padding-top:5px;
  color:#484848;
}

.lista_sortowanie li a:hover{
  background:url('http://naruto.wbijam.pl/images/alfabet_over.gif');
  color:#ffffff;
}

.lista_td {
  width: 70px;
  text-align:center;
}

.lista_td_calendar {
  width: 120px;
  text-align:center;
}

.lista_hover.active{
  background-color: #c2ecbe;
}

/* ****************** */
/* ------ OCENY ----- */
/* ****************** */

#ocena{
  width:180px;
  padding:10px;       
  margin:0px auto;
  clear:both;
  font-weight:bold;
  text-align:center;
  margin-bottom:15px;
}

#ocena_ol {
    margin:0;
    padding:0;          
    position: relative;          
    list-style: none;
    background: url("http://naruto.wbijam.pl/images/icons/star_big_grey.gif") repeat-x;  
    width:160px;      
    height:16px;             
}

.ocena_l{
    height:16px;
}

.ocena_l a{
    position: absolute;
    display: block;          
    height:16px;
    left:0;
    top:0;  
}


.ocena_l a:hover{
    border: 0px solid #fff;
    text-decoration:none;            
    background: url("http://naruto.wbijam.pl/images/icons/star_big.gif") repeat-x;               
}

#e1{           
    width: 16px;          
    z-index:10;               
}           
 
#e2{          
    width: 32px;          
    z-index:9; 
}      
 
#e3{
    width: 48px;            
    z-index:8;          
}      
 
#e4{          
    width: 64px;          
    z-index:7;            
}
      
#e5{          
    width: 80px;          
    z-index:6;            
}

#e6{          
    width: 96px;          
    z-index:5;            
}

#e7{          
    width: 112px;          
    z-index:4;            
}

#e8{          
    width: 128px;          
    z-index:3;            
}

#e9{          
    width: 144px;          
    z-index:2;            
}

#e10{          
    width: 160px;          
    z-index:1;            
}

/* ****************** */
/* ----- GALERIA ---- */
/* ****************** */

.galeria_gora{
  width:190px;
  height:160px;
  padding:5px;
  background-color:#ecf0f3;
  border:1px solid #bbbbbb;
  border-bottom:0px;
  text-align:center;
}

.galeria_dol{
  border:1px solid #bbbbbb;
  background-color:#ffffff;
  padding:5px;
  width:190px;
  height:70px;
}

.galeria_window{
  float:left;
  width:202px;
  height:253px;
  padding:2px;
  margin-right:5px;
  margin-bottom:10px;
}

.galeria_window:hover{
  background-color:#bbbbbb;
}

.textarea-wrapper {

    background: #333;
