css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.shazbat.tv") {

#sidebar, #middle-content, #show_panel,
body,
.col-lg-3, .wrapper, .login-wrap,
.header, .showback, .content-panel, 
.table td, .chatrow, .col-md-2,
.eprow .internal, .tab
{
background: #000000 !important;
}
.form-login-heading, .nav .active .tab,
.row a:hover, .schedule-episode
{
background: #B70000 !important;
}
.schedule-episode a
{
background: transparent !important;
}
.followed
{
background: #008000 !important;
}
.label-tag, .label 
{
background: pink !important;
}
.chatrow, .col-md-2, .login-wrap,
#tab-content, .tab
{
border: 1px #B70000 solid !important;
}
#sidebar .sub-menu > .sub li
{
border-bottom: 2px #000000 solid !important;
}
.showback, .content-panel,
.form-control:focus
{
box-shadow: none !important;
}
button, .col-lg-3 h3, 
#nav-accordion li a
{
color: #FFFFFF !important;
border: 1px #B70000 solid !important;
background: #B70000 !important;
}
input
{
color: #FFFFFF !important;
border: 1px #B70000 solid !important;
background: #000000 !important;
}
.excerpt, .pull-left, td, th, .internal,
h1, h2, h3, h4, h5, h6, .nav, .nav-tabs,
muted, p, .pull-right, .showback, .pull-right a,
.nav a
{
color: #FFFFFF !important;
text-shadow: none !important;
}
}
