css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/* AGENT_SHEET */


#titlebar-close:hover {
    background-color:#333 !important;
}
#titlebar-min:hover {
    background-color:#333 !important;
}
#titlebar-max:hover {
    background-color:#333 !important;
}
#urlbar:hover,
.searchbar-textbox:hover {
    background-color: #1a1a1a !important;
}
#new-tab-button,
.tabs-newtab-button {
 background:#000 !important;
}
#new-tab-button:hover,
.tabs-newtab-button:hover {
 background:#1a1a1a !important;
}
#main-menubar {
    background-color: #000 !important;
}
#navigator-toolbox::after {
    display: none !important;
}
#nav-bar,
#TabsToolbar,
#PersonalToolbar {
    background: #000 !important;
    border: 0px !important;
}
.toolbarbutton-text {
    color: #6a6a6a !important;
}
.toolbarbutton-1 {
    border-bottom: 1px solid transparent !important;
}
findbar {
    -moz-appearance: none !important;
    background-color: #000 !important;
}
#bookmarks-view,
#historyTree,
#history-panel,
#sidebar-header {
    background-color: #000!important;
    color: #ccc!important;
}
menuitem,
menu {
    background-color: #000 !important;
    color: #ccc !important;
    border: none !important;
}
menuseparator {
    display: none !important;
}
menupopup {
    background-color: #000 !important;
    -moz-appearance: none!important;
    border: 1px solid #000 !important;
}
menupopup > menu:hover {
    background-color: #111 !important;
}
menuitem:hover {
    background-color: #111 !important;
    color: #fff !important;
}
arrowscrollbox {
    background-color: #000!important;
    color: #ccc!important;
}
.statuspanel-label {
    color: #ccc !important;
    background: #000 !important;
    border: none !important;
}
#main-menubar,
#nav-bar {
    box-shadow: none !important;
}
#urlbar {
    background-color: #000 !important;
    border: none !important;
    color: #aaa !important;
}
.searchbar-textbox {
    background-color: #000 !important;
    border: none !important;
    color: #aaa !important;
}
menupopup>hbox {
    border: 0px !important;
}
tab {
    border-bottom: 2px solid transparent !important;
}
tab[selected="true"] {
    color: #ddd !important;
    background: #000 !important;
}
tab:not([selected="true"]) {
    color: #6c6c6c !important;
    background: #000 !important;
}
tab:not([selected="true"]):hover {
    color:#ccc !important;
}
#PersonalToolbar {
    height: 24px!important
}
#personal-bookmarks toolbarbutton {
    margin: -1px 0 6px 0 !important
}
#personal-bookmarks .toolbarbutton-icon {
    margin: -3px 2px -3px 0 !important
}
#personal-bookmarks .toolbarbutton-text {
    margin: -2px 0 !important
}
#back-button {
    border-bottom: none !important;
}
#forward-button {
    border-bottom: none !important;
}
tab[selected="true"] {
    border-bottom: 2px solid #333 !important;
}
tab:not([selected="true"]):hover {
    border-bottom: 2px solid #333 !important;
}
tab[selected="true"]:hover {
    border-bottom: 2px solid #555 !important;
    color: #fff !important;
}
.toolbarbutton-1:hover {
    border-bottom: 1px solid #333 !important;
}
toolbarbutton.bookmark-item:hover {
    border-bottom: 1px solid #333 !important;
}
scrollbar {
    -moz-margin-start: -8px;
    -moz-appearance: none!important;
    position: relative;
    background-color: #30303078!important;
    background-image: none!important;
}
scrollbar > slider > thumb {
    width: 6px !important;
    height: 6px !important;
    border-width: 0px;
    -moz-appearance: none!important;
    background-color: #000;
}
scrollbar > slider > thumb:hover {
    background-color: #10101078;
}
scrollbar > scrollbarbutton {
    -moz-appearance: none !important;
    -moz-margin-start: -8px;
}
