    border-bottom: 0px solid #e7e7e700;
}
.global-stats a {
    color: rgb(99, 185, 218);
font-weight: 100;
    font-size: 17px;
    
}
.language-dropdown, .global-currency-dropdown {
    background-color: #0000001a;
    border-color: transparent;
}
.navbar-default {
    background-color: #262626;
    border-color: #303030;
}
thead,th {
    background: #404040;
        font-weight: 300;
color:#666;
    margin-bottom:2px;
}
tbody {
        BACKGROUND: #404040;
}
a.currency-name-container {
    font-weight: 300;
    color: #ddd;
}
a.price {
    color: rgb(99, 185, 218) ;
        font-size: 18px;
}
.positive_change {
    color: #4ad77c;
}
.negative_change {
    color: #ff5943;
}
.percent-24h {
    font-size: 18px;
}
.col-lg-10 {
    /* width: 83.33333333%; */
    left: 50%;
    transform: translate(-50%, -0%);
}

.ct_70unrFr ,.col-md-6,.col-sm-2,.col-lg-2,#title {
 
    display: none;
}
.btn-primary {
    color: #647076;
    background-color: #262626;
    border-color: #262626;
}

#top-nav-outer-container {
    padding: 5px 15px 0 15px;
    background-color: #303030!important;
    border-bottom: 0px solid #303030!important;
}
.pagination>li>a {
    color: #909090;
    background-color: #303030!important;
    border-color: #303030!important;
}
.dataTable td.sorting_1 {
    border: 1px solid #292929;
}
.sorting_asc {
  
    border-left: 0px solid #303030;
    border-right: 1px solid #292929;
}

#title a {
    color: #555;
}

#top-nav-outer-container {
    padding: 5px 15px 0 15px;
    background-color: #101010;
    border-bottom: 1px solid #000;
}
a {
    color: #909090;
    text-decoration: none;
}
img.sparkline {
    border: 0px solid #aaaaaa;
    width: 166px;
    height: 50px;
    -webkit-filter: invert(84%); /* Chrome, Safari, Opera */
    filter: invert(84%);
}
.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #aaa;
    background-color: #262626;
    background-image: none;
    border: 1px solid #303030;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
    color: #555;
    background-color: #303030;
    border: 1px solid #303030;
    border-bottom-color: transparent;
    cursor: default;
}
.pagination>li>a:hover, .pagination>li>span:hover, .pagination>li>a:focus, .pagination>li>span:focus {
    color: #2a6496;
    background-color: #303030;
    border-color: #303030;
}

#charts{
    -webkit-filter: invert(80%); /* Chrome, Safari, Opera */
    filter: invert(80%);
}
table#currencies>tbody>tr>td {
       
    padding: 2px;
     padding-right: 10px;
    vertical-align: middle;
   
    border-bottom: 1px solid #303030;
    border-top: 1px solid #303030;
        box-shadow: 0 2px 2px 0 rgba(0,0,0,0.15), 0 0 0 1px rgba(0,0,0,0.08);
}
.table>thead>tr>th {
    vertical-align: bottom;
    border-bottom: 1px #262626;;
}
thead tr {
    border-top: 0px solid #303030;
}
.coin-summary-item h3 {
    border-top: 0px solid #DDD;
    border-bottom: 0px solid #DDD;
    background-color: #333333;

}
.nav-tabs {
    border-bottom: 0px solid #ddd;
}
img.sparkline {
    border: 0px solid #aaaaaa !important;
 
    
}
hr {
  
    border-top:0px solid #eee;}
}
