css:
  @namespace url(http://www.w3.org/1999/xhtml);
  @-moz-document domain("userstyles.org") {

 * { text-align: justify; } 
 #header { height: 40px; }
 #view_inner  { width: 90%; } 
 #iframe { width: 195%; margin-left: -45%; } 

 .style_data { margin-right: -20px; ; margin-left: -10px; } 
 #style_info { position: absolute; left: 10%; width: 80%; padding-bottom: 500px;  }

 .cssHidden { display: block } 
 #stylish-code {  color: #c0c0c0; margin-left: -10%; margin-bottom: -100px;  height: 500px ; width: 115%; }

 #notes_textarea { font-weight: 600; position: absolute; top: 29%; left: 35%; height: 200px; width: 65%; word-wrap: break-word; padding-right: 2%  }
 #discussions-area { position: absolute; left: 0; margin-top: 110px;  }
 .text_style_page { width: 100%; max-width: 1200px; padding-top: 30px }

 #right_information { position: absolute; top: 20%; left: 70%; }
 #center_information { position: absolute; top: 13%; left: 70%; }
 #search_input { position: absolute; top: 2px; left: 2px; width: 35%; height: 35px; }
 #ownedButtons { position: absolute; top: 0; left: 65%; margin-top: -70px; z-index: 5 }

 #install_style_button { position: absolute; top: 7%; left: 68%; background-image: linear-gradient(#000, #000);  }
 #install_style_button:hover { position: absolute; top: 7%; left: 68%; background-image: linear-gradient(0deg, #0f0 0%, #003200 15%, #000 50%, #003200 85%,  #0f0 100%); }
 #install_button:hover { position: absolute; top: 7%; left: 67%; background-image: linear-gradient(0deg, #0f0 0%, #003200 15%, #000 50%, #003200 85%,  #0f0 100%); color: white; padding: 0 10px 0 10px; }
 #install_button { position: absolute; top: 7%; left: 67%; width: 180px; background-image: linear-gradient(#000, #000); color: white; padding: 0 10px 0 10px; }
 
 #navigation, #footer, #as_userscript, #paypal_form, no-select, #button_middle { display: none !important; }
 #style_info > A, #create_new_style, #top_logo, div.adContainer, .stylish-icon, #share_div, #headline, #title_div, dl 
{ display: none !important; }

}
