css:
@-moz-document domain("www.thepaper.cn") {
body{font-family: "宋体";}
.head  {height: 70px; margin: 0 auto 20px;}
.hdbk {border-bottom:0 none;}
#head  {height: 70px;}
.head_rt {height: auto;}
.head_logo{height: 70px;}
.head_logo a {width: auto; height: auto !important;}
.head_logo a img {height: 40px;}
.head_bd {height: 70px;}
.head_bd .verticalAlign{height: 70px;}
.head_rt .verticalAlign {height: 70px;}
.userhdinfo {left:-30px;}
.head_ad {display: none !important;}
.head_banner .bn_bt {width: auto; height: auto;position: relative;}
.head_banner a.bn_a {
    font-size: 16px;
    width: auto; height: auto;
    word-break: keep-all;
    word-wrap: normal;
    -moz-border-radius: 0px;
    -webkit-border-radius: 0px;
    border-radius: 0px;
}
.lilt_pt {display: none;}
.slider {
    top: 36px;
    left: 8px !important;
    z-index: 999999;
    min-height: 70px;
    width: 240px;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    border-radius: 0;
    border: 0 none;
}
#slider1,
#slider2,
#slider3,
#slider4,
#slider5,
#slider6,
#slider7,
#slider8,
#slider9,
#slider10,
#slider11 {left: 8px !important;}
.slider ul {padding: 5px 0;}
.slider li {
    width: 100%;
    height: auto;
    clear: both;
}
.slider li a {
    position: relative;
    padding: 2px 5px;
    width: auto;
    height: auto;
    display: block;
    text-align: left;
    letter-spacing: normal;
    font-size: 16px;
    word-wrap: break-word;
    border: 0 none;
}
.slider li:hover,
.slider li a:hover { border: 0 none;}
.slider li a p {display: inline;}
.li_prompt {
    bottom: 0;
    left:auto;
    right: 0;
    width: auto;
    height: auto;
    background: transparent none no-repeat;
    padding-right: 0;
}
.lilt_pl {background: transparent none no-repeat;}
.lilt_pr {display: none;}
.pdtt01 {display: none;}
.main_ltad {display: none !important;}
.main_ltad div {margin: 0px 0px 20px !important;}
.news_slide  {display: none;}
.newsbox {padding-top: 10px;}
.newsbox .news_li {
  position: relative !important; left: 0 !important; top:0 !important;
  padding: 5px 0 5px 15px; clear: both; width:auto;
  border: 0 none;
}
.newsbox .news_li:nth-child(1),
.newsbox .news_li:nth-child(2),
.newsbox .news_li:nth-child(3) {display: none;}
.newsbox .news_li:hover {
    border: 0 none;
    background-color: #dcf0fd;
}
.newsbox .news_li:hover .news_share {display: none !important;}
.newsbox .news_li .news_tu {display: none;}
.newsbox .news_li h2 {
    font-weight: 400 !important; font-size: 16px; font-family: "宋体";
    margin-top: 0;
}
.newsbox .news_li p {display: none;}
.newsbox .news_li .pdtt_trbs {bottom: 6px; font-family: "宋体";}
.pjax-switch {display: none;}

.main_rt > div:nth-child(1), .main_rt > div:nth-child(2) {display: none;}

.rt_ad,
.rtapp_down,
.back-top {display: none;}
}
