css:
@-moz-document domain("kinosha.net"), domain("kinosha.cc"), domain("kinosha.su") {
html body {
    padding-top: 1px !important;
    background-color: #02192F;
}
.item-fullfilm {
    margin-bottom: 15px;
    background-color: #e0dddd;
    box-shadow: 0px 0px 4px 4px white;
}
.film-disciption {
    background-color: #B8B8B8;
    text-shadow: 0 0 0px black;
    color: #a00e0e;
    box-shadow: 0px 0px 4px 4px #B8B8B8;
    font-size: 14px;
    border-top: 0px dashed #237ca9;
    width: 100%;
    padding-right: 3px;
    padding-left: 5px;
    padding-top: 0px;
}
div.col-xs-3-line.item {
    width: 100% !important;
    font-size: 250% !important;
    line-height: 25px !important;
    color: #00ccff !important;
    border-style: solid !important;
    border-left-width: 10px !important;
    border-right-width: 10px !important;
    border-top-width: 1px !important;
    border-bottom-width: 1px !important;
}
a.title span {
    font-size: 200% !important;
    line-height: 50px !important;
    font-weight: bold !important;
}
div.poster a img {
    width: 100% !important;
}
div.middle {
    margin-top: 50px !important;
    background-color: #000000 !important;
}
.middle {
    width: 100%;
    position: relative;
    margin: 52px 0 0;
    padding: 18px 0;
    background: #fff 0 0 repeat;
    border-radius: 10px;
    -webkit-box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.1);
    -moz-box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.1);
    box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.1);
}
.slider-full {
    display: none;
}
.seo-text.fs,
.reviews .item.positive,
.com-title-block h4,
.btn-primary,
.modal-footer a,
.feedback-rate li,
.comment-item,
.ralated-films,
.full-soc-block,
.screens a img,
.editorcomm,
.title-block {
    display: none;
}
.newfilms-block .item {
    color: #4b9bff;
    border: 1px solid #0f078e;
    background: rgba(50, 52, 105, 0.37);
}
div.middle {
    position: absolute;
    left: 20px;
    width: 97%;
}
}

@-moz-document url("http://kinosha.net/vieweds/"), url("http://kinosha.net/favorites/"), url("http://kinosha.net/bookmarks/"), url("http://kinosha.cc/vieweds/"), url("http://kinosha.cc/vieweds/"), url("http://kinosha.cc/bookmarks/"), url("http://kinosha.su/vieweds/"), url("http://kinosha.su/vieweds/"), url("http://kinosha.su/bookmarks/") {
.item-film .item {
    width: 100% !important;
    height: 350px !important;
    margin-top: 25px !important;
    border-radius: 10px;
    -webkit-box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.1);
    -moz-box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.1);
    box-shadow: inset 0 0 9px 2px #1fb4ff;
}
.item-film .title span {
    font-size: 30px !important;
    box-shadow: 0px 1px 12px 6px #0775ff !important;
    background-color: black !important;
}
.item-film .poster img {
    width: 200px !important;
    height: auto !important;
    margin-top: 25px !important;
}
.item-film .discription {
    color: white !important;
}
.item-film .about .li {
    font-size: 20px !important;
    color: red !important;
}
}
