css:
/*créé par Lofox  */ 

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.20minutes.fr") {

  
  
  html, body,  #page-content  {
    background: linear-gradient(to right,#000,#1e1e1e);
  }
   
  body {color: #e1e1e1;}
  
   #page-content {
	box-shadow: unset;
}
  
  
  
  /*   entête 0b4892 */
  
  .header, .header::before,.header::after, .header-layout {
	background: #0b4892 !important;
}
 .headermini {
	background: #0b4892;
	
}
  .headermini.is-fixed  {
	background: #0b4892;
}
   .wrap {
	background: transparent;
}
  
   .logo  {
  filter:  grayscale(500%) contrast(900%) invert(100%)
     }
  
  .ajax::after {
	background-color:  hsla(213,86%,30.08%,1) !important;
}
  
  .pdfcta-date {
	color: #fff;
}
  
  .btn-icon.btn-default, .btn.btn-default {
	background-color: #fff;
	color: #0b4892;
}
  
  .header-nav-list::after {

	border-right: 2.5em solid #0b4892;
	background: -webkit-linear-gradient(left,hsla(0,0%,100%,0),#0b4892);
	background: linear-gradient(90deg,hsla(0,0%,100%,0),#0b4892);
	
} 
  
  .menubtn {
      filter: contrast(300%)  invert(100%)
  }
  
  .menubtn:hover .menubtn-disc {
	background-color: #333;
}
  
  
  
  
  
        /* menu */
  .drawer {
    background: linear-gradient(to left,#000,#1e1e1e);
	  box-shadow: unset;
    border: 2px solid #0b4892;
}
  
  .drawer-label {
    background: transparent;
	 color: #fff;
}
  
  .color-default {
	color: #0061da;
}
  
  .color-entertainment {
	color: #ff2a1f;
}
  
  .color-economy {
	color: #0077a8;
}
  
  .color-hightech {
	color: #969696;
}
  .color-media {
	color: #fff;
}
  
  .color-planet {
	color: #68e800;
}

  .skin-quiz .teaser-headline, .skin-quiz .teaser-summary, .skin-quiz .teaser-title {
	color: #f2006c;
}
  
  .drawer-list {
	background: transparent;
}
  
  .drawer-cat {
   	border-bottom:  1px solid #1e1e1e;    
    }
  
  a {
	color: #e1e1e1;
}
  
  
  /*   home  */
  
  .breakingnews .tabs-alert {
border: 1px solid #ff2a1f; 
}
  
  .breakingnews .tabs-alert {
	color: #ff2a1f;
}
  .box {
	border: 1px solid #000;
    background: linear-gradient(to right,#1e1e1e,#000);
}
  .skin-lastminute {
	background-color: #cc190f !important;
	box-shadow: unset;
    	border: 2px solid #cc190f;
}
  .gallery-item:first-child .teaser-title,.gallery-item .teaser-title,
  .preview-cover .teaser-title , .preview .teaser-title {
	color: #e1e1e1;
}
  .skin-live .teaser-headline {
	color: #ff2a1f;
}
  
  img {
  border: 1px solid #555;
    background-color: #689DDC;
  }
  .media img {
	background-color: #0b4892;
}
  .counterbar {
	border-top-width:  1px;
   border-top-style:  solid ;
    border-image:   linear-gradient(#1e1e1e,#000);
    background: linear-gradient(to right,#1e1e1e,#000);
}
  
  .socialfooter {
    background: linear-gradient(to right,#1e1e1e,#000);
	border-top-width:  1px;
   border-top-style:  solid ;
    border-image:   linear-gradient(#1e1e1e,#000);
	box-shadow: unset;
}
  
  /* colone droite */  
  
  .block-list .teaser-title {
	color: #e1e1e1;
}
 
  a:hover, 
  .block-list .teaser:hover {
    background-color: transparent; filter: brightness(110%) contrast(110%)
}
  
   .block-list > li {
    background: linear-gradient(to right,#000,#1e1e1e);
}
  .block-list > li:nth-child(2n) {
    background: linear-gradient(to left,#080808,#262626);
}
  .block-list .teaser.default .teaser-headline {
	color: #0061da;
}
  
  .block-list .teaser.entertainment .teaser-headline {
	color: #ff2a1f;
}
  
  .block-list .teaser.economy .teaser-headline {
	color: #0077a8;
}
  
  .block-list .teaser.hightech .teaser-headline {
	color: #969696;
}
  
  .block-list .teaser.planet .teaser-headline {
	color: #68e800;
}
  
  .block-footer {
    background: linear-gradient(to left,#000,#1e1e1e);    
	border-top:  0px solid #000;  
 	border-left:   1px solid #000;  
	border-right:   1px solid #000;  
	border-bottom:   1px solid #000;  
}
  
  .block-footer a {
	color: #e1e1e1;
}
  
  .pager-current, .pager-max {
	color: #e1e1e1;
}
  
  .survey-title ,.survey-result-percent, .survey-total /* sondage*/ {
	color: #e1e1e1;
}
  
  .survey-result-vote {
	color: #f2006c;
}
 
  .survey-choice {
    background: linear-gradient(to left,#1e1e1e,#000);
	border-bottom: 1px solid #555;
}
  .survey-choice:hover {
    background: linear-gradient(to left,#2e2e2e,#111);
	border-bottom: 1px solid #555;
}
  .survey-choice-label::before {
	border: 1px solid transparent;
	box-shadow: unset;
	background: transparent;

}
  .survey-choice-label {
	color: #e1e1e1;
}
  
    /* horoscope   publireportages */  
 .zodiac {
	color: #ccc;
    background: linear-gradient(to right,#1e1e1e,#000);
}
  
  .zodiac[href]:focus, .zodiac[href]:hover {
    background: linear-gradient(to right,#1e1e1e,#000);
}
  
  .imagebox img {
	filter: brightness(70%);
}
  
  .footer {
	background-color: #0b4892;
}
  
  .socialcount-value {
	background-color: #1e1e1e;
}
  
  
  
    /*  article    */
  
  .header-nav-current {
  background: #fff; 
    border-radius: 20em;
    border-left: 5px solid #fff; 
        border-right: 5px solid #fff; 
}
  
  span.teaser-title, .headermini-layout .teaser-headline {
    background: #fff; 
    border-radius: 20em;
    border-left: 7px solid #fff; 
    border-right: 7px solid #fff; }

  .teaser-title {
    color: #0b4892;
    margin-left: 15px; 
}
  .counterbar a, .datetime, .content figcaption, .counter-unit, .counter-value {
	color: #a9a9a9;
}
  
  .summary.default {
	background-color: #00377E;
}
  
  .summary.entertainment {
	background-color: #A90800;
}
  
  .summary.hightech {
	background-color: #2F2E2E;
}
  
  .summary.planet {
	background-color: #449700;
}
  
.summary.sport {
	background-color: #006282;
}
  
  .summary.economy {
	background-color: #053042;
}
  
  .highlight {
	background: #000;
}
  
  
  
  /* commentaires   */  
  
  
  input[type="date"], input[type="datetime-local"], input[type="datetime"], input[type="email"], input[type="number"], input[type="password"], input[type="search"], input[type="tel"], input[type="text"], input[type="time"], input[type="url"], select, textarea {
	border: 1px solid #121212;
	background-color: #262626;
    color: #e1e1e1;
  }
  
  #pane-comment-report {
	background-color: #000;
    border: 1px solid #777;
   box-shadow: 2px 2px #aaa; 
}
  
  textarea {
	border: 1px solid #555;
  }
  
  .comment, .comment:hover {
	color: #e1e1e1;
	background: #121212;
}
 
  .posts-list > li {
	border-bottom: 1px solid #555;
}
  
  .posts-list {
	border-top: 1px solid #555;
	border-bottom: 1px solid #555;
}
  
  .comment-author-name, .color-community {
	color: #f2006c;
}
  
  
 /*  bloc pied   */  
  
  .block {
    background: linear-gradient(to right,#000,#1e1e1e);
}
  
  .block-economy .block-title-lined {
	color: #0077a8;
}
  
  .backtotop-btn {  /*  bouton up  */ 
	display: block;
	filter: invert(100%)
}
  
  
  /*  bouton connexion hover */ 
  
  .icon-community:hover {
	background-color: #930b48;
}
  
  /* logo  hover   */
 .logo:hover {
	  filter:  grayscale(500%) contrast(900%) invert(100%) brightness(110%)
}
  
  
}
